#define R iResolution
#define T iTime
#define M iMouse

#define PI          3.14159265359
#define PI2         6.28318530718

#define MIN_DIST    1e-4
#define MAX_DIST    50.

float hash21(vec2 p){ return fract(sin(dot(p,vec2(26.34,45.32)))*4324.23); }
mat2 rot(float a){ return mat2(cos(a),sin(a),-sin(a),cos(a));}

float scale = 8.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord.xy-R.xy)/max(R.x,R.y);
    vec3 C = vec3(.005);
    vec2 vv = uv;

    //split screen

    if(vv.x>-.3) {
        uv = uv-vec2(.3535,0);
        uv.x -= .25;
        uv /= uv.x*uv.x + uv.y*uv.y;
        uv.x += 2.;

        uv *= rot(-T*.11);
        uv = vec2(log(length(uv)), atan(uv.y, uv.x))*.6365;//.3175
    }else{
        uv = uv+vec2(.635,0);
        uv *= rot(-T*.11);
        uv = vec2(log(length(uv)), atan(uv.y, uv.x))*.6365;//.3175
    }

    uv.x -= T*.15;

    float px = fwidth(uv.x)*PI2;

    vec2 id=floor(uv*scale);
    float ck=mod(id.x+id.y,2.)*2.-1.;
    //using mod on the id's to skip sections
    //or replace with horz/vert lines to increase
    //the visual space. Click mouse to see grid.

    //mod vars to mix things up
    float m = floor(mod(T*.085+id.y,5.)+1.);//1.75;//
    float n = floor(mod(T*.1+id.x,6.)+1.);//2.15;//
    //mod for long slices
    vec2 cd=vec2(mod(id.x,n),mod(id.y,m))*2.-1.;

    uv=fract(uv*scale)-.5;

    float hs = hash21(id);
    if(hs>.5) uv.x=-uv.x;

    float rd = fract(hs*37.39);

    vec2 p = length(uv-.5) < length(uv+.5) ? uv-.5 : uv+.5;

    float d = length(p)-.5;
    float l = abs(max(abs(uv.x),abs(uv.y))-.5)-.01;

    if(rd>.85) d = min(length(uv.x),length(uv.y));
    //skip section logic
    if(cd.x>.5 ) d = length(uv.y);
    if(cd.y>.5 ) d = length(uv.x);
    if(cd.y>.5 && cd.x>.5) d = 1.;

    C = mix(C,vec3(0,.9,.1),smoothstep(px,-px,abs(d)-.15));
    C = mix(C,vec3(.4,0,.9),smoothstep(px,-px,abs(abs(d)-.35)-.1));

    if(M.z>0.) C = mix(C,vec3(.1),smoothstep(px,-px,l));
    if(vv.x<-.3&&vv.x>-.305) C = vec3(0,.9,.1);

    // Gamma & Output
    C = pow(C,vec3(.4545));
    fragColor = vec4(C,1.0);
}

// Found this on GLSL sandbox. I really liked it, changed a few things and made it tileable.
