// Redefine below to see the tiling...
//#define SHOW_TILING
const vec2 target_resolution = vec2(940, 86);

#define TAU 6.28318530718
#define MAX_ITER 5

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
	float time = iTime * .5 + 23.0;
    // Same texCoord as in Minecraft
    vec2 texCoord = fragCoord/iResolution.xy;
    // Own uv variable
    vec2 uv = texCoord;

    // Center origin around screen center (range -0.5, 0.5)
    uv -= .5;

    // Squish image in excessive dimension
    float r = (iResolution.x/iResolution.y) / (target_resolution.x/target_resolution.y);
    if (r >= 1.) uv.x *= r;
    else uv.y /= r;

    // Recenter origin to start at 0 (range 0.0, 1.0)
    uv += .5;
    uv.y -= 0.5/r - 0.5; 

    vec2 uv0 = uv; 
    uv0.x *= 3.;
    //uv0.x += iTime * 0.3;
    uv0.y *= 1.;
    uv0.y -= iTime * 0.1;


    vec2 p = mod(uv0*TAU, TAU) - 250.0;

	vec2 i = vec2(p);
	float c = 1.0;
	float inten = .009;

    //waterpatterntilingpattern
	for (int n = 0; n < MAX_ITER; n++) 
	{
		float t = time * (1.0 - (2.5/ float(n+1)));
        float t1= time * .2;
		i = p + vec2(cos(t1 - i.x) + sin(t + i.y), sin(t - i.y) + cos(t1 + i.x));
		c += 1.0/length(vec2(p.x / (sin(i.x+ t1)/inten),p.y / (cos(i.y+t)/inten)));
	}

	c /= float(MAX_ITER);

    //1.12 
    //1. 1,
	c = 1.2 - pow(c, 1.1);

    //7.5
	vec3 colour = vec3(pow(abs(c), 10.5));
    colour = clamp(colour + vec3(0.0, 0.35, 0.5), 0.0, 1.0);

	#ifdef SHOW_TILING
	// Flash tile borders...
	vec2 pixel = 2.0 / iResolution.xy;
	uv *= 2.0;
	float f = floor(mod(iTime*.5, 2.0)); 	// Flash value.
	vec2 first = step(pixel, uv) * f;		   	// Rule out first screen pixels and flash.
	uv  = step(fract(uv), pixel);				// Add one line of pixels per tile.
	colour = mix(colour, vec3(1.0, 1.0, 0.0), (uv.x + uv.y) * first.x * first.y); // Yellow line
	#endif

    float timeMotion = smoothstep(0.0, 1.0, sin(iTime) * 0.5 + 0.5 + sin(iTime * 0.2) * 0.5);
    //float timeMotion = sin(iTime) * 0.5 + 0.5;

    //headColor
    vec3 headColor = vec3(1.000,1.000,1.000); 
	//headColor *= pow(uv.y,  3.5) ; //
    //headColor *= pow(uv.y,  10.5) ; //
    headColor *= pow(uv.y, mix(3.5, 10.5, timeMotion));
    colour *= mix(1.0, 0.8, timeMotion);
    fragColor = vec4(colour + headColor, 1.0);

    //endColor
    vec3 endColor = vec3(0.000,0.118,0.200); 
    float endColorMask1 = smoothstep(0.0, 0.2,uv.y);
    float endColorMask2 = smoothstep(0.1, 0.9,uv.y);
    //fragColor.rgb = mix(endColor, fragColor.rgb, endColorMask1); //
    //fragColor.rgb = mix(endColor, fragColor.rgb, endColorMask2); //
    fragColor.rgb = mix(endColor, fragColor.rgb, mix(endColorMask1,endColorMask2, timeMotion));

    //mask
    float mask = pow(uv.y, 1.0);
    //0.492
    mask = smoothstep(0.0,0.472,uv.y);

    //fragColor *= (mask);
    //fragColor = vec4(endColor, 1.0);
    //fragColor = vec4(mask);

    if (uv.x < 0. || uv.x > 1. || uv.y < 0. || uv.y > 1.) fragColor = vec4(0);    
}

