#define DIZERING 1
#define MAX_STEPS 300
const float PI = 3.14159265359;    

const vec3 lightPos   = vec3(2, 2, 1);
const vec4 lightColor = vec4(1.5, 1, 1, 1) * .7;

const vec4 ambientColor = vec4(1, 1, 1, 1) * 0.1;

const float MAX_DIST = 6.0;
const float EPS = 0.001;

mat3 rotationX(float a) {
    return mat3(1, 0, 0,
                0, cos(a), -sin(a),
                0, sin(a), cos(a));
}

mat3 rotationY(float a) {
    return mat3(cos(a), 0, sin(a),
            0, 1, 0,
            -sin(a), 0, cos(a));
}

mat3 rotationZ(float a) {
    return mat3(cos(a), -sin(a), 0,
            sin(a), cos(a), 0,
            0, 0, 1);
}

float smoothUnion(float d1, float d2, float k) {
    float h = clamp(0.5 + 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) - k * h * (1.0 - h); 
}

float smoothSubtraction(float d1, float d2, float k) {
    float h = clamp(0.5 - 0.5 * (d2 + d1) / k, 0.0, 1.0);
    return mix(d2, -d1, h) + k * h * (1.0 - h); 
}

float smoothIntersection(float d1, float d2, float k) {
    float h = clamp(0.5 - 0.5 * (d2 - d1) / k, 0.0, 1.0);
    return mix(d2, d1, h) + k * h * (1.0 - h); 
}

float rand(float frame) {
    return fract(sin(dot(vec3(frame), vec3(12.9898,78.233,45.5432))) * 43758.5453);
}

float sdf_sphere(vec3 p, vec3 c, float r) {
    return length(p - c) - r;
}

float sdf_capped_cone(vec3 p, float h, float r1, float r2) {
    vec2 q = vec2(length(p.xz), p.y);
    vec2 k1 = vec2(r2, h);
    vec2 k2 = vec2(r2 - r1, 2.0 * h);
    vec2 ca = vec2(q.x - min(q.x, q.y < 0.0 ? r1 : r2), abs(q.y) - h);
    vec2 cb = q - k1 + k2 * clamp(dot(k1 - q, k2) / dot(k2, k2), 0.0, 1.0);
    float s = (cb.x < 0.0 && ca.y < 0.0) ? -1.0 : 1.0;
    return s * sqrt(min(dot(ca, ca), dot(cb, cb)));
}

float sdf_torus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}

float sdf_cut_sphere(vec3 p, float r, float h) {
    float w = sqrt(r * r - h * h);
    vec2 q = vec2(length(p.xz), p.y);
    float s = max((h - r) * q.x * q.x + w * w * (h + r - 2.0 * q.y), h * q.x - w * q.y);
    return s < 0.0 ? length(q) - r : q.x < w ? h - q.y : length(q - vec2(w, h));
}

float sdf_cylinder(vec3 p, float h, float r) {
    vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(r,h);
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float length_n(vec2 t, float n) {
    return pow(pow(abs(t.x), n) + pow(abs(t.y), n), 1.0 / n);
}

float sdf_torus(vec3 p, vec2 t, float n, float m) {
    vec2 q = vec2(length_n(p.xz, n) - t.x, p.y);
    return length_n(q, m) - t.y;
}

float sdf_round_cone(vec3 p, float r1, float r2, float h) {
    float b = (r1 - r2) / h;
    float a = sqrt(1.0 - b * b);
    vec2 q = vec2(length(p.xz), p.y);
    float k = dot(q, vec2(-b, a));
    if (k < 0.0) 
        return length(q) - r1;
    if (k > a * h) 
        return length(q - vec2(0.0, h)) - r2;
    return dot(q, vec2(a,b)) - r1;
}

float sdf_plane(vec3 p, vec3 n, float h) {
    return dot(p, n) + h;
}

float sdf(vec3 p) {
    float dTorus = sdf_torus(p - vec3(0, 0.2, 0), vec2(0.31, 0.02));
    float dCappedCone = sdf_capped_cone(p, 0.2, 0.5, 0.35);
    float dCutSphereUp = sdf_cut_sphere(p + vec3(0, 0.55, 0), 0.8, 0.75);
    float subtraction1 = smoothSubtraction(dTorus, dCappedCone, 0.02);
    float union1 = smoothUnion(subtraction1, dCutSphereUp, 0.03);
    float dCutSphereDown = sdf_cut_sphere(p * vec3(1, -1, 1), sqrt(0.29), 0.2);
    float union2 = min(union1, dCutSphereDown);
    float dInvisibleCylinder = sdf_cylinder(p + vec3(0, sqrt(0.29) - 0.05, 0), 0.19, 0.45);
    float subtraction2 = max(union2, -dInvisibleCylinder);
    float dVisibleCylinder = sdf_cylinder(p + vec3(0, sqrt(0.29) - 0.22, 0), 0.01, 0.45);
    float union3 = smoothUnion(subtraction2, dVisibleCylinder, 0.03);
    float dTorus42 = sdf_torus((p - vec3(0, 0.22, 0)) * rotationX(PI / 2.0), vec2(0.1, 0.02), 4.0, 2.0);
    float union4 = smoothUnion(union3, dTorus42, 0.03);
    float dTorus24 = sdf_torus((p + vec3(0.5, 0, 0)) * rotationX(PI / 2.0), vec2(0.14, 0.035), 2.0, 4.0);  
    float union5 = smoothUnion(union4, dTorus24, 0.05);
    float dRoundCone = sdf_round_cone(rotationZ(-PI / 4.0) * (p - vec3(0.46, -0.1, 0)), 0.1, 0.04, 0.35);
    float union6 = smoothUnion(union5, dRoundCone, 0.05);
    float dInvisibleSphere = sdf_sphere(p, vec3(0.73, 0.17, 0), 0.025);
    float subtraction3 = smoothSubtraction(dInvisibleSphere, union6, 0.03);
    float dPlane = sdf_plane(p, vec3(0, 1, 0), 0.34);
    return min(subtraction3, dPlane);
}

vec3 generateNormal(vec3 p) {
    float dx1 = sdf(p + vec3(EPS, 0, 0));
    float dx2 = sdf(p - vec3(EPS, 0, 0));
    float dy1 = sdf(p + vec3(0, EPS, 0));
    float dy2 = sdf(p - vec3(0, EPS, 0));
    float dz1 = sdf(p + vec3(0, 0, EPS));
    float dz2 = sdf(p - vec3(0, 0, EPS));

    return normalize(vec3(dx1 - dx2, dy1 - dy2, dz1 - dz2));
}

float ambientOcclusion(vec3 p, vec3 normal) {
    const float stepSize = 0.03;

    float occ = 0.0;
    float sca = 1.0;

    float h = 0.01;
    for (int i = 0; i < 10; ++i) {
        float d = sdf(p + h * normal);
        occ += (h - d) * sca;
        sca *= 0.95;

        if (occ > 0.35) 
            break;

        h += stepSize;
    }

    return clamp(1.0 - 3.0 * occ, 0.0, 1.0) * (0.3 + 0.5 * normal.y + 0.8 * normal.x);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    #if DIZERING
        vec3 CAMERA_POS = vec3(0, 1, 3);
        vec3 randVals = vec3(rand(float(iFrame)), rand(float(iFrame + 5)), rand(float(iFrame + 15)));
        vec2 uv = (fragCoord - 0.5 * iResolution.xy + (randVals.xy - 0.5)) / iResolution.x;
    #else 
        vec3 CAMERA_POS = vec3(3.0 * cos(iTime), 1, 3.0 * sin(iTime));
        vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.x;
    #endif

    vec3 forward = normalize(-CAMERA_POS);
    vec3 up = vec3(0, 1, 0);
    vec3 right = normalize(cross(forward, up));
    up = cross(right, forward);

    vec3 rayDir = normalize(forward + uv.x * right + uv.y * up);

    vec3 p = CAMERA_POS;
    float totalDist = 0.0;
    for (int i = 0; i < MAX_STEPS; ++i) {
        float dist = sdf(p);

        if (abs(dist) < EPS) {
            if (abs(dist - sdf_plane(p, vec3(0, 1, 0), 0.34)) < EPS) {
                fragColor = vec4(0.357, 0.631, 0.6, 1);
            } else {
                fragColor = vec4(0.867, 0.749, 0.667, 1);
            }
            vec3 normal = generateNormal(p);
            vec3 towardLight = normalize(lightPos - p);
            fragColor *= max(0.0, dot(towardLight, normal)) * lightColor + ambientOcclusion(p, normal) * ambientColor;
            fragColor += pow(max(0.0, dot(reflect(towardLight, normal), normalize(p - CAMERA_POS))), 128.0) * lightColor;
            break;
        }

        totalDist += dist;
        if (totalDist > MAX_DIST) {
            fragColor = vec4(0.733, 0.776, 0.784, 1);
            break;
        }

        p += rayDir * dist;
    }
}

