float dist(vec3 p){
	float d = 0.;

    d = prismSDF(p,0);
    d = min(d,sphereSDF(p,0));
    d = min(d,cubeSDF(p,0));
    d = min(d,octahedronSDF(p,0));
    d = min(d,lineSDF(p,0));
    d = min(d,cylinderSDF(p,0));
    d = min(d,torusSDF(p,0));

    d = min(d,p.y+4.);

	return d;
}

vec3 sNormal(vec3 p){
	vec2 t = vec2(.001,0);
	float d = dist(p);

	return normalize(d - vec3(
		dist(p-t.xyy),
		dist(p-t.yxy),
		dist(p-t.yyx)
	));
}

float diffuse(vec3 p,vec3 L){
	return max(dot(sNormal(p),normalize(L)),0.);
}

// phong
float phong(Light l,Ray r){
    float c = 0.;
    float d = diffuse(r.p,l.L);
    vec3 lRef = reflect(-l.L,N);

    c = pow(max(dot(lRef,-r.dir),0.),32.) * d;

    return c;
}

// blinn phong
float bPhong(Light l,Ray r){
	float c = 0.;
	float d = diffuse(r.p,l.L);

	c += pow(max(dot(normalize(l.L+V),N),0.),32.) * d;

	return c;
}

vec3 render(Ray r){
    vec3 c = vec3(0);
    N = sNormal(r.p);
    for( int i = 0;i< LIGHTS;i++){
        lNormal(l[i],r);
        c += .2;
        c += diffuse(r.p,l[i].L);
        c += bPhong(l[i],r);
    }
    //c = .5+.5*N;
    c *= normalize(vec3(1));

    if(r.dist > MAX_DIST)c = vec3(0);
    return c;
}

void march(inout Ray r,int t){
	for(int i = 0;i<MAX_STEP;i++){
		r.p = r.o + r.dist*r.dir;
		float d = dist(r.p);
		d = t == 0 ? d : min(d,lightDist(r.p,d));
		if(d <= MIN_DIST || d > MAX_DIST)break;
		r.dist += d;
	}
    r.rl += r.dist;
}

void mainImage( out vec4 fColor, in vec2 fCoord )
{
    vec2 uv = (2.*fCoord.xy - iResolution.xy)/iResolution.y;
    uv *= 1.5;
    vec2 m = vec2(time*.1,-abs(sin(time/40.)*1.));
    m = (2.*iMouse.xy-iResolution.xy)/iResolution.y;

    vec3 c = vec3(0);
    vec3 ca = vec3(
        PI/2.+m.y*PI/1.5,
        PI/2.-m.x*PI/1.,
        0
    );

    float camz = PI;
    vec3 camera = vec3(0,0,camz)*rotX(ca.x)*rotY(ca.y);
    vec3 plane = vec3(uv*2.,0)*rotX(ca.x)*rotY(ca.y);

    Ray r = initRay(camera,plane);

    cu[0].p = vec3(1.5,0,1.5);
    cu[0].s = vec3(.4);

    pr[0].p = vec3(1.5,0,0);
    pr[0].s = vec3(.2,.4,6);

    l[0].p = vec3(2)*rotX(ca.x)*rotY(ca.x);

    sp[0].p = vec4(0,0,1.5,.4);

    line[0].a = vec3(.5,.5,-.5)+vec3(-.9,0,-.9);
    line[0].b = vec3(-.5,-.5,.5)+vec3(-.9,0,-.9);
    line[0].r = .2;

    cyl[0].p = vec3(1.5,0,-1.5);
    cyl[0].s = vec2(.5,.4);

    tor[0].r = vec2(.4,.2);
    tor[0].p = vec3(-1.5,0,1.5);

    oc[0].p = vec4(0,0,0,.5);

    //l[0].p = vec3(5);//*rotX(ca.x)*rotY(ca.y);
    //l[1].p = vec3(1,2,1);//*rotX(ca.x)*rotY(ca.y);

    l[0].p.xz = l[0].p.xz * rot2D(iTime*.5);

    march(r,0);
    N = sNormal(r.p);
    V = -r.dir;

    //c = N*.5+.5;
    //c = r.p;
    c += render(r);
    //c = vec3(1./r.rl);
    fColor = vec4(c,1.0);
}

