#define time iTime
#define resolution iResolution
#define mouse iMouse.xy
#define PI 3.14159265
#define MAX_DIST  100.
#define MIN_DIST  .001
#define MAX_STEP  256
#define MAX_REF   2

#define rotX(a) mat3(      1,      0,       0,       0, sin(a), cos(a),      0,- cos(a), sin(a))
#define rotY(a) mat3( sin(a),      0, -cos(a),       0,      1,      0, cos(a),       0, sin(a))
#define rotZ(a) mat3( sin(a), cos(a),       0, -cos(a), sin(a),      0,      0,       0,      1)
#define rot2D(a) mat2(sin(a),cos(a),-cos(a),sin(a))

const int LIGHTS = 1;
const int PRISM = 1;
const int OCTS  = 1;
const int TORUS = 1;
const int SPHERES = 1;
const int CUBES = 1;
const int LINES = 1;
const int CYLS = 1;

vec3 N = vec3(0);
vec3 V = vec3(0);

struct Ray {
	vec3 o;
	vec3 p;
	vec3 dir;
	vec3 c;
	float dist;
    float rl;
};

struct Light {
	vec3 p;
	vec3 c;
    vec3 L;
},l[LIGHTS];

struct Octahedron {
    vec4 p;
    vec3 c;
},oc[OCTS];

struct Prism {
    vec3 p;
    vec3 s;
    vec3 c;
},pr[PRISM];

struct Torus {
    vec3 p;
    vec2 r;
    vec3 c;
},tor[TORUS];

struct Cyl {
    vec3 p;
    vec2 s;
    vec3 c;
},cyl[CYLS];

struct Line {
    vec3 a;
    vec3 b;
    vec3 c;
    float r;
},line[LINES];

struct Cube {
	vec3 p;
	vec3 c;
	vec3 s;
},cu[CUBES];

struct Sphere {
    vec4 p;
    vec3 c;
},sp[SPHERES];


// octahedron
float octahedronSDF(vec3 p,int id){
    Octahedron o = oc[id];
    float d;

    p = abs(p-o.p.xyz);
    float face = p.x+p.y+p.z - o.p.w;

    vec3 q = p;

    if(p.x*3.< face)q = p.xyz;
    else if(p.y*3. < face)q = p.yzx;
    else if(p.z*3. < face)q = p.zxy;
    else return face* (1./sqrt(3.));

    float k = clamp(0.5*(q.z-q.y+o.p.w),0.0,o.p.w);
    float edge = length(vec3(q.x,q.y-o.p.w+k,q.z-k));

    d = edge;
    return d;
}

// torus / donut
float torusSDF(vec3 p,int id){
    float d = 0.;
    Torus t = tor[id];

    vec3 sp;
    sp.xz = normalize(p.xz-t.p.xz)*t.r.x;
    d = length(p-t.p-sp)-t.r.y;

    return d;
}

// cylinder
float cylinderSDF(vec3 p,int id){
    vec2 d = vec2(0);
    Cyl c = cyl[id];
    vec3 cp = p - c.p;

    d = vec2(abs(cp.y),length(cp.xz)) - c.s * vec2(.5,1);

    return min(max(d.x,d.y),0.)+length(max(d,0.));
}

// line / capsule
float lineSDF(vec3 p,int id){
    float d = 0.;
    Line li = line[id];
    vec3 p1 = p - li.a;
    vec3 p2 = li.b - li.a;

    d = length(p1 - clamp(dot(p1,p2)/dot(p2,p2),0.,1.)*p2) - li.r;
    return d;
}

// sphere
float sphereSDF(vec3 p,int id){
    return length(p-sp[id].p.xyz) - sp[id].p.w;
}

float max3(vec3 n){
    return max(max(n.x,n.y),n.z);
}

// cube sdf
float cubeSDF(vec3 p,int id){
    vec3 c = abs((p-cu[id].p))-cu[id].s;
    return length(max(c,0.)) + min(0.,max3(c));
}

//2d polygon sdf
float polygon(vec2 uv,vec2 p,float r,float n){
    uv = uv - p;
    n = floor(n);
    float pa = PI-atan(uv.x,uv.y);      // position angle
    float sa = 2.*PI/n;                 // side angle
    float id = (pa - mod(pa,sa)) / sa ;
    float ra = id * (sa) + sa/2.;       // rotate angle
    float ir = cos(sa/2.)*r;            // inner radius
    float sl = sin(sa/2.)*r;            // half side length

    uv = rot2D(ra)*uv;
    uv += vec2(-ir,0);

    if(uv.x < 0.)return uv.x;

    return length(vec2(uv.x,uv.y - clamp(uv.y,-sl,sl)));
}

// prism sdf
float prismSDF(vec3 p,int id){
    vec2 d = vec2(0);
    Prism ps = pr[id];
    vec3 cp = p - ps.p;

    d = vec2(abs(cp.y)-ps.s.x,polygon(cp.xz,vec2(0),ps.s.y,ps.s.z)-.05) * vec2(.5,1);

    return min(max(d.y,d.x),0.) + length(max(d,0.));
}


void lNormal(inout Light l,Ray r){
	l.L = normalize(l.p-r.p);
    //l.L = normalize(l.p);
}

float lightDist(vec3 p,float d){
    for(int i = 0;i<LIGHTS;i++)
        d = min(d,length(p - l[i].p)-.1);
	return d;
}



Ray initRay(vec3 c,vec3 p){
    // c camera
    // p projection plane
    Ray r;
    r.o = c;
    r.p = r.o;
    r.dir = normalize(p-c);
    r.c = vec3(0);
    r.dist = .5;
    r.rl = 0.;
    return r;
}

float smin(float a, float b, float k){
    float r = min(a,b) - pow(max(0.,k - abs(a - b)/k)*.5,2.);
    return r;
}

