float aastep(float f, float threshold) {
    float margin = clamp(fwidth(f) * 0.7, 0.0, 0.1);
    return smoothstep(threshold - margin, threshold + margin, f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord ) * Scale + 0.5 * iTime;
    vec2 iuv = round(uv * SubDiv) / SubDiv;
    vec2 fuv = uv - iuv;
    vec2 vf = getvec(iuv).xy;
    float len = length(vf.xy);
    bool aimless = len < 0.0001;
    float dir = aimless ? 0.0 : atan(vf.y, vf.x) * 0.5;
    vf = vec2(cos(dir), -sin(dir));
    fuv = vec2(vf.x * fuv.x - vf.y * fuv.y,  
               vf.x * fuv.y + vf.y * fuv.x);
    fragColor = getpix(ivec2(iuv));
    fragColor.a = 1.0;
    if (aimless) {
        fragColor *= 1.0 - aastep(length(fuv.xy), .15 / SubDiv);
    } else {
        fragColor.a *= 1.0 - aastep(abs(fuv.y), .1 / SubDiv);
        float clip = len * 0.5;
        clip = clamp(clip, 0.15, 0.45);
        fragColor.a *= 1.0 - aastep(abs(fuv.x), clip / SubDiv);
    }
    fragColor = fragColor * fragColor.a + vec4(0.25) * (1.0 - fragColor.a);
}

