float match(vec3 p, vec3 q) {
    return max(0.0, 1.0 - distance(p, q) * MatchDerate);
}

vec2 invdist(vec2 uv) {
    // TODO: probably gaussian would be better.
    return uv / dot(uv, uv);
}


vec2 csq(vec2 v) {
    return vec2(v.x * v.x - v.y * v.y, 2.0 * v.x * v.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (all(lessThan(fragCoord.xy, iChannelResolution[1].xy))) {
      ivec2 uv = ivec2(fragCoord);

      vec3 p = getpix(uv).rgb;

      vec2 acc = vec2(0);
      for (int y = -Radius; y <= Radius; ++y) {
          for (int x = -Radius; x <= Radius; ++x) {
              if (x == 0 && y == 0) continue;
              if (abs(x) + abs(y) > Radius * 3 / 2) continue;
              ivec2 o = ivec2(x, y);
              vec3 q = getpix(uv + o).rgb;
              acc += csq(invdist(vec2(o))) * match(p, q);
          }
      }
      p.rg = acc;
      fragColor = vec4(p, 1.0);
    } else {
      fragColor = vec4(0);
    }
}

