void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Recalculate fragCoord according to our pizel size
    vec2 pSize = iResolution.xy / RES; // Size of a vPixel
    vec2 pos = floor(fragCoord / pSize); // Position in our vPixel grid

    fragCoord = (pos * pSize) + (pSize / 2.); // Find appropriate full-scale pixel for our vPixel

    // Take the color from iChannel0 (input image)
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);

