float grid (vec2 uv) {
    //uv -= vec2(0,0.5);
    float steepVP=iResolution.x / iResolution.y; // steepness of viewport diagonals
    float uvSteep=abs(uv.x/uv.y);
    float c;
    float dist;
    if (uvSteep > steepVP) { //vertical walls

        c=abs(uv.x)/steepVP / DARKEN;
    }
    else { // horizontal walls
        c=abs(uv.y) / DARKEN;
    }
    dist=1./c+iTime*SPEED;
