const int STEPS = 50;
const float TRACE_DISTANCE = 200.0;
const float HIT_DISTANCE = 1e-3;
const float SPHERE_RADIUS = 1.0;
const float LIGHT_RADIUS = 0.1;
const float TILE_SIZE = 8.0;
const float PI = 3.14;

vec3 GetSphere() {
    vec3 sphere = vec3(0., 0., 3.);
    // float omega = 1e-2;
    // float freq = 2.0 * PI * omega;
    // sphere.x = 2.0 * ( mod(freq*iTime, 1.0) - 0.5 );
    return sphere;
}

float SdSphere(vec3 p) {
    vec3 sphere = GetSphere();

    float d = length(p-sphere.xyz) - SPHERE_RADIUS;
    return d;
}

vec3 GetLightPos() {
    vec3 pos = GetSphere();
    //pos.xy += vec2(cos(iTime * 0.2), sin(iTime * 5.8));
    float freq = 2.0*PI*0.01;
    pos.xz += 4.9*vec2(sin(freq*iTime), cos(freq*iTime));
    return pos;
}

vec3 GetLight(vec3 p, vec3 n, float power) {
    vec3 lightColor = vec3(1.0, 1.0, 1.0);
    vec3 pos = GetLightPos();
    vec3 l = normalize(pos-p);
    // Diffuse Light
    float diffuse = max(0.0, dot(n, l));
    vec3 intensity = diffuse*lightColor;
    return power*intensity; 
}

float SdLight(vec3 p) {
    vec3 pos = GetLightPos();
    float d = length(p-pos) - LIGHT_RADIUS;
    return d;
}

vec2 GetScene(vec3 p) {

      // Check Spheres
     float sdSphere = SdSphere(p);

     // Check Light
     float sdLight = SdLight(p);

     // Return Map
     if ( sdLight < sdSphere ) {
         return vec2(sdLight, 2.0);
     } else {
         return vec2(sdSphere, 1.0);
     }
}

float BloomEffect(float sd, float amp, float sigma, float falloff) {
    return amp/pow((2.0*PI*sigma*sigma), 0.5)*exp(-falloff*sd*sd);
}

// Shader Ops
vec3 GetNormal(vec3 p) {
    vec2 delta = vec2(1e-2, 0.0);
    vec3 n = vec3(
        GetScene(p+delta.xyy).s - GetScene(p-delta.xyy).s,
        GetScene(p+delta.yxy).s - GetScene(p-delta.yxy).s,
        GetScene(p+delta.yyx).s - GetScene(p-delta.yyx).s
    );
    return normalize(n);
}

vec2 LambertAzimuthalProjection(vec3 p) {
    vec3 normalizedPoint = normalize(p);
    float theta = atan(normalizedPoint.y, normalizedPoint.x);
    float phi = acos(normalizedPoint.z);

    // Normalize theta and phi to the range [0, 1]
    float u = (theta + PI) / (2.0 * PI);
    float v = phi / PI;

    return vec2(u, v);
}

vec3 RotatePlanet(vec3 p, float thetaX, float thetaY, float thetaZ) {

    float cosThetaX = cos(thetaX);
    float sinThetaX = sin(thetaX);
    mat3 rotationMatrixX = mat3(
        1.0,  0.0,       0.0,
        0.0,  cosThetaX, -sinThetaX,
        0.0,  sinThetaX, cosThetaX
    );

    float cosThetaY = cos(thetaY);
    float sinThetaY = sin(thetaY);
    mat3 rotationMatrixY = mat3(
        cosThetaY, 0.0, -sinThetaY,
        0.0,       1.0,  0.0,
        sinThetaY, 0.0, cosThetaY
    );

    float cosThetaZ = cos(thetaZ);
    float sinThetaZ = sin(thetaZ);
    mat3 rotationMatrixZ = mat3(
        cosThetaZ, -sinThetaZ, 0.0,
        sinThetaZ, cosThetaZ,  0.0,
        0.0,       0.0,        1.0
    );

    // Apply the rotations in the desired order (Z, then Y, then X)
    mat3 combinedRotationMatrix = rotationMatrixZ * rotationMatrixY * rotationMatrixX;

    return combinedRotationMatrix * p;
}

vec3 GetPlanetCoordinate(vec3 p) {
   p = normalize(p-GetSphere());
   vec3 theta = vec3(-105., -0., iTime*-10.);      
   p = RotatePlanet(p, radians(theta.x), radians(theta.y), radians(theta.z));
   return p;
}
vec3 PlanetColor(vec3 p, vec3 light) {
    p = GetPlanetCoordinate(p); 
    vec2 angular = LambertAzimuthalProjection(p); 
    vec3 col = mapEarth(vec3(p.y,p.x,p.z));
    col = smoothstep(0.,1.,sqrt(col));
    col = smoothstep(0.,1.,col);
    if (length(light) < 1e-2 ) {
        float time = 1.;
        float temp = NoiseT(time*0.004, p.xyz, 4.0);
        temp = mix(temp, NoiseT(time*0.008, p.xyz, 34.0), 0.8);
        temp = mix(temp, NoiseT(time*0.01, p.xyz, 164.0), 0.4);
        temp = smoothstep(0.76, .82, temp);
        float value = temp*length(col.rg)/pow(2., 0.5);
        col = value*vec3(1.0, 1.0, 0.0);
    } else {
        col = col*light;
    }
    return col;   
}

vec3 SunColor(vec3 p, vec2 uv)
{
    float time = iTime;
    p = normalize(p - GetLightPos());
    float temp = NoiseT(time*0.004, p.xyz, 4.0);
    temp = mix(temp, NoiseT(time*0.008, p.xyz, 34.0), 0.8);
    temp = mix(temp, NoiseT(time*0.01, p.xyz, 164.0), 0.4);
    float spots = NoiseT(time*0.004, p.xyz, 4.0);
    float h = 0.75;
    float c = 0.2;
    if(true && spots < c)
        temp = mix(temp, 0.0, min(1.0, 2.0 * (c-spots)*(1.0/c)));
    else if(true && spots > h)
    	temp = temp * (1.0 + min(1.0, 2.0 * (spots-h)*(1.0/(1.0-h))));
    temp = pow(temp, 2.0) ;
   	vec3 col = vec3(min(1.0, sqrt(temp) * 3.5), min(1.0, temp * 1.2), min(1.0, temp * 0.0));
    float w = 0.38;
    if(temp > w)
        col = mix(col, vec3(1.0),  (temp-w)*(1.0/(1.0-w))); 

    float k = (1.0 + cnoise(vec4(normalize(uv),time*0.01, 0.0) * 64.0)) * 0.5;
    float d = k - 0.999;
    if(d >= 0.0)
    {
        col = mix(col, vec3(2.0,0.9*k,0.05), 1.0 * min(1.0, sqrt(d)) );
    }    
    return col;
}

vec4 RayMarch(vec3 ro, vec3 rd, vec2 uv) {
    // Fragment Color
    vec3 C = vec3(0.0);
    // Distance To Scene
    float dS = 0.0;
    // Loop
    vec2 bounce;
    for(int i=0; i<STEPS; i+=1) {
        vec3 p = ro + dS*rd;
        bounce = GetScene(p);
        if(bounce.s < HIT_DISTANCE || dS > TRACE_DISTANCE) {
            break;
        }
        dS += bounce.s;
    }
    // Position
    vec3 p = ro+dS*rd;
    vec3 n = GetNormal(p);

    // Color Shading
    if(bounce.s < HIT_DISTANCE) {     
        // Check Object on Ray Bounce
        if ( bounce.t == 1.0 ) {
            vec3 light = GetLight(p, n, 1.0);
            C = PlanetColor(p, light);
        } else if ( bounce.t == 2.0) {
            C = SunColor(p, uv);
        }
    // Environment Shading        
    } else {
        // Star Field
        vec2 pos = (uv + 1.) * 1. / 2.0;
        pos.x += iTime*1e-1;
        vec3 texel = texture(iChannel1, pos).rgb;
        float value = length(texel) / 3.0;
        value = smoothstep(0.30, 0.35, value)*3.0;
        C = vec3(value);   
        // Sun Field
        vec3 center = GetLightPos().xyz;
        value = dot(normalize(p), normalize(center));
        value = pow(value, 55.0);
        float b = value * (1.0 + cnoise(vec4(uv, iTime*0.06, 0.0)*1.5)) * 0.5; 
        float k = (1.5 + cnoise(vec4(normalize(uv),iTime*0.01, 0.0) * 32.0)) * 0.5;
        vec3 bloom = vec3(b*1.5, pow(b, 3.0) * k, b*0.0) ;
        C = mix(C, bloom, 0.5);
    }

    return vec4(C, dS);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv - 0.5) * 2.;
    uv.x *= iResolution.x / iResolution.y;

    // Ray Marching
    // Ray Origin
    vec3 ro = vec3(0.0, 0.0, -2.0);
    // Ray Direction
    vec3 rd = vec3(uv, 2.0);
    rd = normalize(rd);
    // Transform Based on Mouse Movement
    vec2 mm = ( iMouse.xy / iResolution.xy - 0.5 ) * 2.0;
    ro.z += max(mm.y, 0.0) * SPHERE_RADIUS; // Adjust the scaling factor as needed
    // Transform the ray direction based on mouse movement
    // rd.xy += mm * 0.1; // Adjust the scaling factor as needed

    // Bounce the Ray
    vec4 bounce = RayMarch(ro, rd, uv);
    // Bounce Color
    vec3 C = bounce.xyz;
    float sd = bounce.w;

    fragColor = vec4(C,1.0);
}

float distanceToSegment( vec2 a, vec2 b, vec2 p )
{
	vec2 pa = p - a, ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float filter_len = smoothstep(-1.0, 0.0, cos(iTime * 0.7 + dot(uv, vec2(-0.3, 0.4)))) * 0.002;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec4 blurry = texture(iChannel0, uv);
    float len = length(blurry.xy);
    if (len < 0.0001) {
        col = texture(iChannel1, uv).rgb;
    } else if (any(isnan(blurry.xy))) {
        col = vec3(0.0);
    } else {
        float t = atan(blurry.y, blurry.x) * 0.5;
        vec2 dir = vec2(cos(t), sin(t)) * filter_len;
        vec4 sum = vec4(0.0);
        for (int i = -stroke_length; i <= stroke_length; ++i) {
            sum += texture(iChannel1, uv + dir * float(i));
        }
        col = sum.rgb / float(2 * stroke_length + 1);
#if 0
        if (fract(uv.y * 10.0) < 0.1) {
            col.g = blurry.x < 0.0 ? 0.1 : 0.9;
        } else if (fract(uv.y * 10.0) < 0.2) {
            col.r = blurry.y < 0.0 ? 0.1 : 0.9;
        }
#endif
    }

    vec4 undermouse = texture(iChannel0, iMouse.xy / iResolution.xy);
    len = length(undermouse.xy);
    bool highlight = false;
    if (len < 0.0001) {
        highlight = length(iMouse.xy - fragCoord.xy) < 10.0;
    } else if (any(isnan(blurry.xy))) {
        highlight = length(iMouse.xy - fragCoord.xy) < 5.0;
    } else {
        float t = atan(undermouse.y, undermouse.x) * 0.5;
        vec2 dir = vec2(cos(t), sin(t)) * 80.0 * len;
        highlight = distanceToSegment(iMouse.xy - dir, iMouse.xy + dir, fragCoord.xy) < 3.0;
    }
    if (highlight) col = vec3(0.0, 1.0, 0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

