vec4 getPixel(vec2 uv) {
    return textureLod(iChannel0, uv, 0.0);
}

// Simple sRGB->Y
float srgb_to_y(in vec3 colour)
{
    return dot(vec3(0.2126, 0.7152, 0.0722), colour);
}

// Simple polynomial weighting functions.
float linear_weight(in float ref, in float val, in float threshold)
{   
    float dist = 1.0 - (abs(ref - val) / threshold);

    return max(dist, 0.0);
}

float quadratic_weight(in float ref, in float val, in float threshold)
{
    float lw = linear_weight(ref, val, threshold);
    return lw * lw;
}

vec2 csquare(vec2 v) {
    return vec2(v.x * v.x - v.y * v.y, 2.0 * v.x * v.y);
}

vec4 bilat(vec2 uv)
{
    vec2 max_siz = vec2(24) / iResolution.xy;

    // reference value at centre of filter.
    vec4 ref = getPixel(uv);
    float ref_intensity = srgb_to_y(ref.xyz);
#if 1
    vec4 sum = vec4(0);

    for (int i = 0; i < estimator_taps; i++)
    {
        vec4 seed = vec4(1.2, 4.5, 6.7, 8.9) * float(i);
        vec2 ofs = vec2(gaussian(uv.xyyx, seed),
                        gaussian(uv.yxxy, seed));

        // point sample and intensity.
        vec4 spl = getPixel(uv + ofs * max_siz);
        float spl_intensity = srgb_to_y(spl.xyz);

        // Weight depends on sample distance from reference intensity.
        const float THRESHOLD = 0.4;
        float weight = quadratic_weight(ref_intensity, spl_intensity, THRESHOLD);

        weight *= 1.0 - clamp(abs(ref.a - spl.a), 0.0, 1.0);
        if (dot(ofs, ofs) > 0.0000001) sum.xy += normalize(csquare(ofs)) * weight;
    }
#else
    vec2 cs = vec2(cos(uv.x + iTime), sin(uv.x + iTime));
    vec2 sum = csquare(cs);
#endif
    return vec4(sum.xy, ref.aa);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    fragColor = vec4(bilat(uv).rgb, 1.0);
}

