const float blur_radius = 1.0;
const int blur_taps = 31;
const int stroke_length = 6;
const int estimator_taps = 64;


float nrand( vec2 n ) {	return fract(sin(dot(n.xy, vec2(12.9898, 78.233)))* 43758.5453); }
vec2 nrand2( vec2 n ) { return vec2(nrand(n * vec2(-3.2145, 1.2345)),
                                    nrand(n * vec2(-5.4321, 3.4521))); }

float gaussian(vec4 seed1, vec4 seed2) {
    return (nrand(seed1.zz + seed2.xy)
          + nrand(seed1.ww + seed2.yz)
          - nrand(seed1.xx + seed2.zw)
          - nrand(seed1.yy + seed2.wx)) * 0.25;
}

float pdf(float x) {
#if 0
    // pdf for s=0.1667: `sum(uniform(-.5,.5, 3)/3)`
    return 2.39365*exp(-18. * x * x);
#else
    // pdf for s=0.144: `sum(uniform(-.5,.5, 4)/4)`
    return 2.77043 * exp(-24.1126 * x * x);
#endif
}


vec4 gsample(sampler2D tex, vec2 uv, vec2 v, float radius) {
#if 1
    vec4 sum = vec4(0);
    float den = 0.0;

    for (int i=-blur_taps; i <= blur_taps; i++) {
        float f = float(i) / float(blur_taps);
        float w = pdf(f / radius);
        vec2 ofs = v * f;
        sum += texture(tex, uv + ofs) * w;
        den += w;
    }
    return sum / den;
#else
    return texture(tex, uv);
#endif
}

