    mat3 ret=mat3(0.);
    nlen=0;
    if(cval==0){
        ret[0][0]=float(48);
        nlen+=1;
        return ret;
    }
    int neg = 0;
    if(cval<0){
        neg=45;
        cval = abs(cval);
    }
    for(int i=0;i<9;i++){
        if(cval>0){
            int tv=cval%10;
            ret[i/3][i%3]=float(48+tv);
            cval=cval/10;
            nlen+=1;
        }
        else{
            break;
        }
    }
    if(neg!=0){
        ret[nlen/3][nlen%3]=float(neg);
        nlen+=1;
    }
    return ret;
}

