    int l_idx = idx.x+idx.y*cube_num_elems_use_sz;

    float fm = 1.;
    vec3 bcol = darkb;
    vec3 embcol = darkb;
    vec3 tacol = dark;
    vec3 tbcol = dark;

    vec4 ta = loadval16(unp_buf+idx*ivec2(2,1));
    vec4 tb = loadval16(unp_buf+idx*ivec2(2,1)+ivec2(1,0));
    ivec3 taa = ivec3(32,32,32);
    ivec3 tbb = ivec3(32,32,32);
    if(int(ta.x)>0){
        taa=ivec3(ta.xyz);
        tbb=ivec3(tb.xyz);
    }

    int tfo = int(ta.w);
    int tfob = int(tb.w);
    bool tfio = tfo>50&&tfob>50;
    if(tfo>50)tfo=tfo-100;
    bool fo = tfo>0;

    switch(tfo/10){
        case 1:bcol = col_red;tacol = -col_red*0.5;fm = 0.95;break;
        case 2:bcol = col_ora;tacol = -col_ora*0.5;fm = 0.95;break;
        case 3:bcol = col_yel;tacol = -col_yel*0.5;fm = 0.75;break;
    }

    switch(tfo-(tfo/10)*10){
        case 1:{vec3 tc = col_p;if(bcol==darkb){bcol = tc;fm = 0.5;}else{embcol = tc;tacol = tc;tbcol = tc;}}break;
        case 2:{vec3 tc = whitel;if(bcol==darkb){bcol = tc;fm = 0.85;}else{embcol = tc;tacol = tc;tbcol = tc;}}break;
    }

    if(fo){
        d = lines1d(p, text_len-cell_border, px*1./scale, 0.051, 0.005);
        col = mix(col, bcol, fm*d*panel*nidx);
    }

    if(tfio){
        d = lines1d(p, text_len-cell_border, px*1./scale, 0.051, 0.005);
        col = mix(col, green, 0.55*d*panel*nidx);
    }



    if(idx==ivec2(clc.xy)){
        d = lines1d(p, text_len-cell_border, px*1./scale, 0.051, 0.005);
        col = mix(col, whitel, clamp(1.-clc.z,0.,1.)*0.45*d*panel*nidx);
    }

    d = lines(p, text_len-cell_border, 1.*px*1./scale, 0.051, 0.005);
    col = mix(col, embcol, d*panel*nidx);
    d = 0.;

    vec3 tma = vec3(0.);
    vec3 tmb = vec3(0.);
    if(tfio){
        vec4 tv = loadval16(inp_buf1);
        vec4 tv2 = loadval16(inp_buf2);
        ivec3 tsa = ivec3(abs(tv.xyz));
        ivec3 tsb = ivec3(abs(tv2.xyz));
        const int ka_mul = 42;
        bvec3 tsta = bvec3(tsa.x>0&&tsa.x!=95&&tsa.x!=ka_mul,tsa.y>0&&tsa.y!=95&&tsa.y!=ka_mul,tsa.z>0&&tsa.z!=95&&tsa.z!=ka_mul);
        bvec3 tstb = bvec3(tsb.x>0&&tsb.x!=95&&tsb.x!=ka_mul,tsb.y>0&&tsb.y!=95&&tsb.y!=ka_mul,tsb.z>0&&tsb.z!=95&&tsb.z!=ka_mul);
        tma+=vec3(tsta);
        tmb+=vec3(tstb);
    }
    vec4 tv = loadval16(sest_idx_res);
    vec4 lsta = loadval16(is_toka);
    int flgsa = int(abs(lsta.w));
    vec4 lstb = loadval16(is_tokb);
    int flgsb = int(abs(lstb.w));
    ivec2 sid = ivec2(-1);
    if(tv.w>0.&&(flgsa!=20&&flgsb!=20)){
        sid = ivec2((int(abs(tv).x)<<8)+int(abs(tv).y),(int(abs(tv).z)<<8)+int(abs(tv).w));;

        if(sid==idx){
            d = lines1d(p, text_len-cell_border, px*1./scale, 0.051, 0.005);
            col = mix(col, green*green, 0.55*d*panel*nidx);
        }
    }
    d = print_text2d(iChannel3, p*1.25+vec2(2.25,0.5), taa, tma);
    if(rbrd) d = clamp(d,0.,1.)*0.;// text dfd fix, it still confused on cells
    col = mix(col, tacol, d*.47*panel*nidx);
    d = print_text2d(iChannel3, p*1.25+vec2(0.75,0.5), tbb, tmb);
    if(rbrd) d = clamp(d,0.,1.)*0.;// text dfd fix, it still confused on cells
    col = mix(col, tbcol, d*.47*panel*nidx);


    if(tv.w>0.&&(flgsa!=20&&flgsb!=20)){
        vec2 tpi = vec2(sid)*(text_len);

        vec2 tp = -tpi+(im)/scale-0.5*text_len;
        vec2 szi = vec2(borders,borders+top_panel);
        tp = clamp(tp,(-res+szi)/scale,vec2(0.));
        d = 1.-smoothstep(0.75,0.75+px/scale,length(tp+sop/scale));
        d = min(d,smoothstep(text_len.y*.25,text_len.y*.25+px/scale,abs(tp+sop/scale).y));
        col = mix(col, green*green*0.85, 0.75*d);
    }
    return col;
}


vec3 print_st(vec2 p, vec3 col);
vec4 draw(vec2 p, float px, vec2 ires){
    vec2 res=ires.xy/ires.y;

    vec2 im = ((loadval2x16(bar_xy,bar_xy_sc).xy*ires.xy)/ires.y);
    vec3 clc = loadval2x16(click_xy,click_xy_sc).xyz;

    vec4 bp = vec4(0.2, 0.045, vec2(0.,-res.y*0.5+0.045));

    vec3 col = white;

    vec2 sop = p;

    p+=0.5*res-vec2(borders*0.5);
    vec2 local_sc = (vec2(tables_size)-(res-vec2(borders,borders+top_panel))/(scale*text_len))*scale*text_len;
    p+=(im/res)*local_sc;

    vec2 op = p;
    float a = 0.;
    float d = 0.;

    vec2 tp = sop+vec2(0.,0.5*top_panel);
    d = lines(tp, res-vec2(borders,borders+top_panel), px, 0.01, 0.001);
    col = mix(col, dark, d);

    // not bool to avoid dfd bugs with texture font
    float panel = lines1d(tp, res-vec2(borders,borders+top_panel), px, 0.01, 0.001); 

    p=sop+vec2(0.,-0.5+0.5*top_panel+0.5*borders);
    if(p.y>0.&&p.x<0.){
        d = lines1d(p+2.*vec2(0.0151)*vec2(1.,-1.), vec2(0.0151), px, 0.01, 0.001);
        col = mix(col, whitel, 0.75*d);
        d = lines1d(p+2.*vec2(0.0151)*vec2(2.75,-1.), vec2(0.0151), px, 0.01, 0.001);
        col = mix(col, col_p, 0.5*d);
        d = lines1d(p+2.*vec2(0.0151)*vec2(5.5,-1.), vec2(0.0151), px, 0.01, 0.001);
        col = mix(col, col_yel, 0.65*d);
        d = lines1d(p+2.*vec2(0.0151)*vec2(7.25,-1.), vec2(0.0151), px, 0.01, 0.001);
        col = mix(col, col_ora, 0.65*d);
        d = lines1d(p+2.*vec2(0.0151)*vec2(9.,-1.), vec2(0.0151), px, 0.01, 0.001);
        col = mix(col, col_red, 0.65*d);
        float td = lines(p+2.*vec2(0.0151)*vec2(1.,-1.), vec2(0.0151), px, 0.01, 0.001);
        td = max(td,lines(p+2.*vec2(0.0151)*vec2(2.75,-1.), vec2(0.0151), px, 0.01, 0.001));
        td = max(td,lines(p+2.*vec2(0.0151)*vec2(5.5,-1.), vec2(0.0151), px, 0.01, 0.001));
        td = max(td,lines(p+2.*vec2(0.0151)*vec2(7.25,-1.), vec2(0.0151), px, 0.01, 0.001));
        td = max(td,lines(p+2.*vec2(0.0151)*vec2(9.,-1.), vec2(0.0151), px, 0.01, 0.001));
        col = mix(col, dark, td);
    }

    d = print_int(iChannel3, 35.*(p+vec2(0.025+0.246,0.015)),conet[0], 1.);
    d = max(d,print_int(iChannel3, 35.*(p+vec2(0.025+0.195,0.015)),conet[1], 1.));
    d = max(d,print_int(iChannel3, 35.*(p+vec2(0.025+0.14,0.015)),conet[2], 1.));
    col = mix(col, dark, 0.75*d);

    ivec2 idx = ivec2(op/(text_len*scale));
    float nidx = ((op.x<0.||op.y<0.)||(idx.x>=tables_size.x||idx.y>=tables_size.y))?0.:1.;

    p = fract(op/(text_len*scale))-0.5;
    p=p*text_len;

    bool rbrd = lines1d(p, text_len-cell_border, 1.*px*1./scale, 2.*0.051, 2.*0.005)<0.99;

    if(((idx.x)%4)==0){
        //d = lines1d(p, text_len-cell_border, px*1./scale, 0.051, 0.005);
        //col = mix(col, whitel, 0.123*d*panel*nidx);
    }else
    if(((idx.y*2)%3)==0){
        d = lines1d(p, text_len-cell_border, px*1./scale, 0.051, 0.005);
        col = mix(col, darkb, 0.073*d*panel*nidx);
    }

    col = print_tk(sop+0.5*res-vec2(borders*0.5), res, (im/res)*local_sc, p, idx, col, rbrd, panel, nidx, px, clc);

    im*=(1.-0.5*2.*blines);
    if(step(res.x*0.5-sop.x,blines*0.5)>0.5){
        col = mix(col, dark, 0.5*step(abs(sop.y-im.y+0.5*res.y-0.5*blines),blines));
    }
    if(step(res.y*0.5+sop.y,blines*0.5)>0.5){
        col = mix(col, dark, 0.5*step(abs(sop.x-im.x+0.5*res.x-0.5*blines*res.x),blines));
    }

    col = print_st(sop, col);



    float vignetteAmt = 1. - dot(sop * 0.65, sop * 0.65);
    col *= max(vignetteAmt,0.6);
    col = col / 2. + col*col;
    col += (hash12(sop*ires*2.33) - .5)*.07;
    col = clamp(col, vec3(0.), vec3(1.));

    return vec4(col,a);
}

