    if(side_idx!=cube_side){
        if(side_idx==cube_side+1&&load_state==0){
            fragColor = mapabuf(ipx);
            //fragColor = self;
        }
        return;
    }


    if(ipx==side_sz-1)
    {
        fragColor = vec4(1.+clamp(self.x,0.,3.), 1.+clamp(self.y,0.,3.), 0., 1.); // (map_status, state, 0., reset)
        return;
    }

    process_UI(fragColor, fragCoord);

