    ivec2 tidx_new_reg = ivec2(tb_reg.x+tb_reg.y,cube_num_elems_use_sz);


    if(load_state==1)
    {

        ivec2 reg = ivec2(0.);

        reg = ivec2(ta_reg.x,ta_reg.x+ta_reg.y);
        if((ipx.x<reg.y&&ipx.y<(reg.y-reg.x))&&(ipx.x>=reg.x&&ipx.y>=0))
        {
            ivec2 local_ipx = ivec2(ipx.x-reg.x,ipx.y);
            int local_idx = local_ipx.x+local_ipx.y*(reg.y-reg.x);
            ivec3 ta = get_token_left(local_idx);
            fragColor = vec4(float(ta.x)+0.5,float(ta.y)+0.5,float(ta.z)+0.5,0.);
        } else {
            reg = ivec2(tb_reg.x,tb_reg.x+tb_reg.y);
            if((ipx.x<reg.y&&ipx.y<(reg.y-reg.x))&&(ipx.x>=reg.x&&ipx.y>=0))
            {
                ivec2 local_ipx = ivec2(ipx.x-reg.x,ipx.y);
                int local_idx = local_ipx.x+local_ipx.y*(reg.y-reg.x);
                ivec3 tb = get_token_right(local_idx);
                fragColor = vec4(float(tb.x)+0.5,float(tb.y)+0.5,float(tb.z)+0.5,0.);
            } else{
                // nop
            }
        }

    } else
    if(load_state==2&&!map_done)
    {
        if((ipx.x<cube_num_elems_use_sz&&ipx.y<cube_num_elems_use_sz))
        {
            ivec2 local_ipx = ipx%cube_num_elems_use_sz;
            int l_idx = local_ipx.x+local_ipx.y*cube_num_elems_use_sz;
            fragColor = restore_db(l_idx);
        }
    }

}
