// 16bit 2x16 save
// scale max 0xfffff
// val min/max= +-scale
// loss <0.000001 on scale=1
vec4 save2x16(vec4 val, ivec2 tpx, ivec4 ipx, vec4 scale) {
    vec4 tv = clamp((val/scale+1.)*0.5,0.,1.);
    ivec4 itv=ivec4(tv*float(0x3fffff));
    ivec4 ret =ivec4(0);
    if(tpx==ipx.xy)ret = (itv>>11);
    else ret = (itv)&0x7ff;
    return vec4(ret-0x7ff/2);
}

bool is_reset(){
    return loadval16(resetx).w<0.5;
}

vec4 loadval2x16(ivec4 ipx, vec4 scale) {
    if(is_reset())return vec4(0.);
    ivec4 a = ivec4(loadval16(ipx.xy)+float(0x7ff/2));
    ivec4 b = ivec4(loadval16(ipx.zw)+float(0x7ff/2));
    vec4 ret = scale*2.*(vec4((a<<11)+b)/float(0x3FFFFF)-0.5);
    if(abs(ret.x)<0.000035)ret.x=0.; // 16-bit scale for loadval2x16
    if(abs(ret.y)<0.000035)ret.y=0.;
    if(abs(ret.z)<0.000035)ret.z=0.;
    if(abs(ret.w)<0.000035)ret.w=0.;
    return ret;
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

float lines1d(vec2 p, vec2 bsz, float px, float a, float b) {
    float d = 0.;
    d = max(d, smoothstep(px, -px, (sdBox(p, bsz / 2.) - a) - b));
    return d;
}

vec2 postmove_bar(vec2 mzw) {
    float fps = clamp(1./max(iTimeDelta,0.001),1.,9999.);
    vec2 pm = loadval2x16(click_pm,click_pm_sc).xy;
    for(int i = 0; i<2; i++){
        if(mzw[i]<0.){
            mzw[i]+= max((-1.5*(mzw[i])/fps+0.00001),0.00001);
            if(mzw[i]>0.)mzw[i]=0.;
        }else{
            if(mzw[i]>1.){
                mzw[i]-= max((1.5*(mzw[i]-1.)/fps+0.00001),0.00001);
                if(mzw[i]<1.)mzw[i]=1.;
            }else{

            }
        }
        mzw[i]+=pm[i];
    }
    return mzw;
}

vec4 process_bar(out ivec2 cidx, out float mtimer, out bool dx_bar, out bool dy_bar, out bool panel) {
    bool reset = (iFrame-wfr)<=0||is_reset();
    panel = false;dx_bar=false;dy_bar=false;
    if(reset){
        cidx = ivec2(-1000.);
        mtimer = 0.;
        return vec4(0.);
    }
    vec4 self = loadval2x16(bar_xy,bar_xy_sc);
    vec4 ret = self;
    vec2 screen_ires = vec2(textureSize(iChannel3,0).xy);
    vec2 res=screen_ires.xy/screen_ires.y;
    vec2 im=iMouse.xy/screen_ires.y-0.5*res;
    vec2 im_local=iMouse.xy/screen_ires.xy;

    vec4 tclc = loadval2x16(click_xy,click_xy_sc);
    cidx = ivec2(tclc.xy);
    mtimer = tclc.z;
    if(iMouse.z<=0.)mtimer+=iTimeDelta;

    if(iMouse.z>0.){
        vec2 lc_local = self.zw;
        if(lc_local.x<=0.)lc_local.xy = iMouse.zw/screen_ires.xy;

        vec2 lc = (lc_local.xy*screen_ires.xy)/screen_ires.y-0.5*res;

        panel = lines1d(lc+vec2(0.,0.5*top_panel), res-vec2(borders,borders+top_panel), 0.0001, 0.01, 0.001)>0.00001; 
        dx_bar = step(res.x*0.5-lc.x,blines*0.5)>0.5;
        dy_bar = step(res.y*0.5+lc.y,blines*0.5)>0.5;

        if(dx_bar||dy_bar){
            if(dx_bar)ret.y=(im_local.y)*(1.+0.5*2.*blines)-0.5*blines;
            if(dy_bar)ret.x=(im_local.x)*(1.+0.5*2.*blines)-0.5*blines;
        }else{
            if(panel){
                vec2 tp = lc;
                tp+= 0.5*res-vec2(borders*0.5);
                vec2 t_im = ((ret.xy*screen_ires.xy)/screen_ires.y);

                vec2 local_sc = (vec2(tables_size)-(res-vec2(borders,borders+top_panel))/(scale*text_len))*scale*text_len;
                tp+= t_im*vec2(local_sc/res);

                if(self.z<=0.){
                    ivec2 idx = ivec2(tp/(text_len*scale));
                    mtimer=0.;
                    cidx = idx;
                }
                vec4 limm = loadval2x16(im_xy,im_xy_sc);
                vec2 imov = vec2(0.);
                if(self.z>0.)imov = (im_local-limm.xy)*1.;
                if(abs(imov.x)<0.000035)imov.x=0.; // 16-bit scale for 2x16
                if(abs(imov.y)<0.000035)imov.y=0.; // 16-bit scale for 2x16

                ret.xy+=-(imov.xy)*1./(local_sc/res);
            }
        }
        ret.zw = lc_local;

    }else{
        ret.zw = iMouse.zw/screen_ires.xy;
    }
    return ret;

}

vec4 postmove_im() {
    vec4 self = loadval2x16(click_pm,click_pm_sc);
    vec4 last_m = loadval2x16(click_xy,click_xy_sc);
    vec4 bar = loadval2x16(bar_xy,bar_xy_sc);
    vec4 ret = self;
    float fps = clamp(1./max(iTimeDelta,0.001),1.,9999.);
    ivec2 tp; float tm; bool dx; bool dy; bool pa;
    vec4 tc = process_bar(tp, tm, dx, dy, pa);
    if(iMouse.z<=0.){
        vec2 sdir = self.xy;
        if(bar.z>0.){
            sdir = (bar.xy-self.zw);
            sdir = 10.*scale*(sdir);
        }else {
            sdir = sdir*(0.999-0.169*clamp(25./fps,0.01,1.));
            if(bar.x<0.||bar.x>1.)sdir.x*=(0.999-0.499*clamp(25./fps,0.01,1.));
            if(bar.y<0.||bar.y>1.)sdir.y*=(0.999-0.499*clamp(25./fps,0.01,1.));
        }
        ret.xy = sdir;
    }else{
        if(!(pa||dx||dy)){
            vec2 sdir = self.xy;
            vec4 tim = loadval2x16(im_xy,im_xy_sc);
            if(bar.z>0.&&tim.z<=0.){
                sdir = (bar.xy-self.zw);
            }else {
                sdir = sdir*(0.999-0.169*clamp(25./fps,0.01,1.));
                if(bar.x<0.||bar.x>1.)sdir.x*=(0.999-0.499*clamp(25./fps,0.01,1.));
                if(bar.y<0.||bar.y>1.)sdir.y*=(0.999-0.499*clamp(25./fps,0.01,1.));
            }
            ret.xy = sdir;
        }else{
            ret.xy = vec2(0.);
        }
        ret.zw = bar.xy;
    }
    if(abs(ret.x)<0.000035)ret.x=0.; // 16-bit scale for 2x16
    if(abs(ret.y)<0.000035)ret.y=0.; // 16-bit scale for 2x16
    return ret;
}

