const int Key_Backspace = 8, Key_Enter = 13, Key_Shift = 16, Key_Delete = 46, 
Key_0 = 48, Key_1 = 49, Key_2 = 50, Key_3 = 51, Key_4 = 52,
Key_5 = 53, Key_6 = 54, Key_7 = 55, Key_8 = 56, Key_9 = 57, 
