Key_X = 88, Key_Y = 89, Key_Z = 90,
Key_Numpad0 = 96, Key_Numpad1 = 97, Key_Numpad2 = 98, Key_Numpad3 = 99,
Key_Numpad4 = 100, Key_Numpad5 = 101, Key_Numpad6 = 102, Key_Numpad7 = 103, 
Key_Numpad8 = 104, Key_Numpad9 = 105, Key_NumpadMultiply = 106;

const int ka_mul = 42;
bool key(int K){return step(0.5, texelFetch(keyboard_texture, ivec2(K, 0), 0).x)>0.5;}
int get_key(){
    if(key(Key_Backspace))return -1;
    if(key(Key_Delete))return -1;
    if(key(Key_NumpadMultiply))return ka_mul;
    bool sft = key(Key_Shift);
    int kf = 0;
    for(int i=Key_0;i<=Key_9+ANGLE_loops;i++){
        if(key(i)||key(i-Key_0+Key_Numpad0)){
            kf=i;
            break;
        }
    }
    if(kf>0){
        if(sft&&kf==Key_8)return ka_mul;
        else return kf;
    }
    for(int i=Key_A;i<=Key_Z+ANGLE_loops;i++){
        if(key(i)){
            kf=i;
            break;
        }
    }
    if(kf>0){
        if(!sft){
            kf+=32;
            return kf;
        }
    }
    return kf;

}

vec4 proinp_left()
{
    bool reset = (iFrame-wfr)<=0||is_reset();
    reset=reset||key(Key_Backspace)||key(Key_Delete);
    if(reset){
        return vec4(95.,95.,95.,0.005);
    }
    vec4 self = loadval16(inp_buf1);
    int tk = get_key();
    if(self.w<0.&&tk>0){
        return self;
    }
    self.w=abs(self.w);
    ivec3 skey = ivec3(self.xyz);
    bvec3 st = bvec3(fract(self.w/1000.)*10.>0.5,fract(self.w/100.)*10.>0.5,fract(self.w/10.)*10.>0.5);
    bvec3 ost = st;
    if(tk<0){
        skey = ivec3(95,95,95);
        st = bvec3(false);
    }else{
        if(tk>0){
            if(!st.x){skey[0]=tk;st.x = true;}
            else{
                if(!st.y){skey[1]=tk;st.y = true;}
                else{
                    if(!st.z){skey[2]=tk;st.z = true;}
                }
            }
        }
    }

    float est = 0.;
    est+=100.*float(st.x);
    est+=10.*float(st.y);
    est+=1.*float(st.z);
    float sig = 1.;
    if(tk!=0&&(!ost.x||!ost.y||!ost.z))sig=-1.;
    return vec4(vec3(skey)+0.5,sig*(est+0.005));
}

