            ivec3 st = ivec3(self.xyz);
            int cidx = int(self.w);

            if((flgsa==1&&(flgsb==1||flgsb==2||flgsb==3||flgsb==4||flgsb==5||flgsb==6||flgsb==30||flgsb==0))||
                (flgsb==1&&(flgsa==1||flgsa==2||flgsa==3||flgsa==4||flgsa==5||flgsa==6||flgsa==30))){
                ivec3 ttt = ivec3(0);
                if(ipx.x%2==0) ttt = ivec3(lsta.xyz); else ttt = ivec3(lstb.xyz);
                bvec3 tst = not(bvec3(ttt.x>0&&ttt.x!=95&&ttt.x!=ka_mul,ttt.y>0&&ttt.y!=95&&ttt.y!=ka_mul,ttt.z>0&&ttt.z!=95&&ttt.z!=ka_mul));

                bvec3 tst_t = bvec3(ttt.x==st.x||tst.x,ttt.y==st.y||tst.y,ttt.z==st.z||tst.z);
                if(all(tst_t)){
                    if(cidx>50)cidx=cidx-100;
                    cidx+=100;
                }else {
                    if(cidx>50)cidx=cidx-100;
                }
            }else{
                if(flgsa==20||flgsb==20){
                    if(cidx>50)cidx=cidx-100;
                }
            }
            bool kd = key(Key_Backspace)||key(Key_Delete);
            if(cidx>50&&kd)cidx=cidx-100;

            return vec4(vec3(st)+0.5,sign(float(cidx))*(float(abs(cidx))+0.5));
       }else return vec4(0.,0.,0.,0.);
   }

   return vec4(0.,0.,0.,0.);

}

vec4 is_token(int ti){
    bool reset = (iFrame-wfr)<=0||is_reset();
    reset=reset||key(Key_Backspace)||key(Key_Delete);
    if(reset){
        return vec4(0.,0.,0.,0.);
    }

    ivec2 new_db_reg = ivec2(0,cube_num_elems_use_sz);
    ivec2 ta_reg = ivec2(new_db_reg.x+new_db_reg.y,0);
    ivec2 tb_reg = ivec2(ta_reg.x+cube_tokens_use_sz,0);

    ivec2 sidx = is_toka;
    ivec2 inp = inp_buf1;
    ivec2 tt_reg = ta_reg;
    if(ti==1){
        sidx = is_tokb;
        inp = inp_buf2;
        tt_reg = tb_reg;
    }
    vec4 self = loadval16(sidx);
    ivec3 st = ivec3(self.xyz);
    int flgs = int(abs(self.w));
    if(flgs>=1&&flgs<6)flgs+=1;
    bool tkey = key(Key_Enter);
    if(flgs>=4){
        if(!(self.w>0.)&&tkey){
            flgs=4;
        }
    }
    vec4 pbs = loadval16(inp);
    ivec3 ch = ivec3(abs(pbs.xyz));
    bvec3 tst = not(bvec3(ch.x>0&&ch.x!=95&&ch.x!=ka_mul,ch.y>0&&ch.y!=95&&ch.y!=ka_mul,ch.z>0&&ch.z!=95&&ch.z!=ka_mul));
    if(ch!=st&&!all(tst)){
        st = ch;
        int fidx = 0;
        bool fbd = false;
        for(int i=0;i<tokens+ANGLE_loops&&!fbd;i++){
            ivec2 ttidx = ivec2(i%32,i/32); 
            vec4 tt = loadval16(ivec2(tt_reg) + ttidx);
            ivec3 ttt = ivec3(tt.xyz);
            bvec3 tst_t = bvec3(ttt.x==st.x||tst.x,ttt.y==st.y||tst.y,ttt.z==st.z||tst.z);
            if(all(tst_t)){
                fbd = true;
                fidx = i;
                break;
            }
        }
        if(fbd){
            flgs=1;
        }else{
            flgs=20;
        }
    }else{
        if(ch==ivec3(ka_mul,95,95)||ch==ivec3(ka_mul,ka_mul,95)||ch==ivec3(ka_mul)){
            flgs=30;
        }
    }
    return vec4(vec3(st)+0.5,(float(tkey)*2.-1.)*(float(flgs)+0.5));
}

vec4 sest_idx_reg_fn(ivec2 ipx){
    bool reset = (iFrame-wfr)<=0||is_reset();
    reset=reset||key(Key_Backspace)||key(Key_Delete);
    if(reset){
        return vec4(0.,0.,0.,0.);
    }

    vec4 self = loadval16(ipx);
    ivec2 nfidx = ivec2((int(abs(self).x)<<8)+int(abs(self).y),(int(abs(self).z)<<8)+int(abs(self).w));;
    vec4 ls = loadval16(sest_idx_res);
    ivec2 lfidx = ivec2((int(abs(ls).x)<<8)+int(abs(ls).y),(int(abs(ls).z)<<8)+int(abs(ls).w));;
    bvec4 ft = greaterThan(self,vec4(0.));
    ivec2 onfidx = lfidx;
    int oix = onfidx.x+onfidx.y*cube_num_elems_use_sz;

    ivec2 lipx = ipx-sest_idx_reg;
    int l_idx = lipx.x+lipx.y*sest_reg_sz.x;

    vec4 lsta = loadval16(is_toka);
    int flgsa = int(abs(lsta.w));
    vec4 lstb = loadval16(is_tokb);
    int flgsb = int(abs(lstb.w));

    ivec2 fidx = ivec2(0);

    bool ra = (flgsa==2&&(flgsb==2||flgsb==3||flgsb==4||flgsb==5||flgsb==6||flgsb==30||flgsb==0))||(flgsb==2&&(flgsa==2||flgsa==3||flgsa==4||flgsa==5||flgsa==6||flgsa==30));
    bool rb = (flgsa==4&&(flgsb==4||flgsb==30||flgsb==0))||(flgsb==4&&(flgsa==4||flgsa==30));
    bool lke = ft.x;
    bool tkey = key(Key_Enter);
    if(rb)ft.x = false;else if(!ft.x)ft.x = tkey;
    rb = rb&&(tkey||lke)&&ls.w>0.;

    if(ra||rb){
        ft.zw = bvec2(false);
        int rsz = sest_reg_sz.x*sest_reg_sz.y;
        int lfst = num_elems/rsz;
        int olfst = lfst;
        if(l_idx==rsz-1)lfst+=num_elems-lfst*rsz;
        bool fndt = false;
        for(int i=0;i<lfst+ANGLE_loops;i++){
            int ti = i+l_idx*olfst;
            ivec2 tidx = ivec2(ti%cube_num_elems_use_sz, ti/cube_num_elems_use_sz);
            vec4 ta = loadval16(unp_buf+tidx*ivec2(2,1));
            vec4 tb = loadval16(unp_buf+tidx*ivec2(2,1)+ivec2(1,0));
            if(!(ta.x>0.)||!(tb.x>0.))break;
            int tfo = int(ta.w);
            int tfob = int(tb.w);
            bool tfio = tfo>50&&tfob>50;
            if(tfio){
                if(rb){
                    int nix = tidx.x+tidx.y*cube_num_elems_use_sz;
                    if(nix<=oix){
                        fidx=tidx;
                        fndt=true;
                        continue;
                    }else{
                        fidx=tidx;
                        fndt=true;
                        break;
                    }
                }else{
                    fidx=tidx;
                    fndt=true;
                    break;
                }
            }
        }
        ft.w=fndt;

    }
    nfidx=fidx;

    vec4 tft = vec4(ft)*2.-1.;
    return tft*vec4(float(nfidx.x>>8)+0.5,float(nfidx.x&0xff)+0.5,float(nfidx.y>>8)+0.5,float(nfidx.y&0xff)+0.5);
}

vec4 sest_idx_res_fn(){
    bool reset = (iFrame-wfr)<=0||is_reset();
    reset=reset||key(Key_Backspace)||key(Key_Delete);
    if(reset){
        return vec4(0.,0.,0.,0.);
    }

    vec4 self = loadval16(sest_idx_res);
    ivec2 nfidx = ivec2((int(abs(self).x)<<8)+int(abs(self).y),(int(abs(self).z)<<8)+int(abs(self).w));;
    ivec2 onfidx = nfidx;
    int oix = onfidx.x+onfidx.y*cube_num_elems_use_sz;
    bvec4 ft = greaterThan(self,vec4(0.));

    vec4 lsta = loadval16(is_toka);
    int flgsa = int(abs(lsta.w));
    vec4 lstb = loadval16(is_tokb);
    int flgsb = int(abs(lstb.w));

    bool ra = (flgsa==3&&(flgsb==3||flgsb==4||flgsb==5||flgsb==6||flgsb==30||flgsb==0))||(flgsb==3&&(flgsa==3||flgsa==4||flgsa==5||flgsa==6||flgsa==30));
    bool rb = (flgsa==5&&(flgsb==5||flgsb==30||flgsb==0))||(flgsb==5&&(flgsa==5||flgsa==30));
    bool lke = ft.x;
    bool tkey = key(Key_Enter);
    if(rb)ft.x = false;else if(!ft.x)ft.x = tkey;
    rb = rb&&(tkey||lke);

    if(ra||rb){
        ft.zw = bvec2(false);
        bool brk = false;
        for(int j=0;j<sest_reg_sz.y+ANGLE_loops&&!brk;j++){
            for(int i=0;i<sest_reg_sz.x+ANGLE_loops&&!brk;i++){ 
                vec4 tv = loadval16(sest_idx_reg+ivec2(i,j));

                if(tv.w>0.){
                    ivec2 nw = ivec2((int(abs(tv).x)<<8)+int(abs(tv).y),(int(abs(tv).z)<<8)+int(abs(tv).w));;
                    if(rb){
                        int nix = nw.x+nw.y*cube_num_elems_use_sz;
                        if(nix<=oix){
                            ft.w = true;
                            continue;
                        }else{
                            nfidx=nw;
                            ft.w = true;
                            brk = true;
                        }
                    }else{
                        nfidx=nw;
                        ft.w = true;
                        brk = true;
                    }
                }
            }
        }
        if(!ft.w){ft.z=true;}
    }

    vec4 tft = vec4(ft)*2.-1.;
    return tft*vec4(float(nfidx.x>>8)+0.5,float(nfidx.x&0xff)+0.5,float(nfidx.y>>8)+0.5,float(nfidx.y&0xff)+0.5);
}


void process_UI( inout vec4 fragColor, in vec2 fragCoord )
{

    ivec2 ipx = ivec2(fragCoord);

    if(ipx==resetx){
        fragColor = vec4(0.,0.,0.,1.);
        return;
    }

    if(ipx==bar_xy.xy||ipx==bar_xy.zw){
        ivec2 tp; float tm; bool dx; bool dy; bool pa;
        fragColor = process_bar(tp,tm, dx, dy, pa);
        if(iMouse.z>0.&&dx){
            fragColor.x = postmove_bar(fragColor.xy).x;
        }else if(iMouse.z>0.&&dy){
            fragColor.y = postmove_bar(fragColor.xy).y;
        }else if(iMouse.z>0.&&!pa&&!dx&&!dy){
            fragColor.xy = postmove_bar(fragColor.xy);
        }else if(iMouse.z<=0.){
            fragColor.xy = postmove_bar(fragColor.xy);
        }
        fragColor = save2x16(fragColor, ipx, bar_xy, bar_xy_sc);
        return;
    }

    if(ipx==click_xy.xy||ipx==click_xy.zw){
        ivec2 tp; float tm; bool dx; bool dy; bool pa;
        vec4 tc = process_bar(tp,tm, dx, dy, pa);
        fragColor = vec4(vec2(tp)+0.5,tm,0.);
        fragColor = save2x16(fragColor, ipx, click_xy, click_xy_sc);
        return;
    }

    if(ipx==im_xy.xy||ipx==im_xy.zw){
        vec2 screen_ires = vec2(textureSize(iChannel3,0).xy);
        fragColor = vec4(iMouse.xyzw/screen_ires.xyxy);
        fragColor = save2x16(fragColor, ipx, im_xy, im_xy_sc);
        return;
    }

    if(ipx==click_pm.xy||ipx==click_pm.zw){
        fragColor = postmove_im();
        fragColor = save2x16(fragColor, ipx, click_pm, click_pm_sc);
        return;
    }

    if(ipx==inp_buf1){
        fragColor = proinp_left();
        return;
    }
    if(ipx==inp_buf2){
        fragColor = proinp_right();
        return;
    }

    if(ipx.x>=unp_buf.x&&ipx.x<unp_buf.x+unp_reg.x&&ipx.y>=unp_buf.y&&ipx.y<unp_buf.y+unp_reg.y){
        fragColor = unp_reg_fn(ipx);
        return;
    }
    if(ipx==is_toka){
        fragColor = is_token(0);
        return;
    }
    if(ipx==is_tokb){
        fragColor = is_token(1);
        return;
    }
    if(ipx.x>=sest_idx_reg.x&&ipx.x<sest_idx_reg.x+sest_reg_sz.x&&ipx.y>=sest_idx_reg.y&&ipx.y<sest_idx_reg.y+sest_reg_sz.y){
        fragColor = sest_idx_reg_fn(ipx);
        return;
    }
    if(ipx==sest_idx_res){
        fragColor = sest_idx_res_fn();
        return;
    }

}
















