ivec3 get_token_left(int idx){
    int ida2 = idx/(map_ta1_len*4);
    int ida1 = idx%(map_ta1_len*4);

    int a = get_8bits_byidx_map_ta2(ida2*2);
    int b = get_8bits_byidx_map_ta2(ida2*2+1);
    int c = get_8bits_byidx_map_ta1(ida1);
    return ivec3(a,b,c);
}

ivec3 get_token_right(int idx){
    int ida2 = idx/(map_tb1_len*4);
    int ida1 = idx%(map_tb1_len*4);

    int a = get_8bits_byidx_map_tb2(ida2*2);
    int b = get_8bits_byidx_map_tb2(ida2*2+1);
    int c = get_8bits_byidx_map_tb1(ida1);
    return ivec3(a,b,c);
}

