int get_by_idx(int idx, int slf, int nsfl, int bits){
  int local_idx = idx*bits/8;
  int local_ndx = idx*bits-local_idx*8;
  int to_read = 8-local_ndx;

  int tval = slf;
  to_read+=-bits;
  to_read=max(to_read, 0);
  int lbs = min(8-local_ndx, bits);
  int m = (1 << lbs) - 1;
  int v = (tval >> to_read) & m;
  if(8-local_ndx<bits)
  {
    local_idx = (idx*bits+lbs)/8;
    tval = nsfl;
    to_read = 8-(bits-(8-local_ndx));
    m = (1 << (8-to_read)) - 1;
    v = (v<<(8-to_read))+((tval >> (to_read)) & m);
  }
  return v;
}


ivec3 get_mxz(int idx){
    int fpos = 0;
    int floc[] = int[](0,0,0,0,0,0,0,0);
    for(int i=0;i<1024+ANGLE_loops;i++){
        for(int j=0;j<8+ANGLE_loops;j++)
        {
            int tval = -1;
            int ntval = -1;
            tval=get_8bits_byidx_map_map_uint_angle(((i+0)*size_map_bits_size)/8,j);
            if((i+1)<1024){
                ntval=get_8bits_byidx_map_map_uint_angle(((i+1)*size_map_bits_size)/8,j);
            }else {ntval = 0;}
            int tsz=get_by_idx(i, tval, ntval, size_map_bits_size);
            fpos+=tsz;
            if(idx<fpos){
                return ivec3(j,floc[j]+idx-(fpos-tsz),i);
            }
            floc[j]+=tsz;
        }
    }
    return ivec3(-1,-1,-1);
}

ivec4 to8bitsa(uint val){
  ivec4 ret = ivec4(0);
  for(int i=0;i<4+ANGLE_loops;i++){
    ret[i] = int(val>>((3-i)*8))&0xFF;
  }
  return ret;
}

int get_map_by_idx(int midx, int lidx){

    ivec2 m1p[] = ivec2[](ivec2(1,0), ivec2(0,1), ivec2(1,1), ivec2(0,0));

    int xidx = lidx*map_bits_rnge;
    int xidx2 = (lidx+1)*map_bits_rnge;
    int xidx4 = xidx/32;
    ivec2 tidx = ivec2(xidx4%32, (xidx4/32));

    int xidx42 = xidx2/32;
    ivec2 tidx2 = ivec2(xidx42%32, (xidx42/32));

    float tval=texelFetch(iChannel3, m1p[midx/2]*ivec2(33,33)+tidx, 0)[midx%2];
    float ntval=texelFetch(iChannel3, m1p[midx/2]*ivec2(33,33)+tidx2, 0)[midx%2];

    int rde = xidx4*32;
    int tsft = xidx-rde;
    int ldxi = tsft/8;
    int ldxi2 = (tsft+map_bits_rnge)/8;

    ivec4 a1 = to8bitsa(floatBitsToUint(tval));
    ivec4 a2 = to8bitsa(floatBitsToUint(ntval));

    int tarr[] = int[](a1[0],a1[1],a1[2],a1[3],a2[0],a2[1],a2[2],a2[3]);
    int tval8 = tarr[ldxi];
    int ntval8 = tarr[ldxi2];

    return get_by_idx(lidx, tval8, ntval8, map_bits_rnge);


}

vec4 restore_db(int idx){
    if(idx>=num_elems){
        return vec4(-1.);;
    }

    ivec3 fi = get_mxz(idx);
    int tv = get_map_by_idx(fi.x, fi.y);
    int tvf = (fi.x*dvdr+tv);

    // return token left, token right ID
    vec4 tret = vec4(vec2(fi.z>>8,fi.z&0xff)+0.5,vec2(tvf>>8,tvf&0xff)+0.5);
    return tret;

}


vec4 mapabuf(ivec2 ipx){

    vec4 ret = vec4(0.);
    int lidx = ipx.x+ipx.y*side_sz.x;
    int jdx = lidx/128;
    if(jdx>7)return ret;
    float tret = texelFetch(iChannel3,ivec2(lidx%128,33*2+3+jdx/2),0)[jdx%2];
    ret = vec4(to8bitsa(floatBitsToUint(tret)))+0.5;
    return ret;
}

