int get_8bits_byidx_map_ta2(int idx){
    int local_idx = idx/4;
    uint val = get_map_ta2(local_idx);
    val = (val>>((3-(idx%4))*8))&0xFFu;
    return int(val);
}

int get_8bits_byidx_map_ta1(int idx){
    int local_idx = idx/4;
    uint val = get_map_ta1(local_idx);
    val = (val>>((3-(idx%4))*8))&0xFFu;
    return int(val);
}

int get_8bits_byidx_map_tb2(int idx){
    int local_idx = idx/4;
    uint val = get_map_tb2(local_idx);
    val = (val>>((3-(idx%4))*8))&0xFFu;
    return int(val);
