int get_8bits_byidx_map_map_uint_angle(int idx, int jdx){
    int local_idx = idx/4;
    int tidx = local_idx+jdx*128;
    ivec2 ipx = ivec2(tidx%side_sz.x,tidx/side_sz.x);
    vec4 tval = loadval16_s2(ipx);
    return int(tval[idx%4]);
}

