const float scale = .051;

const int num_elems = 28476;
const int cube_num_elems_use_sz = 169; // sqrt(num_elems)

const int tokens = 1024;
const int cube_tokens_use_sz = 32; // sqrt(tokens)

const int map_bits_rnge = 7;
const int size_map_bits_size = 4;
const int dvdr = (1<<map_bits_rnge);

const int cube_side = 0;

#define ANGLE_loops min(iFrame,0)

void convert_cube_uv_to_xyz(in int index, in vec2 uv, out vec3 rd);
vec4 read_cubem(samplerCube textr, ivec2 px, int side_idx, ivec2 side_res){
    px.y = (side_res.y-1) - px.y; //vflip
    vec2 tuv = (vec2(px)+0.5)/vec2(side_res);
    vec3 trd;
    convert_cube_uv_to_xyz(side_idx, tuv, trd);
    return textureLod(textr, trd, 0.);
}

// https://www.shadertoy.com/view/NttGWr
int getside_idx(vec3 rd){
    int Front_side = 0;
    int Back_side = 1;
    int Up_side = 2;
    int Down_side = 3;
    int Left_side = 4;
    int Right_side = 5;

    if(length(rd)<0.0001)return -1;

    vec3 a = abs(rd);
    bvec3 ip = greaterThan(rd,vec3(0.));
    if (ip.x && a.x >= a.y && a.x >= a.z) {return Front_side;}else
    if (!ip.x && a.x >= a.y && a.x >= a.z) {return Back_side;}else
    if (ip.y && a.y >= a.x && a.y >= a.z) {return Up_side;}else
    if (!ip.y && a.y >= a.x && a.y >= a.z) {return Down_side;}else
    if (ip.z && a.z >= a.x && a.z >= a.y) {return Left_side;}else
    if (!ip.z && a.z >= a.x && a.z >= a.y) {return Right_side;}
}

void convert_cube_uv_to_xyz(in int index, in vec2 uv, out vec3 rd)
{
  float uc = 2.0 * uv.x - 1.0;
  float vc = 2.0 * uv.y - 1.0;
  rd=vec3(0.0001,0.9999,0.0001);
  if(index==0){rd=normalize(vec3(1.0, vc, -uc));return;}
  if(index==1){rd=normalize(vec3(-1.0, vc, uc));return;}
  if(index==2){rd=normalize(vec3(uc, 1.0, -vc));return;}
  if(index==3){rd=normalize(vec3(uc,-1.0, vc));return;}
  if(index==4){rd=normalize(vec3(uc, vc, 1.0));return;}
  if(index==5){rd=normalize(vec3(-uc, vc, -1.0));return;}
}

