const int MAX_STEPS = 20;
const float HIT_DISTANCE = 1e-2;
const float TRACE_DISTANCE = 20.0;
// Focal Distance
const float focalDistance = 1.5;
// Scene Geometry
const int SPHERE_GRID_RESOLUTION = 3;
const int COLUMNS = SPHERE_GRID_RESOLUTION;
const int ROWS = SPHERE_GRID_RESOLUTION;
const int COUNT = COLUMNS * ROWS;
const float SPHERE_RADIUS = 0.2;
const float PI = 3.141;

float random(float x) {
  return fract(sin(x) * 43758.5453);
}

float perlinNoise(float x) {
  float x0 = floor(x);
  float x1 = x0 + 1.0;

  float t = x - x0;

  float r0 = random(x0);
  float r1 = random(x1);

  float n0 = mix(r0, r1, smoothstep(0.0, 1.0, t));

  return n0;
}

vec3 rotateRayDirection(vec3 rayDirection) {
  // Get the normalized mouse position
    vec2 mouse = iMouse.xy / iResolution.xy;

    // Convert mouse position to rotation angles
    float rotationX = mouse.x * 2.0 * 3.14159;
    float rotationY = mouse.y * 2.0 * 3.14159;

    // Compute camera rotation matrix
    mat2 rotationMatrix = mat2(cos(rotationX), -sin(rotationX),
                               sin(rotationX), cos(rotationX));

    // Apply camera rotation to the ray direction
    rayDirection.xy = rotationMatrix * rayDirection.xy;
    return rayDirection;
}

// credit: http://erkaman.github.io/glsl-cos-palette/
vec3 cosPalette(  float t,  vec3 a,  vec3 b,  vec3 c, vec3 d ){
    return a + b*cos( 6.28318*(c*t+d) );
}


// Function to retrieve audio data from texture
vec4 getAudioSample(float position) {
  vec2 uv = vec2(position / 512., 0.5);
  return texture(iChannel0, uv);
}

float getFrequencyBand(float frequency) {
  float bandWidth = 48.;
  float bandIndex = floor(frequency / bandWidth);
  float bandStart = bandIndex * bandWidth;
  float bandEnd = (bandIndex + 1.0) * bandWidth;
  float bandCenter = (bandStart + bandEnd) * 0.5;
  return bandCenter;
}

vec3 applyNormalMap(vec3 p, vec3 n) {
    return 0.01*n;;
}

vec3 getSphereCenter(int index) {

    if ( COUNT == 1 ) {
        return vec3(0.0, 0.0, 0.);
    }

    int colIndex = index % COLUMNS;
    int rowIndex = index / COLUMNS;
    float x = (float(colIndex) / float(COLUMNS / 2) - 1.) * iResolution.x / iResolution.y;
    float y = 1. - float(rowIndex) / float(ROWS / 2);
    return vec3(x, y, 0.0);
}


vec2 mapTheSpheres(vec3 p, float map) {
    int mapIndex = -1;
    for(int i=0; i<COUNT; i+=1) {
        vec3 location = getSphereCenter(i);
        vec4 sphere = vec4(location, SPHERE_RADIUS);
        float distanceToSurface = length(p-sphere.xyz) - sphere.w;
        if(distanceToSurface < map) {
            map = distanceToSurface;
            mapIndex = i;
        }
    }
    return vec2(map, mapIndex);
}


vec2 mapTheStage(vec3 p, float map) {

    // Plane
    vec2 zPlane = vec2(0.0, 0.0);

    // Set Distance
    float planeDist = zPlane.x - p.z;

    if(planeDist < map) {
        return vec2(planeDist, COUNT);
    } else {
        return vec2(map, -1);
    }

}



vec2 mapTheScene(vec3 p) {

    vec2 map_stage = mapTheStage(p, TRACE_DISTANCE);
    vec2 map_spheres = mapTheSpheres(p, map_stage.x);
    vec2 map = map_spheres.y == -1.0 ? map_stage : map_spheres;
    return map;
}

vec3 GetNormal(vec3 p) {

    const vec3 small_step = vec3(HIT_DISTANCE, 0.0, 0.0);
    float gradient_x = mapTheScene(p + small_step.xyy).s - mapTheScene(p - small_step.xyy).s;
    float gradient_y = mapTheScene(p + small_step.yxy).s - mapTheScene(p - small_step.yxy).s;
    float gradient_z = mapTheScene(p + small_step.yyx).s - mapTheScene(p - small_step.yyx).s;

    vec3 normal = vec3(gradient_x, gradient_y, gradient_z);

    return normalize(normal);
}

vec3 GetLight(vec3 p, vec3 n, float power) {

    vec3 lightPos = vec3(0., 0., -1.);
    vec3 lightColor = vec3(1.0);
    // lightPos.xy += vec2(cos(iTime * 0.2), sin(iTime * 5.8));
    // lightPos.xz += vec2(sin(1.*iTime), cos(1.*iTime));
    vec3 l = normalize(lightPos-p);
    // Diffuse Light
    float diffuse_intensity = max(0.0, dot(n, l));
    vec3 lightIntensity = diffuse_intensity*lightColor;
    return power*lightIntensity;
}



vec3 getShader(vec3 p, vec2 uvCoord, vec2 reflectAt) { 

    // Init Color
    vec3 material = vec3(0.0);


    // Set Material
    if(reflectAt.y < float(COUNT)) {
        material = vec3(0.0, 0.0, 1.0);
        //material = vec3(0.0, 0.0, 1.0);
        vec3 closestCenter = getSphereCenter(int(reflectAt.y));
        float d = distance(p.xy, closestCenter.xy);

        material = vec3(
            cosPalette(
                1.0 - (d / SPHERE_RADIUS)*0.7,
                vec3(0.31,0.83,0.95),
                vec3(0.97,1.00,0.00),
                vec3(0.83,0.64,0.00),
                vec3(0.92,1.00,0.39)
             )
        );
        d = sin(2.*PI*40.0*d - 5.*iTime);
        material = material * ( d > 0.5 ? 1.0 : 0.0);    
    } else {

        vec2 map = mapTheSpheres(p, TRACE_DISTANCE);
        vec3 closestCenter = getSphereCenter(int(map.y));
        float d = distance(p.xy, closestCenter.xy) - SPHERE_RADIUS;

        if(d < 0.02) {
          material = vec3(sin(2.0*PI*d + iTime) + 0.8
          );
        } else if (d < 0.03) {
           material = vec3(1.0, 1.0, 0.0);
        } else {

            material = vec3(1.0, 0.0, 0.0);

            // Animation progress between 0 and 1
            // float progress = mod(iTime * 1.0, 1.0); 
            // Convert progress to sample position
            //float samplePosition = progress * 512.; 
            //  float waveform = getAudioSample(samplePosition).r;
            // vec3 audioPalette = cosPalette(
            //    waveform * 5.,
            //    vec3(0.8,0.5,0.4),
            //    vec3(0.2,0.4,0.2),
            //    vec3(1.0,1.0,1.0),
            //    vec3(0.0,0.25,0.25)
            // );
            // material += (audioPalette * 5.0 * d); 

            material = material * (1.0 - 3.5*d);
            d = (1.0 - abs(sin(2.*PI*20.0*d - iTime))) <0.3 ? 0. : 1.;
            material = material*d;
        }
    }
    // Filter
    return material;
}



vec3 RayMarch(vec3 rayOrigin, vec2 uvCoord) {
    // Define State Variables
    vec3 intersectionAtImagePlane = vec3(uvCoord, focalDistance);
    vec3 rayDirection = normalize(intersectionAtImagePlane);
    // Rotate Ray Direction
    if (iMouse.z > 0.0) {
        rayDirection = rotateRayDirection(rayDirection);
    }
    // Run Accumulator 
    float distanceToScene = 0.0;
    vec3 p = vec3(0.0);
    // Set Environment Lighting
    vec3 light = vec3(0.0);
    vec3 color = vec3(0.0);
    // Get March on the Scene and Set Color
    for(int i=0; i<MAX_STEPS;i+=1) {
        p = rayOrigin + distanceToScene * rayDirection;
        vec2 reflectAt = mapTheScene(p);
        vec3 n = GetNormal(p);
        distanceToScene += reflectAt.x;
        if(reflectAt.x<HIT_DISTANCE) {
            // Get Light
            color = GetLight(p, n, 2.50);
            // Get Material
            color = color*getShader(p, uvCoord, reflectAt);
            // Get Sound Effect
            // vec3 sound = getSoundEffect(uvCoord);
            // color = color+(0.1*sound);
            break;
        }
        if(distanceToScene > TRACE_DISTANCE) {
            break;
        }
    }
    // Return the color at raymarch
    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uvCoord = fragCoord.xy / iResolution.xy;
    // Translate origin to center and scale coordinates to -1 to 1
    uvCoord = (uvCoord - 0.5) * 2.0;

    // Scale coordiantes to aspect ratio
    uvCoord.x *= iResolution.x / iResolution.y;

    // Save Original Value
    vec2 uv0 = uvCoord;

    // Locate the camera
    vec3 p = vec3(0, 0, -2);
    // Run the RayMarcher renderer
    vec3 color = RayMarch(p, uvCoord);

    // Output to screen
    fragColor = vec4(color,1.0);
}

