//gets a color at a position on a gradient
vec3 grad(vec4[7] grad, float x){
    x = fract(x);
    for (int i = 0; i < grad.length(); i++){
        if (grad[i].a > x){
            vec4 gradStart = grad[i - 1];
            vec4 gradEnd = grad[i];
            return mix(gradStart.rgb,gradEnd.rgb,smoothstep(gradStart.a,gradEnd.a,x));
        }
    }
}

//samples the cloud renderer using a median filter (sort of) for the nice smoothing
vec4 medianSampler(in vec2 uv, in vec2 ires, in int radius){
    vec4 avg = vec4(0);
    float elements = pow(float(radius*2 + 1),2.0);
    for (int x = -radius; x <= radius; x++){
        for (int y = -radius; y <= radius; y++){
            avg += texture(iChannel1, (uv + vec2(x,y)/ires)*(min(res,iResolution.xy)/ires));
        }
    }
    avg /= elements;

    vec4 data = vec4(0.0);
    float dist = 999.0;
    for (int x = -radius; x <= radius; x++){
        for (int y = -radius; y <= radius; y++){
            vec4 pxcol = texture(iChannel1, (uv + vec2(x,y)/ires)*(min(res,iResolution.xy)/ires));
            float d = abs((pxcol.x - avg.x)*(pxcol.y - avg.y));
            if (d < dist){
                data = pxcol;
                dist = d;
            }
        }
    }
    data.z = avg.z;
    return data;
}

//renders a lens flare
vec3 renderLensFlare(vec3 rd, vec3 light, vec3 position, vec3 rotation, vec2 uv){
    vec3 lr = rotate3dbackwards(-light,rotation,vec3(0));
    if (lr.z > 0.0){
        vec2 luv = lr.xy*cameraFrustum/lr.z;
        float ld = distance(luv,uv);
        vec2 ln = (luv-uv)/ld;

        //ghosts
        vec3 ghosts = vec3(0);
        ghosts += renderhex(uv, -luv*0.25, 0.35, vec3(0.25,0.75,0));
        ghosts += renderhex(uv, luv*0.25, 0.125, vec3(1,0.5,0.5));
        ghosts += renderhex(uv, luv*0.1, 0.4, vec3(1,1,1));
        ghosts += renderhex(uv, luv*1.8, 0.5, vec3(0,0.5,0.75));
        ghosts += renderhex(uv, luv*1.25, 0.2, vec3(1,1,0.5));
        ghosts += renderhex(uv, -luv*1.25, 1.25, vec3(0.5,0.5,0.25));
        ghosts += fpow(1.0 - abs(distance(luv*0.8,uv) - 0.35),0.985)*vec3(0.15,0.1,0);

        float flare = fpow(textureLod(iChannel2,ln*0.25,0.0).r,0.3)*0.01/ld;
        float bloom = fpow(max(1.01 - ld,0.0),0.9);

        return (ghosts/(1.0 + distance(uv,luv)) + vec3(1.0,0.8,0.1)*min(bloom + flare,1.0));
    }
    return vec3(0);
}

vec3 getSkyColor(vec3 dir){
    vec3 skycol = vec3(0);
    vec3 water = vec3(0.3,0.4,0.8);
    vec3 sky = vec3(0,0.6,1);
    vec3 haze = vec3(0.9,0.9,0.9);
    if (dir.y < 0.0) {
        skycol = mix(haze, water, clamp(-dir.y,0.0,1.0));
    } else {
        skycol = mix(haze, sky, clamp(dir.y,0.0,1.0));
    }
    return skycol;
}

