    float density = renderdata.w;
    float hit = renderdata.z;
    vec3 point = depth*ray.direction + position;
    //from https://advances.realtimerendering.com/s2015/The%20Real-time%20Volumetric%20Cloudscapes%20of%20Horizon%20-%20Zero%20Dawn%20-%20ARTR.pdf
    float densshade = (1.0 - pow(2.0,-2.0*density))*0.3 - 0.2;

    //cloud colors
    float lining = fpow(max(dot(ray.direction,-lightDirection),0.0),0.8);
    col = mix(grad(cloudLit,t), grad(cloudShadowed,t), clamp(shadow - lining - densshade,-0.5,1.0));
    //fade out at distance
    col = mix(col,getSkyColor(ray.direction),clamp(depth/far,0.0,1.0));

    //sun effects
    vec3 flare = renderLensFlare(ray.direction, lightDirection, position, rotation, cam.uv);
    col += max(flare/(1.0 + shadow*10.0),0.0);

    fragColor = col.rgbb;
    //fragColor = vec4(hit);
}

