//resolution to render clouds at (must be less than or equal to screen res)
#define res vec2(800,450)
//sets smoothing (higher res screen = use more smoothing)
#define medianRadius 2 

#define PI 3.14159265
#define maxsteps 512
#define maxlightsteps 16
#define far 500.0
#define cameraFrustum 0.7

const float sensitivity = 4.0;
const float moveSpeed = 0.1;
const float moveDamping = 0.7;

struct Ray {
    vec3 origin;
    vec3 direction;
};

struct Camera {
    vec3 position;
    Ray ray;
    vec2 uv;
    mat3 transmat;
};

struct Hit {
    bool hit;
    float dist;
    vec3 point;
    float shadow;
    float density;
};

//fakes x^n for specular effects
float fpow(float x, float k){
    return x > k ? pow((x-k)/(1.0-k),2.0) : 0.0;
}

mat3 rotationMatrix(vec3 a){
    //calculate consts
    float cx = cos(a.x);
    float sx = sin(a.x);
    float cy = cos(a.y);
    float sy = sin(a.y);
    float cz = cos(a.z);
    float sz = sin(a.z);
    //return matrix
    return mat3(
        cy*cz, sx*sy*cz - cx*sz, cx*sy*cz + sx*sz,
        cy*sz, sx*sy*sz + cx*cz, cx*sy*sz - sx*cz,
        -sy, sx*cy, cx*cy
    );
}

//returns a point rotated in 3d (radians)
vec3 rotate3d(vec3 point, vec3 angle, vec3 origin){ 
    point -= origin; //subtract pivot
    point *= rotationMatrix(angle); //rotate point
    point += origin; //add pivot
    return point;
}

//returns a point rotated in 3d (radians)
vec3 rotate3dbackwards(vec3 point, vec3 angle, vec3 origin){ 
    point -= origin; //subtract pivot
    point *= inverse(rotationMatrix(angle)); //rotate point
    point += origin; //add pivot
    return point;
}

//https://iquilezles.org/articles/noacos/
mat3 rotationAlign(vec3 d, vec3 z){
    vec3  v = cross( z, d );
    float c = dot( z, d );
    float k = 1.0f/(1.0f+c);

    return mat3( v.x*v.x*k + c,     v.y*v.x*k - v.z,    v.z*v.x*k + v.y,
                 v.x*v.y*k + v.z,   v.y*v.y*k + c,      v.z*v.y*k - v.x,
                 v.x*v.z*k - v.y,   v.y*v.z*k + v.x,    v.z*v.z*k + c    );
}

//camera constructor
Camera createCamera(vec2 fragCoord, vec2 resolution, vec3 cameraPos, vec3 camRot, float frustumLength){
    vec3 uv = vec3((fragCoord - resolution*0.5)/resolution.y, frustumLength);
    vec3 uvd = normalize(uv);
    mat3 rotmat = rotationMatrix(camRot);
    vec3 ro = uv*rotmat + cameraPos;
    vec3 rd = uvd*rotmat;
    mat3 rottomat = rotationAlign(uvd,vec3(0,0,1));
    mat3 TransitionMatrix = mat3(
        vec3(1,0,0)*rottomat*rotmat, 
        vec3(0,1,0)*rottomat*rotmat, 
        vec3(0,0,1)*rottomat*rotmat
    );
    return Camera(cameraPos, Ray(ro, rd), uv.xy, TransitionMatrix);
}

float smin( float a, float b, float k){
     float h = max(k-abs(a-b), 0.0)/k;
     return min(a, b) - h*h*k*0.25;
}

float sdHex(vec2 p){
    p = abs(p);
	vec2 q = vec2(p.x*2.0*0.5773503, p.y + p.x*0.5773503);
	return dot(step(q.xy,q.yx), 1.0-q.yx);
}

vec3 renderhex(vec2 uv, vec2 p, float s, vec3 col){
    uv -= p;
    if (abs(uv.x) < 0.2*s && abs(uv.y) < 0.2*s){
        return mix(vec3(0),mix(vec3(0),col,0.1 + fpow(length(uv/s),0.1)*10.0),smoothstep(0.0,0.3,sdHex(uv*5.0/s)));
    }
    return vec3(0);
}

