const int _w = 87;
const int _a = 65;
const int _s = 83;
const int _d = 68;
const int _space = 32;
const int _shift = 16;
const int _c = 67;
const int _v = 86;

float keyPressed(int key){
    return float(texelFetch(iChannel1, ivec2(key,0),0).x >= 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 col = vec4(0);
    //rotation
    if (floor(fragCoord) == vec2(1,0)) {
        vec2 prevpos = texelFetch(iChannel0, ivec2(1,0), 0).zw; //previous dragged mouse pos
        vec4 mp = texelFetch(iChannel0, ivec2(2,0), 0); //prev mouse pos
        vec4 m = vec4(iMouse.xy/iResolution.xy,iMouse.zw/iResolution.xy); //current mouse pos

        if (iFrame <= 1){
            col = vec2(0.35,0.09).xyxy;
        } else {
            //if mouse button up
            if (m.z < 0.0 && mp.z >= 0.0){
                col = texelFetch(iChannel0, ivec2(1,0), 0).xyxy;
            } else if (m.z >= 0.0){
                col = vec4(m.xy - abs(m.zw) + prevpos, prevpos);
            } else {
                col = texelFetch(iChannel0, ivec2(1,0), 0);
            }
        }

        if (col.y*sensitivity > PI/2.0){
            col.y = PI/2.0/sensitivity;
        }
        if (col.y*sensitivity < -PI/2.0){
            col.y = -PI/2.0/sensitivity;
        }
    }
    //the last mouse position
    if (floor(fragCoord) == vec2(2,0)) {
        col = vec4(iMouse.xy/iResolution.xy,iMouse.zw/iResolution.xy);
    }

    //velocity
    if (floor(fragCoord) == vec2(3,0)){
        vec3 velocity = texelFetch(iChannel0, ivec2(3,0), 0).xyz;
        vec3 position = texelFetch(iChannel0, ivec2(4,0), 0).xyz;
        vec2 forward = rotate3d(vec3(1,0,0),vec3(0,texelFetch(iChannel0, ivec2(1,0),0).x*sensitivity,0), vec3(0)).xz;
        vec2 right = vec2(-forward.y,forward.x);
        vec3 relative = vec3(keyPressed(_d)-keyPressed(_a), 0, keyPressed(_w)-keyPressed(_s));
        float up = keyPressed(_space) - keyPressed(_shift);
        vec3 move = vec3(relative.x*forward.x + relative.z*right.x, up, relative.x*forward.y + relative.z*right.y);
        velocity = move != vec3(0) ? velocity*moveDamping + normalize(move)*moveSpeed : velocity*moveDamping;

        if (position.y < -1000.0){
            velocity = vec3(0);
        }
        col.xyz = velocity;
    }

    //position
    if (floor(fragCoord) == vec2(4,0)){
        vec3 velocity = texelFetch(iChannel0, ivec2(3,0), 0).xyz;
        vec3 position = texelFetch(iChannel0, ivec2(4,0), 0).xyz;
        position += velocity*iTimeDelta*60.0;

        if (position.y < -500.0){
            position = vec3(0,24,0);
        }

        if (iFrame == 0){
            position = vec3(0,-40,0);
        }
        col.xyz = position;
    }

    //resolution
    if (floor(fragCoord) == vec2(5,0)){
        col.xy = iResolution.xy;
    }
    //light direction
    if (floor(fragCoord) == vec2(6,0)){
        float t = texelFetch(iChannel0,ivec2(6,0),0).w;
        if (iFrame == 0){
            t = 0.5;
        }
        col.xyz = vec3(sin(t*PI*2.0),cos(t*PI*2.0),0);
        col.w = t + (keyPressed(_c) - keyPressed(_v))*0.01;
    }

    fragColor = col;
}

