//3d noise from texture
float noise(vec3 p){
  p -= 0.5;
  vec3 i = p - floor(p); //interpolant
  vec2 o = vec2(0.0, 1.0);

  float r0 = texelFetch(iChannel1, ivec3(mod(p + o.xxx,32.0)),0).r;
  float r1 = texelFetch(iChannel1, ivec3(mod(p + o.yxx,32.0)),0).r;
  float r2 = texelFetch(iChannel1, ivec3(mod(p + o.xyx,32.0)),0).r;
  float r3 = texelFetch(iChannel1, ivec3(mod(p + o.yyx,32.0)),0).r;
  float r4 = texelFetch(iChannel1, ivec3(mod(p + o.xxy,32.0)),0).r;
  float r5 = texelFetch(iChannel1, ivec3(mod(p + o.yxy,32.0)),0).r;
  float r6 = texelFetch(iChannel1, ivec3(mod(p + o.xyy,32.0)),0).r;
  float r7 = texelFetch(iChannel1, ivec3(mod(p + o.yyy,32.0)),0).r;

  vec3 k = smoothstep(0.0,1.0,i);
  float k1 = mix(r0,r1,k.x);
  float k2 = mix(r2,r3,k.x);
  float k3 = mix(r4,r5,k.x);
  float k4 = mix(r6,r7,k.x);

  float c1 = mix(k1,k2,k.y);
  float c2 = mix(k3,k4,k.y);

  return mix(c1,c2,k.z);
}

