//sigmoid wave similar to cos
float fcos(float x){
  x = abs(2.0*fract(x) - 1.0)/(2.0*PI);
  return 2.0*x*x*(3.0 - 2.0*x) - 1.0;
}

//cloud sdf
float sceneSdf(vec3 p){
    p.x += iTime;
    float v0 = noise(p/64.0 + vec3(fcos(iTime*0.02 - PI/2.0),0,fcos(iTime*0.02)));
    if (v0 > 0.7) return 5.0;
    float v1 = noise(p/16.0 + v0);
    float v2 = noise(p/4.0 + v1);
    float v3 = noise(p);
    float highcloud = p.y*0.6 - 40.0;
    float yshape = min(max(-0.2*p.y, 0.07*p.y), highcloud*highcloud + 0.2);
    float n = v0*4.0 + v1*2.0 + v2*v1 + v3*v2*0.25 - 2.5 + yshape;
    return n/2.0;
}

//cloud raymarcher
Hit marchRay(Ray ray, vec3 lightDir){
    vec3 pos = ray.origin;
    vec3 dir = ray.direction;
    float t = 0.0;
    bool hit = false;
    float multiplier = 1.5;

    //depth cast
    for(int i = 0; i < maxsteps; i++){ 
        if (t >= far || (pos.y > 70.0 && dir.y > 0.0) || (pos.y < -15.0 && dir.y < 0.0)) break;
        if (i == maxsteps-1) pos = dir*10.0;
        float sdf = sceneSdf(pos);
        if (sdf <= 0.01){
            hit = true;
            break;
        }
        t += abs(sdf)*(multiplier + t*0.001);
        pos += dir*abs(sdf)*(multiplier + t*0.001);
    }

    if (hit){
        vec3 lightpos = pos;
        float shadow = 0.0;

        //shadow cast
        for(int i = 0; i < maxlightsteps; i++){
            float sdf = sceneSdf(lightpos);
            if (lightpos.y > 70.0 || lightpos.y < -15.0) break;
            if (sdf < 0.0){
                shadow += -sdf;
            }
            lightpos += -lightDir*0.5;
        }

        vec3 pos2 = pos;
        float density = 0.0;

        //density cast
        for(int i = 0; i < maxlightsteps; i++){
            float sdf = sceneSdf(pos2);
            if (pos2.y > 70.0 || pos2.y < -15.0) break;
            if (sdf < 0.0){
                density += -sdf;
            }
            pos2 += dir*0.5;
        }

        //hit
        return Hit(hit, t, pos, sqrt(shadow), density);
    }
    //no hit
    return Hit(hit, far, pos, 0.5, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    //limit resolution
    if (fragCoord.x > min(res.x,iResolution.x) || fragCoord.y > min(res.y,iResolution.y)) return;
    //cam setup
    vec3 position = texelFetch(iChannel0, ivec2(4,0),0).xyz;
    vec3 rotation = vec3(texelFetch(iChannel0, ivec2(1,0),0).yx*vec2(-1,1),0)*sensitivity;
    Camera cam = createCamera(fragCoord, min(res,iResolution.xy), position, rotation, cameraFrustum);
    Ray ray = cam.ray;
    float t = fract(texelFetch(iChannel0, ivec2(6,0),0).w);
    vec3 lightDirection = texelFetch(iChannel0, ivec2(6,0),0).xyz*(t > 0.25 && t < 0.75 ? 1.0 : -1.0);

    //raymarch
    Hit hit = marchRay(ray, lightDirection);

    fragColor = vec4(hit.shadow, hit.dist, hit.hit, hit.density);
}

