vec4 bounds = vec4(30,100,160,18);

//////////////////

struct Implicit {
	float Distance;
	vec3 Gradient;
	vec4 Color;
};

Implicit CreateImplicit() { return Implicit(0.0, vec3(0.0), vec4(0.0)); }
Implicit CreateImplicit(float iValue) { return Implicit(iValue, vec3(0.0), vec4(0.0)); }
Implicit CreateImplicit(float iValue, vec4 iColor) { return Implicit(iValue, vec3(0.0), iColor); }

Implicit Negate(Implicit iImplicit) {
	return Implicit(-iImplicit.Distance, -iImplicit.Gradient, iImplicit.Color);
}

Implicit Add(Implicit a, Implicit b) {
	return Implicit(a.Distance + b.Distance, a.Gradient + b.Gradient, (a.Color + b.Color) * 0.5);
}

Implicit Subtract(Implicit a, Implicit b)  {
	return Implicit(a.Distance - b.Distance, a.Gradient - b.Gradient, (a.Color + b.Color) * 0.5);
}

Implicit Add(float iT, Implicit iImplicit) {
	return Implicit(iT + iImplicit.Distance, iImplicit.Gradient, iImplicit.Color);
}
Implicit Add(Implicit iImplicit, float iT) { return Add(iT, iImplicit); }
Implicit Subtract(float iT, Implicit iImplicit) { return Add(iT, Negate(iImplicit)); }
Implicit Subtract(Implicit iImplicit, float iT) { return Add(-iT, iImplicit); }

Implicit Multiply(Implicit a, Implicit b) {
	return Implicit(a.Distance * b.Distance, a.Distance * b.Gradient + b.Distance * a.Gradient, (a.Color + b.Color) * 0.5);
}
Implicit Multiply(float iT, Implicit iImplicit) { return Implicit(iT * iImplicit.Distance, iT * iImplicit.Gradient, iImplicit.Color); }
Implicit Multiply(Implicit iImplicit, float iT) { return Multiply(iT, iImplicit); }

Implicit Square(Implicit iA) { return Multiply(iA, iA); }

Implicit Divide(Implicit a, Implicit b) {
	return Implicit(a.Distance / b.Distance, (b.Distance * a.Gradient - a.Distance * b.Gradient) / (b.Distance * b.Distance), (a.Color + b.Color) * 0.5);
}
Implicit Divide(Implicit a, float b) { return Implicit(a.Distance / b, a.Gradient / b, a.Color); }

Implicit Min(Implicit a, Implicit b) 
{
	if (a.Distance <= b.Distance)
		return a;

	return b;
}

Implicit Max(Implicit a, Implicit b) {
	if (a.Distance >= b.Distance)
		return a;

	return b;
}

float mix11(float a, float b, float t) {
    return mix(a, b, t * 0.5 + 0.5);
}

Implicit Exp(Implicit iImplicit)
{
	float exp = exp(iImplicit.Distance);
	return Implicit(exp, exp * iImplicit.Gradient, iImplicit.Color);
}

Implicit Log(Implicit iImplicit)
{
	return Implicit(log(iImplicit.Distance), iImplicit.Gradient / iImplicit.Distance, iImplicit.Color);
}

Implicit Sqrt(Implicit iImplicit)
{
	float sqrt = sqrt(iImplicit.Distance);
	return Implicit(sqrt, iImplicit.Gradient / (2.0 * sqrt), iImplicit.Color);
}

Implicit Abs(Implicit iImplicit)
{
	return Implicit(abs(iImplicit.Distance), sign(iImplicit.Distance) * iImplicit.Gradient, iImplicit.Color);
}

Implicit Mod(Implicit iImplicit, float iM)
{
	return Implicit(mod(iImplicit.Distance, iM), iImplicit.Gradient, iImplicit.Color);
}

Implicit Shell(Implicit iImplicit, float thickness, float bias) 
{
	thickness *= 0.5;
	return Subtract(Abs(Add(iImplicit, bias * thickness)), thickness);
}

Implicit EuclideanNorm(Implicit a, Implicit b) {
    return Sqrt(Add(Multiply(a, a), Multiply(b, b)));
}
Implicit EuclideanNorm(Implicit a, Implicit b, Implicit c) {
    return Sqrt(Add(Add(Multiply(a, a), Multiply(b, b)), Multiply(c, c)));
}

// Booleans
// https://mercury.sexy/hg_sdf/
Implicit IntersectionEuclidean(Implicit a, Implicit b, float radius) {
    Implicit maxab = Max(a, b);
    Implicit r = CreateImplicit(radius, maxab.Color);

    Implicit ua = Implicit(Max(Add(a, r), CreateImplicit()).Distance, a.Gradient, a.Color);
    Implicit ub = Implicit(Max(Add(b, r), CreateImplicit()).Distance, b.Gradient, b.Color);

	Implicit op = Add(Min(Negate(r), maxab), EuclideanNorm(ua, ub));

    if (maxab.Distance <= 0.0)
        op.Gradient = maxab.Gradient;

    if (min(a.Distance, b.Distance) > 0.)
        op.Color = mix(a.Color, b.Color, 0.5 + 0.5 * (b.Distance - a.Distance)/(a.Distance + b.Distance));

    return op;
}

// https://mercury.sexy/hg_sdf/
Implicit UnionEuclidean(Implicit a, Implicit b, float radius) {
    Implicit ab = Min(a, b);
    Implicit r = CreateImplicit(radius, ab.Color);

    Implicit ua = Max(Subtract(r, a), CreateImplicit(0.0, a.Color));
    Implicit ub = Max(Subtract(r, b), CreateImplicit(0.0, b.Color));

	Implicit op = Subtract(Max(r, ab), EuclideanNorm(ua, ub));

    if (ab.Distance > 0.0)
        op.Gradient = ab.Gradient;

    return op;
}

// https://mercury.sexy/hg_sdf/
Implicit UnionEuclidean(Implicit a, Implicit b, Implicit c, float radius) {
    Implicit zero = CreateImplicit(0.0);
    Implicit r = CreateImplicit(radius);
    Implicit ua = Max(Subtract(r, a), zero);
    Implicit ub = Max(Subtract(r, b), zero);
    Implicit uc = Max(Subtract(r, c), zero);

    Implicit abc = Min(a, Min(b, c));

	Implicit op = Subtract(Max(r, abc), EuclideanNorm(ua, ub, uc));

    if (abc.Distance > 0.0)
        op.Gradient = abc.Gradient;

    return op;
}

Implicit UnionChamfer(Implicit iA, Implicit iB, float k)
{
    Implicit h = Multiply(Max(Subtract(CreateImplicit(k), Abs(Subtract(iA, iB))), CreateImplicit()), 1.0 / k);
    Implicit h2 = Multiply(h, 0.5);
    Implicit result = Subtract(Min(iA, iB), Multiply(h2, k * 0.5));
    float param = h2.Distance;
    result.Color = mix(iA.Color, iB.Color, iA.Distance < iB.Distance ? param : (1.0 - param));

    return result;
}

// Polynomial Smooth Min 2 from https://iquilezles.org/articles/smin/ and https://iquilezles.org/articles/distgradfunctions2d/
Implicit UnionSmoothMedial(Implicit a, Implicit b, float k) 
{
    float h = max(k-abs(a.Distance-b.Distance),0.0);
    float m = 0.25*h*h/k;
    float n = 0.50 * h/k;
    float dist = min(a.Distance,  b.Distance) - m; 

    float param = (a.Distance < b.Distance) ? n : 1.0 - n;
    vec3 grad = mix(a.Gradient, b.Gradient, param);
    vec4 color = mix(a.Color, b.Color, param);


    return Implicit(dist, grad, color);
}

Implicit UnionSmooth(Implicit a, Implicit b, float k){
    a.Distance -= k;
    b.Distance -= k;

 //   if (min(a.Distance, b.Distance) >= 0.)
 //       return (Min(a, b));

    return Add(UnionSmoothMedial(a, b, abs(a.Distance + b.Distance) * abs(1.-dot(a.Gradient, b.Gradient))), k);
}


Implicit IntersectionSmoothMedial(Implicit iA, Implicit iB, float k){
    return Negate(UnionSmoothMedial(Negate(iA), Negate(iB), k));
}


Implicit IntersectionSmooth(Implicit iA, Implicit iB, float k){
    return Negate(UnionSmooth(Negate(iA), Negate(iB), k));
}



// R0 fro, https://www.cambridge.org/core/journals/acta-numerica/article/abs/semianalytic-geometry-with-rfunctions/3F5E061C35CA6A712BE338FE4AD1DB7B
Implicit UnionRvachev(Implicit iA, Implicit iB, float k)
{
    Implicit result = Subtract(Add(iA, iB), Sqrt(Add(Square(iA), Square(iB))));
  //  float param = 0.5;
  //  result.Color = mix(iA.Color, iB.Color, iA.Distance < iB.Distance ? param : (1.0 - param));

    return result;
}

Implicit IntersectionRvachev(Implicit iA, Implicit iB, float k){
    return Negate(UnionRvachev(Negate(iA), Negate(iB), k));
}


// Primitives

Implicit Plane(vec3 p, vec3 origin, vec3 normal, vec4 color) 
{
    vec3 grad = normalize(normal);
    float v = dot(p - origin, grad);
    return Implicit(v, grad, color);
}
Implicit Plane(vec2 p, vec2 origin, vec2 normal, vec4 color) 
{
    return Plane(vec3(p, 0.0), vec3(origin, 0.0), vec3(normal, 0.0), color);
}


Implicit Circle(vec2 p, vec2 center, float iRadius, vec4 color)
{
	vec2 centered = p - center;
    float len = length(centered);
	float length = len - iRadius;
	return Implicit(length, vec3(centered / len, 0.0), color);
}

mat2 Rotate2(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat2(
        vec2(c, -s),
        vec2(s, c)
    );
}

Implicit RectangleCenterRotated(vec2 p, vec2 center, vec2 size, float angle, vec4 color)
{
	vec2 centered = p - center;
    mat2 rot = Rotate2(-angle);
    centered = rot * centered;

	vec2 b = size * 0.5;
	vec2 d = abs(centered)-b;
	float dist = length(max(d, vec2(0.0))) + min(max(d.x, d.y), 0.0);

	vec2 grad = d.x > d.y ? vec2(1.0, 0.0) : vec2 (0.0, 1.0);
	if (d.x > 0. && d.y > 0.)
		grad = d / length(d);

	grad *= -sign(centered);

	return Implicit(dist, vec3(grad * rot, 0.0), color);
}

Implicit RectangleUGFSDFCenterRotated(vec2 p, vec2 center, float size, float angle, vec4 color)
{
	vec2 centered = p - center;
    mat2 rot = Rotate2(-angle);
 //   centered = rot * centered;
    size *= 0.5;

    Implicit x = Plane(centered, vec2(0.), rot * vec2(-1., 0.), color);
    Implicit y = Plane(centered, vec2(0.), rot * vec2(0., -1.), color);
    Implicit cornerA = Subtract(Max(x, y), size);
    Implicit cornerB = Subtract(Max(Negate(x), Negate(y)), size);

	return IntersectionEuclidean(cornerA, cornerB, 0.);
}

Implicit TriangleWaveEvenPositive(Implicit param, float period, vec4 color)
{
	float halfPeriod = 0.5 * period;
    float wave = mod(param.Distance, period) - halfPeriod;
	float dist = halfPeriod - abs(wave);
	vec3 grad = -sign(wave) * param.Gradient;
	return Implicit(dist, grad, color);
}


// Viz
vec4 DrawVectorField(vec3 p, Implicit iImplicit, vec4 iColor, float iSpacing, float iLineHalfThick)
{
	vec2 spacingVec = vec2(iSpacing);
	vec2 param = mod(p.xy, spacingVec);
	vec2 center = p.xy - param + 0.5 * spacingVec;
	vec2 toCenter = p.xy - center;

	float gradParam = dot(toCenter, iImplicit.Gradient.xy) / length(iImplicit.Gradient);
	float gradLength = length(iImplicit.Gradient);

	bool isInCircle = length(p.xy - center) < iSpacing * 0.45 * max(length(iImplicit.Gradient.xy) / gradLength, 0.2);
	bool isNearLine = abs(dot(toCenter, vec2(-iImplicit.Gradient.y, iImplicit.Gradient.x))) / gradLength < iLineHalfThick + (-gradParam + iSpacing * 0.5) * 0.125;

	if (isInCircle && isNearLine)
		return vec4(iColor.rgb * 0.5, 1.);

	return iColor;
}

image_code:
/*
 * Polaris Star Trails
 * 
 * My first shader
 *
 * Inspired by:
 * https://www.rmg.co.uk/sites/default/files/styles/large/public/2023-06/BN-256390-11%20Radio%20Polaris%20by%20Jo%C3%A3o%20Yordanov%20Serralheiro.jpg?itok=nlSUd2Ms
 * https://image.winudf.com/v2/image/Y29tLkRyZWFtV2FsbHBhcGVycy5TdGFyVHJhaWwwMV9zY3JlZW5zaG90c18zXzU5NGJhZmQ3/screen-3.jpg?fakeurl=1&type=.jpg
 */

#define PI 3.1415926538
#define TWOPI 2.0 * PI
#define VIEW_SCALE 13.0
#define TRAIL_COUNT 60.0
// Radian length of the star trails, aka "exposure time"
#define TRAIL_RAD TWOPI * 0.05
#define TRAIL_LAYERS 18.0

float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

vec2 rotate(vec2 uv, float th) {
  return mat2(cos(th), sin(th), -sin(th), cos(th)) * uv;
}


float sdCircle(vec2 p, float r) {
    return length(p) - r;
}


// Screen Blend Mode
// src: https://www.shadertoy.com/view/4tSGWV
vec3 screen(vec3 target, vec3 blend){
    return 1.0 - (1.0 - target) * (1.0 - blend);
}

// src: https://iquilezles.org/articles/palettes/
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( TWOPI*(c*t+d) );
}

vec3 pSky(in float t) {    
    // [[0.138 0.668 0.408] [0.000 0.500 0.500] [0.000 0.178 0.178] [0.000 0.338 1.148]]    
    return palette(t,
        vec3(0.138, 0.668, 0.408),
        vec3(0.000, 0.500, 0.500),
        vec3(0.000, 0.178, 0.178),
        vec3(0.000, 0.338, 1.148)
    );
}

vec3 pStars(in float t) {        
    // [[0.718 0.718 1.388] [-0.922 -0.922 0.725] [-0.060 -0.060 1.028] [0.738 0.738 -0.072]]
    return palette(t,
        vec3(0.718, 0.718, 1.388),
        vec3(-0.922, -0.922, 0.725),
        vec3(-0.060, -0.060, 1.028),
        vec3(0.738, 0.738, -0.072)
    );
}

vec3 drawStarTrail(vec2 randSeed, float starTrail, float trailId, float dist, vec2 cStarUv) {
    // Not all of them
    if (random(sin(randSeed)) < 0.3) {
        return vec3(0);
    }

    // TEST - Change all star trail starts with time
    //float radStart = mod(0.0 + iTime, TWOPI);
    // Radians around the circle where the star starts    
    float radStart = random(cos(randSeed)) * TWOPI;
    // starRadStart will never be greater than TWOPI, but starRadEnd can be
    float radEnd = radStart + TRAIL_RAD;

    // Find the coordinates of the star trail start
    vec2 posStart = vec2(dist * cos(radStart), dist * sin(radStart));
    // Find the coordinates of the star trail end
    vec2 posEnd = vec2(dist * cos(radEnd), dist * sin(radEnd));

    // Find the angle of the current coord, range [0, TWOPI]
    float radCurrent = PI + atan(cStarUv.y, cStarUv.x);
    // Find the coordinates of the current star trail
    vec2 posCurrent= vec2(dist * cos(radCurrent), dist * sin(radCurrent));

    // If the current is less than the start value, then atan() looped.
    if (radCurrent < radStart) {
        // Add TWOPI to also push it out of bounds.
        // range [0, TWOPI + TRAIL_RAD]
        radCurrent += TWOPI;
    }

    // Return nothing if we are anywhere other than the star trail
    if (radStart > radCurrent || radCurrent > radEnd) {
        return vec3(0);
    }

    // Find a reasonable star trail edge fade lenth
    // TODO: Is there a better option for this calculation?
    float edgeSize = sqrt(dist/1000.0);

    // Find the start/end edge distance
    float edgeStart = distance(posStart, posCurrent);
    float edgeEnd = distance(posCurrent, posEnd);

    // Default to no fade
    float edgeDistance = 1.0;
    // Fade near the start and end to avoid flat edges to star trails
    if (edgeStart < edgeSize) {
        edgeDistance = edgeStart/edgeSize;
    }
    else if (edgeEnd < edgeSize) {
        edgeDistance = edgeEnd/edgeSize;
    }

    // Fill star trail
    float rand = random(randSeed * trailId);
    // Fade out the selected color by distance to all edges
    return edgeDistance * pow(starTrail+0.1,2.0) * pStars(rand);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Find the diviser to handle vertical and horizontal screens.
    float resolutionDiviser = min(iResolution.y,iResolution.x);
    vec2 uv = (fragCoord.xy-iResolution.xy*.5)/resolutionDiviser;

    // Sky Palette [0,1]
    vec3 col = pSky(uv.y+.5);

    uv *= VIEW_SCALE;

    // Draw Center Star
    vec2 cStarPos = -(iMouse.xy-iResolution.xy*.5)/resolutionDiviser*VIEW_SCALE;
    // Center the star at startup
    if (iMouse.x < 0.00001 && iMouse.y < 0.00001) {
        cStarPos.x = -(iResolution.x/2.0-iResolution.x*.5)/resolutionDiviser*VIEW_SCALE;
        cStarPos.y = -(iResolution.y/2.0-iResolution.y*.5)/resolutionDiviser*VIEW_SCALE;
    }
    vec2 cStarUv = uv + cStarPos;

    vec2 scaledMouse = iMouse.xy/resolutionDiviser;
    // Rotate around the center start
    cStarUv = rotate(cStarUv, iTime/(10.) + length(scaledMouse.xy)*5.);
    // Draw the center star
	float cStar = sdCircle(cStarUv,0.13);    
    col += vec3(smoothstep(0.0,0.15,-cStar));

    // Find distance to center star
    float dist = distance(-cStarPos,uv);

    // Create a unique ID for each trail
    float trailId = floor(dist * TRAIL_COUNT/PI) + 1.0;

    // Create star trails from a sine wave emanating from the center star
    float starTrail = abs(sin(dist * TRAIL_COUNT));

    // TEST - Select a color for the trail background
    //vec3 trailColor = (mod(trailId, 2.0) < 0.5) ? vec3(0,0.7,0.2) : vec3(0,1,0.8);    
    // TEST - Draw star trails
    //col += trailColor * step(0.6, starTrail);

    vec2 randSeed = vec2(sin(trailId), cos(trailId));

    // Draw a few bright layers
    for(float i = 1.0; i<3.0; i++) {
        randSeed = tan(randSeed);
        col = screen(col, drawStarTrail(randSeed, starTrail, trailId, dist, cStarUv));
    }
    // Draw many dim layers for depth
    for(float i = 1.0; i<TRAIL_LAYERS; i++) {
        randSeed = tan(randSeed);
        col = screen(col, i/TRAIL_LAYERS*0.6 * drawStarTrail(randSeed, starTrail, trailId, dist, cStarUv));
    }

    fragColor = vec4(col, 1.0);
}

vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    float scaling = 2.0;
    vec2 olduv = uv;
    uv *= scaling;
    uv.x *= iResolution.x / iResolution.y;

    // Output to screen 
    uv = exp(-abs(uv*1.1)+max(min(iTime-2.0,40.0),0.0))*max(min(iTime-5.4,0.0),-2.0)  + min(max(iTime - 5.4,0.0),25.0)*exp(abs(uv))*length(uv);
    vec3 color1 = palette(abs(1.0-(sin(uv.x)))/scaling);
    vec3 color2 = palette(sin(uv.y)/scaling);

    uv = 0.2*uv + 0.8*exp(-uv); //optional
    float weight = floor(uv.x*uv.y/pow(scaling,2.0))*pow(abs(cos(iTime+1.57)),2.0);
    vec3 color3 = palette(weight);

    vec3 color = weight*color1 + (1.0-weight)*color2 + color3/2.0;
    color += texture(iChannel0,olduv*scaling).rgb*0.5;

    //vec3 color = palette(value*exp(-length(uv)/scaling) + iTime*0.5);
    float intensity = abs(cos(length(uv)*100000.0));

    fragColor = vec4(color,1.0);
    fragColor = -exp(-tan(length(uv))*fragColor*max(min(iTime-5.3,0.0),-2.0))*min(iTime-5.3,0.0) + min(max(iTime - 5.4,0.0),1.0)*fragColor;
    fragColor = vec4(fragColor.xyz*intensity,1.0);
}

void mainImage(out vec4 O, in vec2 U)
{
    vec2 u = U.xy / iResolution.xy;

    // RGB sweep test
    vec3 col = sw(u);

    // alternate between flim and native sRGB
    if (mod(iTime, 3.) > 1.5)
    {
        col = pow(col, vec3(1. / 2.2));
    }
    else
    {
        float exposure = 0.;
        col = flim(col, exposure, true);
           // Inputs:
          // colour
         // exposure
        // convert to srgb or not. (If you did pow(O, 1./2.2), then false)

    }

    // output
    O = vec4(col, 1.);
}

common_code:
/*-----------------------------------------------

flim - Filmic Color Transform

Input Color Space:   Linear BT.709 I-D65
Output Color Space:  Linear BT.709 I-D65 / sRGB 2.2 (depends on arguments)

Description:
  Experimental port of flim for GLSL/Shadertoy
  matching flim v1.1.0.

Author:
  Bean (beans_please on Shadertoy)

Minified by:
  Ahsen (01000001 on shadertoy)

Original Repo:
  https://github.com/bean-mhm/flim

Original Shader:
  https://www.shadertoy.com/view/dd2yDz

-----------------------------------------------*/


const vec3 pf=vec3(1),pb=vec3(1),pff=vec3(1);
const float pe=4.3,ps=0.,gr=1.05,gg=1.12,gb=1.045,rr=.5,rg=2.,br=.1,rm=1.,gm=1.,bm=1.,lm=-10.,lx=22.,tx=.44,ty=.28,sx=.591,sy=.779,fe=6.,fd=5.,pfe=6.,pfd=27.5,ffs=0.,ms=1.02;

vec3 op(vec3 c,float p){return pow(c,vec3(1./p));}
float fw(float v,float s,float e){return s + mod(v-s,e-s);}
float fr(float v,float s,float e,float r,float f){return r + ((f-r)/(e-s))*(v-s);}
float f0( float v,float s,float e){return clamp((v-s)/(e-s),0.,1.);}
vec3 rh(vec3 r){
    float a,i,h,s,v,d;
    vec3 c;
    a=max(r[0],max(r[1],r[2]));
    i=min(r[0],min(r[1],r[2]));
    d=a-i;
    v=a;
    if (a!=0.){s=d/a;}
    else{s=0.; h=0.;}
    if (s==0.){h=0.;}
    else{c=(vec3(a)-r.xyz)/d;
        if (r.x==a){h=c[2]-c[1];}
        else if (r.y==a){h=2.+ c[0]-c[2];}
        else{h=4.+ c[1]-c[0];}
        h/=6.;
        if (h < 0.){h +=1.;}
    }
    return vec3(h,s,v);
}

vec3 hr(vec3 w){
    float f,p,q,t,h,s,v;
    vec3 g;
    h=w[0];
    s=w[1];
    v=w[2];
    if (s==0.){g=vec3(v,v,v);}
    else{
        if (h==1.){h=0.;}
        h*=6.;
        int i=int(floor(h));
        f=h-float(i);
        g=vec3(f,f,f);
        p=v*(1.-s);
        q=v*(1.-(s*f));
        t=v*(1.-(s*(1.-f)));
        if (i==0){g=vec3(v,t,p);}
        else if (i==1){g=vec3(q,v,p);}
        else if (i==2){g=vec3(p,v,t);}
        else if (i==3){g=vec3(p,q,v);}
        else if (i==4){g=vec3(t,p,v);}
        else{g=vec3(v,p,q);}
    }
    return g;
}

vec3 bs(vec3 c,float h,float s,float v)
{
    vec3 r=rh(c);
    r[0]=fract(r[0] + h + .5);
    r[1]=clamp(r[1]*s,0.,1.);
    r[2]=r[2]*v;
    return hr(r);
}

float fa(vec3 c){return (c.x + c.y + c.z)/3.;}
float fs(vec3 c){return c.x + c.y + c.z;}
float fr(vec3 c){return max(max(c.x,c.y),c.z);}
vec3 uo(vec3 c,float p,float w){
    float m=fa(c);
    float n=f0(m,p/1000.,1.-(w/1000.));
    return c*(n/m);
}

vec3 s(float h){
    h=fw(h*360.,0.,360.);
    vec3 c=vec3(1,0,0);
    c=mix(c,vec3(1,1,0),f0(h,0.,60.));
    c=mix(c,vec3(0,1,0),f0(h,60.,120.));
    c=mix(c,vec3(0,1,1),f0(h,120.,180.));
    c=mix(c,vec3(0,0,1),f0(h,180.,240.));
    c=mix(c,vec3(1,0,1),f0(h,240.,300.));
    c=mix(c,vec3(1,0,0),f0(h,300.,360.));
    return c;
}

vec3 sw(vec2 u){
    float h=1.-u.y;
    float e=fr(u.x,0.,1.,-5.,10.);
    return s(h)*pow(2.,e);
}

float ss(float v,float x,float y,float sx,float sy){
    v=clamp(v,0.,1.);
    x=clamp(x,0.,1.);
    y=clamp(y,0.,1.);
    sx=clamp(sx,0.,1.);
    sy=clamp(sy,0.,1.);
    float s=(sy-y)/(sx-x);
    if (v < x){
        float t=s*x/y;
        return y*pow(v/x,t);
    }
    if (v < sx){
        float i=y-(s*x);
        return s*v + i;
    }
    float sp=-s/(((sx-1.)/pow(1.-sx,2.))*(1.-sy));
    return (1.-pow(1.-(v-sx)/(1.-sx),sp))*(1.-sy)+sy;
}

float dm(float m,float d){
    float o=pow(2.,lm);
    float f=f0(log2(m + o),lm,lx);
    f=ss(f,tx,ty,sx,sy);
    f*=d;
    f=pow(2.,-f);
    return clamp(f,0.,1.);
}

vec3 rl(vec3 c,vec3 st,vec3 dt,float d){
    vec3 sn=st/fs(st);
    vec3 dn=dt/fr(dt );
    float m=dot(c,sn);
    float f=dm(m,d);
    return mix(dn,vec3(1),f);
}

vec3 rd(vec3 c,float e,float d){
    c*=pow(2.,e);
    vec3 r=rl(c,vec3(0,0,1),vec3(1,1,0),d);
    r*=rl(c,vec3(0,1,0),vec3(1,0,1),d);
    r*=rl(c,vec3(1,0,0),vec3(0,1,1),d);
    return r;
}

vec3 ge(float p,float s,float r,float m){
    vec3 o=hr(vec3(fw(p + (r/360.),0.,1.),1./s,1.));
    o/=fs(o);
    o*=m;
    return o;
}

mat3 fm(float rs,float gs,float bs,float rr,float gr,float br,float rm,float gm,float bm){
    mat3 m;
    m[0]=ge(0.,rs,rr,rm);
    m[1]=ge(1./3.,gs,gr,gm);
    m[2]=ge(2./3.,bs,br,bm);
    return m;
}

vec3 np(vec3 c,vec3 b){
    c=rd(c,fe,fd);
    c*=b;
    c=rd(c,pfe,pfd);
    return c;
}

vec3 flim(vec3 c,float e,bool t){
    c=max(c,0.);
    c*=pow(2.,pe + e);
    c=min(c,5000.);
    mat3 x=fm(gr,gg,gb,rr,rg,br,rm,gm,bm);
    mat3 i=inverse(x);
    vec3 b=pb*x;
    const float g=1e7;
    vec3 w=np(vec3(g),b);
    c=mix(c,c*pf,ps);
    c*=x;
    c=np(c,b);
    c*=i;
    c=max(c,0.);
    c/=w;
    vec3 f=np(vec3(0.),b);
    f/=w;
    c=uo(c,fa(f)*1000.,0.);
    c=mix(c,c*pff,ffs);
    c=clamp(c,0.,1.);
    float m=fa(c);
    float mix_fac =(m<.5)? f0(m,.05,.5):f0(m,.95,.5);
    c=mix(c,bs(c,.5,ms,1.),mix_fac);
    c=clamp(c,0.,1.);
    if (t) c=op(c,2.2);
    return c;
}

