//slider
void drawSlider(int id, float val, inout float ui, vec2 p, vec4 b){  

    float gap = 5.;
    vec4 data = readPixel(vec2(id,1)); 

    //if first frame write default value
    if(data.g < 0.1){ 
        data = vec4(val,.4,0,1);
        setPixel(vec2(id,1), data);
    }    

    //draw bg
    ui = mix(ui, .6, fillMask(dfBoxRounded(p,b,4.0))); 

    //if mouse over slider write new value
    if(dfBox(iMouse.xy,b)<=gap){
        float distanceFromLeftEdege = clamp(iMouse.x, b.x, b.x+b.z)-b.x;
        data.r = distanceFromLeftEdege/ b.z;        
        //data.a=1.0;
        setPixel(vec2(id,1), data);

        b.w=35.0;
    }

    //draw sliding bar   
    if(ui >=0.6){
    	b.z *= data.r;
        ui = mix(ui, .8, fillMask(dfBox(p,b)));
    }

    //draw number    
    vec4 tmp = text;
    ui = mix(ui, 0., printFloat(data.r, 3., p, b.xy + vec2(8., 5.)));
    text = tmp;
}

//boolean
void drawBoolean(int id, float val, inout float ui, vec2 p, vec4 b){  
    float gap = 11.;
    vec4 data = readPixel(vec2(id,1)); 

    //if first frame write default value
    if(data.g < 0.1){ 
        data = vec4(val, .4, 0, 1);
        setPixel(vec2(id, 1), data);
    }    

   // b.z = b.x + b.w;
    //draw bg
    ui = mix(ui, .6, fillMask(dfBoxRounded(p,b,4.0))); 

    //if mouse over slider write new value
    if(dfBox(iMouse.xy, b) <= gap && iMouse.w > 0.0){
     //   float distanceFromLeftEdege = clamp(iMouse.x, b.x, b.x + b.z) - b.x;
      //  data.r = round(distanceFromLeftEdege / b.z);        
        //data.a=1.0;
        data.r = round(1.0 - data.r);
        setPixel(vec2(id, 1), data);

        b.w = 35.0;
    }

    //draw sliding bar   
    if(ui >=0.6){
    	b.z *= data.r;
        ui = mix(ui, .8, fillMask(dfBox(p,b)));
    }

    //draw number    
    vec4 tmp = text;

    moveCursor(b.xy + vec2(8., 5.));
    if (data.r > 0.5) {
        _T R U E
    }
    else {
        _F A L S E
    }
    ui = mix(ui, 0., text.w);
    text = tmp;
}

