const float EPS = .001;
const float FAR = 40.;
const int ITRS = 16;
const float FOCAL_LEN = .9;
struct Ray
{
    vec3 origin;
    vec3 dir;
};
Ray constructViewRay(vec2 screen_pos, vec3 pos, vec3 look_dir, vec3 up, float len)
{
    Ray r = Ray(pos, vec3(0));

    vec3 side = cross(up, look_dir);
    vec3 cam_up = cross(look_dir,side);

    r.dir = normalize(side*screen_pos.x+cam_up*screen_pos.y+look_dir*len);

    return r;
}
float smin( float a, float b, float k )
{//https://iquilezles.org/articles/smin/
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}
float sdBox(vec3 p, vec3 b)
{
  //https://iquilezles.org/articles/distfunctions/
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
const vec3 degr2 = vec3(0.7071,-0.7071,0);

float map(vec3 p, out float depth)
{
    float im = iMouse.x/iResolution.x*6.28;
    float dist = FAR;
    float sc = 1.;
    int id = 0;

    for(int i = 0; i < ITRS; i++)
    {
        float di = float(i)*.2+sin(iTime-3.)*.1+.05;
        vec3 plane = normalize(vec3(sin(di), cos(di), cos(di)));

        p -= plane*min(dot(plane, p),0.)*2.;

        p -= degr2.yyz*min(dot(degr2.yyz, p),0.)*2.;
        p -= degr2.xzx*min(dot(degr2.xzx, p),0.)*2.;

        p -= vec3(0,0,1)*min(dot(vec3(0,0,1), p),0.)*2.;
        float it = sin(iTime*.1)*.15+.075;
        p = mat3(vec3(cos(it),0,sin(it)),
        vec3(0,1,0),
        vec3(-sin(it),0,cos(it)))*p;

        float box = sdBox(p, vec3(1,1,4))*sc;
        depth = (box < dist) ? depth : float(i);
        dist = smin(dist, box,.2);

        p.z -=2.2;
        p.x -=.3;
        p.y +=.3;

        p*=1.1;
        sc/=1.1;

    }
    return dist;
}
float march(Ray r, out float itrDepth)
{
    float depth = .01;
    itrDepth = 0.;
    while(depth < FAR)
    {
        float dist = map(r.origin+r.dir*depth, itrDepth);
        if(dist < EPS)
        {
            break;
        }
        depth+=dist;
    }
    return depth;
}

vec3 mapNormal(vec3 pos)
{
    float ignore = 0.; 
    vec3 eps = vec3(.001, 0, 0);
    return normalize(vec3(map(pos+eps,ignore)-map(pos-eps,ignore),
    map(pos+eps.yxy,ignore)-map(pos-eps.yxy,ignore),
    map(pos+eps.yyx,ignore)-map(pos-eps.yyx,ignore)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy/iResolution.xy);
    vec2 screenPos = (fragCoord.xy/iResolution.x*2.-1.)+vec2(0,iResolution.y/iResolution.x)*.75;
    vec3 eye = vec3(sin(iTime-5.), cos(iTime-5.), 0.)*25.;
    Ray viewRay = constructViewRay(screenPos, eye, normalize(-eye), vec3(0,0,1), FOCAL_LEN);
    float itrDepth = 0.;

    float dist = march(viewRay, itrDepth);
    vec3 col = texture(iChannel0, viewRay.dir.xzy).xyz;
    if(dist<FAR)
    {
        vec3 nrm = mapNormal(viewRay.origin+viewRay.dir*dist); 
        col = mix(vec3(0.855,0.463,0.106), vec3(0.008,0.043,0.094), float(itrDepth)/float(ITRS));
        col *= max(dot(nrm, normalize(vec3(.4,.4,.6))),0.)*vec3(4.)+texture(iChannel0, nrm.xzy).xyz;
    }

    fragColor = vec4(pow(col*vec3(0.867,0.765,0.675), vec3(1./2.2)),1.0);
}

vec2 distort(vec2 p){
    // vec2 center=vec2(.5);
    vec2 center=iMouse.xy/iResolution.xy;
    float radius=.9;
    float strength=1.1;

    p-=center;

    float d=length(p);
    // p*=d;

    d/=radius;

    float dPow=pow(d,2.);
    // p*=dPow;

    float dRev=strength/(1.+dPow);
    p*=dRev;

    p+=center;

    return p;
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 uv=fragCoord/iResolution.xy;
    uv=distort(uv);
    vec4 tex=texture(iChannel0,uv);
    vec4 col=tex;
    fragColor=col;
}

#define PI 3.1415926
#define TAU 6.2831852
#define T(s) fract(iTime * s) * PI * 4.

#define calcLine( a ) step( mod( a, .5 ) - .25, 0. )
#define calcLine2( a ) step( mod( a, .5 ) - .1, 0. )

#define enableMouse 1

vec2 m = vec2( 0.6, 0.55 ); 
mat2 m2 = mat2( .1, .1, -.7, .5 );

float noise( in vec2 f ){
  f *= m2;
  return ( cos( f.x + .18975 ) * sin( f.y + .494516 ) + .1615246 );
}

float fbm( in vec2 x, in float H )
{    
    float t = 0.0;
    for( int i=0; i<10; i++ )
    {
        float f = pow( 2.0, float(i) );
        float a = pow( f, -H );
        t += a*noise(f*x);
    }
    return t;
}

int eyeInt = 0;


mat2 rotation(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}

float smin (float a, float b, float r)
{
    float h = clamp(.5+.5*(b-a)/r,0.,1.);
    return mix(b, a, h) - r*h*(1.-h);
}

float smax(float a, float b, float c) {
    return -smin(-a, -b, c);
}

float getPath( vec2[3] path, in vec2 uv ){
    return path[0].x * pow( uv.x, 2. ) + path[0].y * pow( uv.y, 2. ) + path[1].x * uv.x * uv.y + path[1].y * uv.x + path[2].x * uv.y + path[2].y;
}

vec2 path1[3] = vec2[]( vec2( 0.0036000000000000337, 0.008099999999999927 ), vec2( 0.010800000000000087, -0.008496000000000004  ), vec2( 0.003480000000000096, -0.004072799999999995 ) );

vec2 path2[3] = vec2[]( vec2( 0.03609999999999998, 0.003600000000000006 ), vec2( -0.022800000000000015, 0.03411199999999999  ), vec2( -0.003252000000000005, 0.005616280000000001 ) );

vec2 path3[3] = vec2[]( vec2( 0.010000000000000007, 0.0001000000000000445 ), vec2( -0.0020000000000000157, 0.005656000000000005  ), vec2( 0.0028940000000000007, 0.0005264499999999997 ) );


float calcBody( vec2 uv ){

    float d = 0.5;

    // body
    d = min( d,
        length( uv * vec2( 1.2, 1. ) ) - .5
    );

    d = smin( d, length( uv + vec2( .27, -.25 ) ) - .2, .3 );
    d = smin( d, length( uv + vec2( -.27, -.25 ) ) - .2, .3 );

    d = smin( d, length( uv + vec2( .15, .2 ) ) - .25, .3 );
    d = smin( d, length( uv + vec2( -.15, .2 ) ) - .25, .3 );

    return d;
}

float calcEar( vec2 uv ){
    return max(
        max(
            getPath( path1, uv ),
            getPath( path2, uv )
        ),
        -getPath( path3, uv )
    );
}

float calcWing( vec2 uv ){
    float d = .1;

    uv.x -= .08;
    uv.y += .25;

    uv.x -= cos( T( .25 ) ) * .01;
    uv.y -= cos( T( .25 ) ) * .02;

    d = min(
        d,
        length( uv * rotation( .1 )  * vec2( 1., 1.5 ) + vec2( .33, -.3 ) ) - .35
    );

    d = smin(
        d,
        length( uv + vec2( .35, -.05 ) ) - .2,
        .2
    );

    return d;

}

float calcEyeSockets( vec2 uv ){
    float d = .1;

    d = min(
        d,
        length( uv * vec2( 1., 1.5 ) + vec2( 0.25, -.3 ) ) - .25
    );

    d = min(
        d,
        length( uv * vec2( 1., 1.5 ) + vec2( -0.25, -.3 ) ) - .25
    );

    d = smin(
        d,
        length( uv * vec2( 1., 1.3 ) + vec2( -0.0, -.32 ) ) - .1,
        .3
    );

    d = smin(
        d,
        length( uv + vec2( -0.25, -.27 ) ) - .1,
        .2
    );

    d = smin(
        d,
        length( uv + vec2( 0.25, -.27 ) ) - .1,
        .2
    );

    return d;
}

float calcEyeOline( vec2 uv ){
    return length( uv + vec2( .25, -.23 ) ) - .13;
}

float calcEye( vec2 uv ){
    float d = .1;
    eyeInt++;

    d = min(
        d,
        length( uv + vec2( .28, -.24 ) ) - .07
    );

    d = max(
        d,
        eyeInt == 1 ?
        -( length( uv * rotation( -.1 )  * vec2( 1., 1.2 ) + vec2( .33, -.3 ) ) - .03 ) :
        -( length( uv * rotation( -.15 )  * vec2( 1., 1.5 ) + vec2( .35, -.35 ) ) - .025 )
    );

    return d;

}

float calcBeak( vec2 uv ){

    float d = .1;

    d = min(
        d,
        length( uv + vec2( 0., -.05 ) ) - .05
    );

    d = smin(
        d,
        length( uv + vec2( 0.05, -0.03 - .05 ) ) - .03,
        .03
    );

    d = smin(
        d,
        length( uv + vec2( -0.03, -0.03 - .05 ) ) - .03,
        .04
    );

    return d;

}

float calcChesty( vec2 uv ){

    float d = .1;

    d = min(
        d,
        length( uv * vec2( .6, 1. ) + vec2( 0.02, .25 ) ) - .19
    );

    return d;

}

float getPat( vec2 uv ){
    float wave = 0.;

    uv.x -= cos( T( .025 ) ) * .1;
    uv.y += sin( T( .025 ) ) * .1;

    uv.y -= .05;
    uv.y = mod( uv.y, .1 ) - .05;

    vec2 uv2 = uv;
    vec2 uv3 = uv;

    uv2.x = mod( uv2.x, .1 ) - .05;
    wave = ( length( uv2 ) - .05 );

    uv3.x = mod( uv3.x, .1 ) - .05;
    wave = max(
        wave,
        -( length( uv3 + vec2( 0., -.01 ) ) - .05 )
    );

    return wave;
}

float calcClawu( vec2 uv ){
    float p = 0.;

    float py = uv.y;

    if( py < 0. ){
        py *= 6.;
    }

    if( py <= -1. ){
        py = -1.;
    }

    if( py > 0. ){
        py *= 3.;
    }

    if( py >= 1. ){
        py = 1.;
    }

    py = abs( pow( py, 2. ) ) - 1.;
    py = abs( py );

    p = abs( uv.x ) - .1 * py;

    return p;
}

float calcClawg( vec2 uv ){

    float d = .1;

    d = min( d,
        calcClawu( uv * 3. * rotation( PI * 1.05 ) + vec2( 0.3, 0.1 ) )
    );

    d = min( d,
        calcClawu( uv * 3. * rotation( PI * 1.05 ) + vec2( -0.25, 0. ) )
    );

    d = min( d,
        calcClawu( uv * 2.6 * rotation( PI * 1.1 ) )
    );

    return d;
}

float calcWoocry( vec2 uv ){

    float d = .1;

    float py = uv.y;

    py /= 2.;
    py += .5;

    d = min(
        d,
        max(
            abs( uv.x ) - .1 * py,
            abs( uv.y ) - .15
        )
    );

    return d;
}

vec3 texMap( vec2 uv )
{

    vec3 col = vec3( 
        mix( vec3( 1. ), vec3( 0.73, 0.82, 0.84 ), calcLine( uv.y * .6 + abs(uv.x -.0 ) * .5 - ( iTime * .05 ) ) )
    );

    uv.y += cos( T( .25 ) ) * .005;

    float d = 0.;

    // ears
    d = .1;
    d = min( d,
        calcEar( uv * vec2( -1., 1. ) * rotation( .3 ) + vec2( -.12, -.35 ) )
    );

    d = min( d,
        calcEar( uv * vec2( 1., 1. ) * rotation( .1 ) + vec2( 0., -.3 ) )
    );
    d = step( d, 0. );

    col = mix(
        col,
        vec3( 0.92, 0.40, 0.15 ),
        d
    );

    // wings
    d = .1;
    d = min( d,
        calcWing( uv )
    );

    d = min( d,
        calcWing( uv * vec2( -1., 1. ) + vec2( .01, -.05 ) )
    );

    d = step( d, 0. );

    col = mix(
        col,
        vec3( 0. ),
        d
    );

    // body
    d = .1;
    d = min( d,
        calcBody( uv )
    );

    d = step( d, 0. );

    col = mix(
        col,
        vec3( 0.91, 0.8, 0.15 ),
        d
    );

    // eye
    d = max(
        calcBody( uv ),
        -calcEyeSockets( uv )
    );

    d = max(
        d,
        -( uv.y - .2 )
    );

    d = step( d, 0. );
       col = mix(
        col,
        vec3( 0.92, 0.40, 0.15 ),
        d
    );

    d = .1;
    d = min( d,
        calcEyeSockets( uv )
    );

    d = step( d, 0. );

    col = mix(
        col,
        vec3( 1. ),
        d
    );

    d = .1;
    d = min( d,
        calcEyeOline( uv + vec2( -.5, 0. ) )
    );

    d = min( d,
        calcEyeOline( uv + vec2( 0., 0. ) )
    );

    d = smoothstep( 0., d, .01 );
    d = dot(d, d);
    d = dot(d, d);
    d = dot(d, d);

    col = mix(
        col,
        vec3( 0. ),
        d
    );


    // eye2
    d = .1;
    d = min( d,
        calcEye( uv )
    );

    d = min( d,
        calcEye( uv * vec2( -1., 1. ) )
    );


    d = step( d, 0. );

    col = mix(
        col,
        vec3( 0. ),
        d
    );

    // beak
    d = .1;
    d = min( d,
        calcBeak( uv )
    );

    d = step( d, 0. );

    col = mix(
        col,
        vec3( 0. ),
        d
    );

    // chesty
    d = .1;
    d = min( d,
        calcChesty( uv )
    );

    d = step( d, 0. );

    col = mix(
        col,
        vec3( 0.15, 0.53, 0.60 ),
        d
    );


    col = mix(
        col,
        vec3( 1. ),
        d * step( getPat( uv ), 0. )
    );


    // claw
    d = .1;

    d = min( d, calcClawg( uv + vec2( -.22, 0.62 ) ) );
    d = min( d, calcClawg( uv * vec2( -1., .95 ) + vec2( -.2, 0.6 ) ) );

    d = step( d, 0. );

    col = mix(
        col,
        vec3( 0. ),
        d
    );

    // woocry
    d = .1;

    uv += vec2( -.55, -.65 );
    uv *= 1.2;
    d = min( d, calcWoocry( uv * rotation( PI * -.25 ) + vec2( -.1, -0.1 ) ) );
    d = step( d, -1. + round( fract( iTime + 1. ) ) );

    col = mix(
        col,
        vec3( 0.92, 0.40, 0.15 ),
        d
    );

    d = .1;
    d = min( d, calcWoocry( uv * 1.2 * rotation( PI * -.3 ) + vec2( -.3, -0.15 ) ) );

    d = step( d, -1. + round( fract( iTime * .25 ) ) );

    col = mix(
        col,
        vec3( 0.92, 0.40, 0.15 ),
        d
    );

    d = .1;
    d = min( d, calcWoocry( uv * 1.2 * rotation( PI * -.35 ) + vec2( -.45, -0.15 ) ) );

    d = step( d, -1. + round( fract( iTime * .5 ) ) );

    col = mix(
        col,
        vec3( 0.92, 0.40, 0.15 ),
        d
    );

    return col;
}

float calcB( vec3 p ){
    float d = .1;

    p.xz *= rotation( clamp( m.x, -1., 1. ) );
    p.yz *= rotation( clamp( m.y, -.5, .3 ) );

    // p.z += fbm( p.yz, 1.1 ) * .5;

    d = smax(
      smax( abs( p.x ) - .8, abs( p.z ) - .15, .1 ),
      abs( p.y ) - .8,
      .3
    );

    d = smin( d,
        max(
            length( p.yz * vec2( 1., 2.1 ) + vec2( 0., fbm( p.yz, 1. ) * .05 ) ) - .5,
            abs( p.x ) - .7
        )
        ,.5
    );

    return d;
}

float ground( vec3 p ){
    float d = .1;

    p.xz *= rotation( PI * -.5 );

    d = min(
        d,
        p.y + .7
    );

    d = min(
        d,
        p.x + 1.1
    );

    return d;
}

float map(vec3 p) {
    float d = .1;

    d = min(
        d,
        calcB( p )
    );

    d = min(
        d,
        ground( p )
    );


    return d;
}

// IQ

float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax, int technique )
{
	float res = 1.0;
    float t = mint;
    float ph = 1e10; // big, such that y = 0 on the first iteration

    for( int i=0; i<32; i++ )
    {
		float h = map( ro + rd*t );

        // traditional technique
        if( technique==0 )
        {
        	res = min( res, 10.0*h/t );
        }
        // improved technique
        else
        {
            // use this if you are getting artifact on the first iteration, or unroll the
            // first iteration out of the loop
            //float y = (i==0) ? 0.0 : h*h/(2.0*ph); 

            float y = h*h/(2.0*ph);
            float d = sqrt(h*h-y*y);
            res = min( res, 10.0*d/max(0.0,t-y) );
            ph = h;
        }

        t += h;

        if( res<0.0001 || t>tmax ) break;

    }
    res = clamp( res, 0.0, 1.0 );
    return res*res*(3.0-2.0*res);
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos) {
    vec2 e = vec2(1.0, -1.0) * 0.5773;
    const float eps = 0.0005;
    return normalize(e.xyy * map(pos + e.xyy * eps) +
        e.yyx * map(pos + e.yyx * eps) +
        e.yxy * map(pos + e.yxy * eps) +
        e.xxx * map(pos + e.xxx * eps));
}

vec3 raymarching( vec3 ro, vec3 rd ){

    float t = 0.,
          dist = 0.;

    vec3 lig = normalize(vec3(.57703));
    lig.xz *= rotation( PI * 1.3 );
    lig.xy *= rotation(  PI * .5 );

    vec3 hal = normalize(lig - rd);

    vec3 pos = vec3( 0. );

    for( int i = 0; i < 64; i++ ){

        pos = ro + rd * t;

        dist = map( pos );

        if( t > 1e3 ){
            break ;
        }

        t += dist;
    }


    if( dist < 1e-3 ){

        vec3 nor = calcNormal(pos);

        float dif = clamp(dot(nor, vec3(.4, 1., -.5)), 0.0, 1.0);

        float shadow = calcSoftshadow( pos, lig, 0.1, 3.0, 1 );
        dif *= shadow;

        float spe = pow(clamp(dot(nor, hal), 0.0, 1.0), 50.0) * .6;
        spe *= dif;

        float amb = dot(nor, vec3(0.0, 1.0, 0.0));

        vec3 reflection = reflect(rd, nor);

        vec3 col = vec3( 0. );

        if( map( pos ) == ground( pos ) ){

            vec3 posg = pos;
            posg.zx *= rotation( PI * .2 );
            float pt = fbm( vec2( fbm( posg.zx - ( 100. ), 1. ) ), .1 );

            posg.xy += calcLine( pos.x );

            col += spe + vec3( .3 ) * dif + amb * .2 + mix( vec3( vec3(pt) * vec3( .1 ) ), vec3(pt) * vec3( 0.37, 0.34, 0.31 ) * .7, calcLine( pos.x ) ) * .25 ;
            col += vec3( 0.73, 0.82, 0.84 ) * .1;
        }

        if( map( pos ) == calcB( pos ) ){

            vec3 posg = pos;
            posg.xz *= rotation( clamp( m.x, -1., 1. ) );
            posg.yz *= rotation( clamp( m.y, -.5, .3 ) );
            nor.xz *= rotation( clamp( m.x, -1., 1. ) );
            nor.yz *= rotation( clamp( m.y, -.5, .3 ) );

            col = spe + vec3( .1 ) * dif + ( .3 + calcLine2( posg.y * 1.5 )) * max( nor.x, 0. ) + (  vec3( 0.73, 0.82, 0.84 ) * .5 +  calcLine2( posg.y * 1.5 )) * max( -nor.x, 0. );
            col += max( -nor.z, .0 ) * texMap( posg.xy * 1.5 );
        }

        return col;

    }

    return vec3( 0. );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    if( iMouse.z > .01 && enableMouse == 1 ){
        m = iMouse.xy/iResolution.xy;
    }

    uv = (uv - .5) * 2.;
    m = (m - .5) * 2.;

    uv.x *= iResolution.x / iResolution.y;
    m.x *= iResolution.x / iResolution.y;

    // Time varying pixel color
    vec3 col = vec3( 0. );

    vec3 ro = vec3( 0., 0., -2. );
    vec3 rd = vec3( uv, 1. );

    col = raymarching( ro, rd );

    // Output to screen
    fragColor = vec4(col,1.0);
}
