#define OCTAVES  8.0

#define LIVE_SMOKE 1

float rand(vec2 co){
   return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float rand2(vec2 co){
   return fract(cos(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

// Rough Value noise implementation
float valueNoiseSimple(vec2 vl) {
   float minStep = 1.0 ;

   vec2 grid = floor(vl);
   vec2 gridPnt1 = grid;
   vec2 gridPnt2 = vec2(grid.x, grid.y + minStep);
   vec2 gridPnt3 = vec2(grid.x + minStep, grid.y);
   vec2 gridPnt4 = vec2(gridPnt3.x, gridPnt2.y);

    float s = rand2(grid);
    float t = rand2(gridPnt3);
    float u = rand2(gridPnt2);
    float v = rand2(gridPnt4);

    float x1 = smoothstep(0., 1., fract(vl.x));
    float interpX1 = mix(s, t, x1);
    float interpX2 = mix(u, v, x1);

    float y = smoothstep(0., 1., fract(vl.y));
    float interpY = mix(interpX1, interpX2, y);

    return interpY;
}

float getLowFreqs()
{
    const int NUM_FREQS = 32;
    /* Close to the spectrum of the voice frequencies for this song. */
    const float lowStart = 0.65;
    const float lowEnd = 0.75;
    float result = 0.0;

    for (int i = 0; i < NUM_FREQS; i++)
    {
        result += texture(iChannel0,
                            vec2(lowStart + (lowEnd - lowStart)*float(i)/float(NUM_FREQS - 1),
                                 0.25)).x;
    }

    return smoothstep(0.0, 1.0, (result / float(NUM_FREQS)) * 2.);
}

float fractalNoise(vec2 vl) {
    float persistance = 2.0;
    float amplitude = 0.5;
    float rez = 0.0;
    vec2 p = vl;

    for (float i = 0.0; i < OCTAVES; i++) {
        rez += amplitude * valueNoiseSimple(p);
        amplitude /= persistance;
        p *= persistance;
    }
    return rez;
}

float complexFBM(vec2 p) {
    float sound = getLowFreqs();
    float slow = iTime / 2.5;
    float fast = iTime / .5;
    vec2 offset1 = vec2(slow  , 0.); // Main front
    vec2 offset2 = vec2(sin(fast )* 0.1, 0.); // sub fronts

    return 
#if LIVE_SMOKE
        (1. + sound) * 
#endif
        fractalNoise( p + offset1 + fractalNoise(
            	p + fractalNoise(
                	p + 2. * fractalNoise(p - offset2)
            	)
        	)
        );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 blueColor = vec3(0.07,0.2,.5);
    vec3 orangeColor2 = vec3(0, 0, 0);

    vec3 rez = mix(orangeColor2, blueColor, complexFBM(uv));

    fragColor = vec4(rez, 1.0);

}

//experimenting with 3D Gradient noise from: https://www.shadertoy.com/view/Xsl3Dl
#define layers 5 //int how many layers
#define speed .25 //float speed multiplyer
#define scale 1.2 //float scale multiplyer
vec3 hash( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));
	p = -1.0 + 2.0*fract(sin(p)*43758.5453123);

	return p;
}
float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //normalized device coordinates from -1 to 1
    vec2 uv = (fragCoord-iResolution.xy-.5)/iResolution.y;
    //time value
    float t = iTime*speed;

    uv *= scale;
    float h = noise(vec3(uv*2.,t));
    //uv distortion loop 
  for (int n = 1; n < layers; n++){
      float i = float(n);
    uv -= vec2(0.7 / i * sin(i * uv.y+i + t*5. + h * i) + 0.8, 0.4 / i * sin(uv.x+4.-i+h + t*5. + 0.3 * i) + 1.6);
  }

    uv -= vec2(1.2 * sin(uv.x + t + h) + 1.8, 0.4 * sin(uv.y + t + 0.3*h) + 1.6);


    // Time varying pixel color
    vec3 col = vec3(.5 * sin(uv.x) + 0.5, .5 * sin(uv.x + uv.y) + 0.5, .5 * sin(uv.y) + 0.8)*0.8;

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Optional: Set iChannel0 to the keyboard for some key controls.
//           (I keep keyboard off so it looks nice in the iOS ShaderToy app)
//           key controls (if enabled)
//           1 - heat map
//           2 - shadows
//           3 - reflections
//           4 - branch height variation


const uint  march_MaxSteps  = 250u;
const float march_epsilon   = 0.01;
      float march_understep = 0.25;
const float normal_epsilon  = 0.05;

// set in readKeyboard().
bool do_Shadows;
bool do_Reflections;
bool do_HeatMap;
bool do_BranchHeightVariety;


float gMapCount = 0.0;

int  gId        = 0;
int  gIdEnd     = 0;
bool gTrackId   = !false;

void updateId(in vec3 q) {
    if (gTrackId) {
        gIdEnd = gIdEnd * 4 + 4;
        gId *= 4;
        gId += int(sign(q.x) * 0.5 + 0.5);
        gId += int(sign(q.z) * 1.0 + 1.0);
    }
}

float idToUnit() {
    float f = float(gId) / float(gIdEnd - 1);
    return f;
}

float gBigDir1;
float gBigDir2;
float gT;

void setupScene() {
    gT = iTime * 0.3 - 10.0;
    gBigDir1 = 0.05 * cos(gT * 0.3);
    gBigDir2 = 0.05 * sin(gT * 0.3 + 1.0);

    gTrackId = do_BranchHeightVariety;
}

float sdScene(in vec3 p) {

    vec4  mp = repeated_mirrored(p.xz, 60.0);
    float cellDist = dot(mp.zw, mp.zw);


    if (p.y < 0.0) {
        // nothing below the floor
        return 1e9;
    }

    gMapCount += 1.0;

    float t = gT + mp.z + mp.w * 0.5;

    p.xz = mp.xy;

    float d = 1e9;

    gId    = 0;
    gIdEnd = 0;

    vec3 q = p;

    float h = 45.0 / (1.0 - .3 * dot(mp.zw, mp.zw));
    float r =  1.0;

    if (cellDist > 3.5) {
        return p.y;
    }


    float f;

    if (cellDist < 1.0) {
        q.xz *= rot2(q.y * gBigDir1);
        updateId(q);
        f = smoothstep(h * 0.1, h * 1.1, q.y);
        q.xz = abs(q.xz) - h * 0.25 * f;
        r *= 1.0 - 0.1 * f;
    }
    else {
        t *= 0.11;
    }

    q.xz *= rot2(q.y * gBigDir2 + t * 1.5);
    updateId(q);
    f = smoothstep(h * 0.3, h * 0.7, q.y);
    q.xz = abs(q.xz) - h * 0.08 * f;
    r *= 1.0 - 0.1 * f;


    q.xz *= rot2(q.y * 0.08 + t * 1.2);
    updateId(q);
    f = smoothstep(h * 0.4, h, q.y);
    q.xz = abs(q.xz) - h * 0.04 * f;
    r *= 1.0 - 0.75 * f;

    if (do_BranchHeightVariety) {
        f = float(gId % 13) / 12.0;
    }
    else {
        f = 0.9;
    }


    h += -3.0 + 6.0 * f;

    r *= 1.0 + 1.5 * smoothstep(-0.5, 0.0, q.y - h * 0.98 + 3.0 * (f - 1.0));

    float dCap = sdDisk(q.xz, r);
    float dBox = sdBox(q, vec3(r - 0.2, h, r - 0.2)) - 0.1;

    float dTree = mix(dCap, dBox, smoothstep(0.0, h, q.y));

    d = min(d, dTree);

    /*
    q = p;
    float s = 25.0;
    q.xz = mod(q.xz, s) - s/2.0;
    float sr = 30.0 / (1.0 + length(p.xz));
    q.y -= sr;
    d = min(d, sdSphere(q, sr));*/

    if (dot(p.xz, p.xz) < 50.0) {
        d = smin(d, p.y, 4e0);
    }


    return d;
}

vec3 gradientScene(in vec3 p) {
    if (p.y < march_epsilon) {
        // cheat for floor
        return vY;
    }
    float d = sdScene(p);
    return vec3(
        sdScene(p + vX * normal_epsilon) - d,
        sdScene(p + vY * normal_epsilon) - d,
        sdScene(p + vZ * normal_epsilon) - d
    );
}

vec3 normalScene(in vec3 p) {
    return normalize(gradientScene(p));
}

float rayVsScene(in vec3 ro, in vec3 rd, out bool outOfSteps) {

    // analytic ground plane in addition to the raymarched one.
    float pt = rayVsPlane(ro, rd, vY, 0.0);

    outOfSteps = false;

    float t = 0.0;

    for (uint n = 0u; n < march_MaxSteps; ++n) {
        vec3  p = ro + t * rd;
        float d = sdScene(p);
        if (d < march_epsilon) {
            return min(pt, t);
        }
        if (dot(p.xz, p.xz) > 60000.0) {
            return pt;
        }

        t += d * march_understep;
    }

    outOfSteps = true;
    return pt;
}

const vec3 lightDir = normalize(vec3(1.0, 1.5, 1.9));

vec3 sky(in vec3 ro, in vec3 rd) {
    vec3 c = v1 * (1.0 - (0.2 + 0.8 * saturate(rd.y)));
    c.r *= 0.2;
    c.g *= 0.4;

    float d = dot(rd, lightDir) * 0.5 + 0.5;

    c = mix(c, v1, sqr(sqr(sqr(d))) * 0.85);

    float dddd = d * d;
    dddd *= dddd;
    c *= mix(sin(dddd * dddd * 30.0 + gT) * 0.5 + 0.5, 1.0, 1.0 - dddd * 0.1);

    float a = atan(rd.z, rd.x);

    c = mix(c, v1 * (1.0 - sqrt(d)), 0.3 * smoothstep(0.06 + (1.0 - d) * 0.02 * cos(a * 23.0), 0.0, abs(rd.y)));
    return c;
}

vec3 calcAlbedo(in vec3 p) {
    gTrackId = true;
    sdScene(p);

    bool outerTree = dot(p.xz, p.xz) > 1200.0;

    vec3 pop = outerTree ? vec3(0.4, 0.0, 0.5) : vX;
    vec3 a;
    /*
    float f = idToUnit();
    albedo.r = sin(f * 1211.23);
    albedo.g = sin(f * 1315.43);
    albedo.b = sin(f * 1416.63);
    albedo = mix(albedo * 0.7, v1, 0.4);
    */
    float noise  = whangHashNoise(0u, 0u, uint(gId));
    float bNoise = step(0.3, noise);
    a = mix(pop, v1, bNoise);
    a = mix(a, 0.1 * mix(pop, v1, 0.5), smoothstep(40.0, 20.0, p.y));

    if (outerTree) {
        a *= 0.3;
    }

    a = mix(a, v1 * 1.0, smoothstep( 2.0, -3.0, p.y));

    return a;
}

vec3 calcDiffuse(in vec3 p, in vec3 rd, in vec3 n, in vec3 albedo) {
    vec3 c = albedo * (saturate(dot(n, lightDir)));

    float redness = albedo.r - albedo.g;
    c.r += 1.0 * redness * smoothstep(1.0, 10.0, p.y) * 0.9 * (sqr((sqr(saturate(dot(n, -rd))))));

    return c;
}

vec3 render(in vec3 ro, in vec3 rd) {
    bool outOfSteps;
    float t = rayVsScene(ro, rd, outOfSteps);
    if (t > 1e8) {
        return sky(ro, rd);
    }

    vec3 p;
    vec3 n;

    p = ro + t * rd;
    n = normalScene(p);

    vec3 a = calcAlbedo(p);
    vec3 c = calcDiffuse(p, rd, n, a);

    // shadow and one reflection bounce
    ro = p + n * march_epsilon * 1.5;



    if (do_Shadows) {
        // shadow

        // use less precision for shadows
        march_understep = mix(march_understep, 1.0, 0.5);

        float ts = rayVsScene(ro, lightDir, outOfSteps);
        p = ro + ts * lightDir;

        if (ts < 1e9 || outOfSteps) {
            c *= 0.4;
        }
    }

    if (do_Reflections) {
        // reflect

        // use even less precision for shadows
        march_understep = mix(march_understep, 1.0, 0.5);

        const float reflectMin = 0.07;
        const float reflectMax = 0.9;
        float fres = mix(reflectMin, reflectMax, smoothstep(0.9, 0.1, abs(dot(rd, n))));

        vec3 c2;
        rd = reflect(rd, n);

        if (t > 170.0) {
            c2 = sky(ro, rd);
        }
        else {
            t = rayVsScene(ro, rd, outOfSteps);
            if (t > 1e8) {
                c2 = sky(ro, rd);
            }
            else {
                p = ro + t * rd;
                n = normalScene(p);
                a = calcAlbedo(p);
                c2 = calcDiffuse(p, rd, n, a);
            }
        }


        c = mix(c, c2, fres);
    }

    return c;
}

vec3 getRayDir(in vec2 xy, in vec3 ro, in vec3 lookTo, in float fov) {
    vec3 camFw = normalize(lookTo - ro);
    vec3 camRt = normalize(cross(camFw, vY));
    vec3 camUp = cross(camRt, camFw);

    vec3 rd;
    rd = camFw + fov * (camRt * xy.x + camUp * xy.y);
    rd = normalize(rd);

    return rd;
}

bool keyboardNumberToggle(int number) {
    const int k0_regular = 48;
    const int k0_numPad  = 96;

    bool regular = bool(texelFetch(iChannel0, ivec2(k0_regular + number, 2), 0).x);
    bool numPad  = bool(texelFetch(iChannel0, ivec2(k0_numPad  + number, 2), 0).x);

    return regular ^^ numPad;
}

void readKeyboard() {
    do_HeatMap             =  keyboardNumberToggle(1);
    do_Shadows             = !keyboardNumberToggle(2);
    do_Reflections         = !keyboardNumberToggle(3);
    do_BranchHeightVariety = !keyboardNumberToggle(4);
}

void mainImage(out vec4 RGBA, in vec2 XY)
{
    readKeyboard();

    const float zoom = 0.8;

    vec2 xy = (XY * 2.0 - RES) / MINRES / zoom;

    const float heatMapSize = 0.35;
    bool isHeatMap = do_HeatMap && XY.x < RES.x * heatMapSize && XY.y < RES.y * heatMapSize;
    if (isHeatMap) {
        xy = (XY * 2.0 - RES * heatMapSize) / MINRES / heatMapSize / zoom;
    }

    setupScene();

    vec2 M = iMouse.xy;
    if (length(M) < 100.0) {
        M = RES/2.0;
        M.x += gT * 21.0;
        M.y *= 1.5 + 0.3 * sin(iTime * 0.05);
    }

    vec3 ro = vec3(0.0, 40.0 - 50.0 * (M.y / RES.y - 0.5), -80.0);
    ro.xz *= rot2(3.141 + (M.x / RES.x - 0.5) * -7.0);
    vec3 lt = vY * 26.0;
    vec3 rd = getRayDir(xy, ro, lt, 0.3);


    RGBA.rgb = render(ro, rd);

    if (isHeatMap) {
        float h = saturate(gMapCount / 500.0);
        vec3 cool = vec3(0.0, 0.0, 0.7);
        vec3 warm = vec3(1.0, 0.9, 0.0);
        RGBA.rgb = mix(cool, warm, h);
    }

    // gamma
    RGBA.rgb = pow(RGBA.rgb, vec3(1.0/2.2));

    RGBA.a   = 1.0;
}

