#define RES    (iResolution.xy)
#define MINRES (min(RES.x, RES.y))

const vec3 vX = vec3(1.0, 0.0, 0.0);
const vec3 vY = vX.yxy;
const vec3 vZ = vX.yyx;
const vec3 v0 = vX.yyy;
const vec3 v1 = vX.xxx;

mat2 rot2(in float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat2(c, s, -s, c);
}

float saturate(float v) { return clamp(v, 0.0, 1.0); }
vec2  saturate(vec2  v) { return clamp(v, 0.0, 1.0); }
vec3  saturate(vec3  v) { return clamp(v, 0.0, 1.0); }

float sqr(float a) {
    return a * a;
}


float sdSphere(in vec3 p, in float r) {
    return length(p) - r;
}

float sdDisk(in vec2 p, in float r) {
    return length(p) - r;
}

// Inigo Quilez
// https://iquilezles.org/articles/distfunctions
float sdVerticalCapsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}

// Inigo Quilez
// https://iquilezles.org/articles/distfunctions
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}


//--------------------------------------------------------------------------------

// iq
float smin(float a, float b, float k) {
    float h = max(k - abs(a-b), 0.) / k;
    return min(a, b) - h*h*h*k*1./6.;
}

// Inigo Quilez
// https://iquilezles.org/articles/sdfrepetition/
// fast space repetition by mirroring every other instance
// returns (vec2 space, vec2 id)
vec4 repeated_mirrored( vec2 p, float s )
{
    vec2 id = round(p/s);
    vec2  r = p - s*id;
    vec2  m = vec2( ((int(id.x)&1)==0) ? r.x : -r.x,
                   ((int(id.y)&1)==0) ? r.y : -r.y );
    return vec4(m, id);
}


//--------------------------------------------------------------------------------

// from https://www.cs.princeton.edu/courses/archive/fall00/cs426/lectures/raycast/sld017.htm
float rayVsPlane(in vec3 ro, in vec3 rd, in vec3 n, in float d) {
    float t = -(dot(ro, n) + d) / (dot(rd, n));
    if (t < 0.0) {
        t = 1e9;
    }
    return t;
}


//--------------------------------------------------------------------------------

// SebH
// https://www.shadertoy.com/view/ldjczd
float whangHashNoise(uint u, uint v, uint s)
{
    uint seed = (u*1664525u + v) + s;

    seed  = (seed ^ 61u) ^(seed >> 16u);
    seed *= 9u;
    seed  = seed ^(seed >> 4u);
    seed *= uint(0x27d4eb2d);
    seed  = seed ^(seed >> 15u);

    float value = float(seed) / (4294967296.0);
    return value;
}

