    float angle = iTime;
    float s = sin(angle);
    float c = cos(angle * 0.5);

    mat2 rotMat = mat2(c, s, -s, c);
    vec2 rotUv = ((uv - 0.5) * rotMat) + 0.5;

    vec4 recursive = texture(iChannel0, 1.001 * vec2(uv.x / fraction, uv.y) - rotUv * 0.005) / 1.0005;

    vec3 colMul = 0.5 + 0.5 * sin(iTime + vec3(0.0, 2.09439, 4.18879));

    float add = 1.0 / (1.0 + exp(200.0 * length(rotUv - 0.45)));

    fragColor = clamp(recursive + vec4(add * colMul, 1.0), vec4(0.0), vec4(1.0));
}

