#define TWOPI 6.283185
#define PI 3.14159
#define DEG2RAD 3.14159 / 180.
#define RAD2DEG 180. / 3.14159 
#define C_VYNIL_1 vec3(0.05)
#define C_VYNIL_2 vec3(0.2)
#define C_VYNIL_1L vec3(0.2)
#define C_VYNIL_2L vec3(0.6)
#define C_BACK vec3(0.8, 0.8, 1.0)
#define C_IN vec3(.5, .2, .2)
#define RED vec3(1., 0., 0.)
#define GRE vec3(0., 1., 0.)
#define BLU vec3(0., 0., 1.)

#define VYNIL_AREA_1 .05
#define VYNIL_AREA_2 .25
#define VYNIL_AREA_3 .35
#define VYNIL_AREA_4 .85
#define VYNIL_AREA_5 .9
#define VYNIL_LINE_LEN .01

float xstep(float minV, float maxV, float v) {return step(minV, v) * step(v, maxV);}
float sat(float t) {return clamp(t, 0., 1.);}
float normrange(float v, float minV, float maxV) { return sat((v-minV)/(maxV-minV)); }
float xnormrange(float v, float minV, float maxV) { return normrange(v, minV, maxV) * xstep(minV, maxV, v); }
float normAng(float a) { return mod(mod(a, TWOPI) + TWOPI, TWOPI); }
float angDist(float a1, float a2) {
  a1 = normAng(a1);
  a2 = normAng(a2);
  float diff1 = normAng(a1 - a2);
  float diff2 = normAng(a2 - a1);
  return min(diff1, diff2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy - 0.5) * vec2(iResolution.x/iResolution.y, 1) * 2.;

    float d = length(uv);

    vec3 col = vec3(0, 0, 0);
    float stepCircIn1 = xstep(0.          , VYNIL_AREA_1, d);
    float stepCircIn2 = xstep(VYNIL_AREA_1, VYNIL_AREA_2, d);
    float stepCircIn3 = xstep(VYNIL_AREA_2, VYNIL_AREA_3, d);
    float stepCircIn4 = xstep(VYNIL_AREA_3, VYNIL_AREA_4, d);
    float stepCircIn5 = xstep(VYNIL_AREA_4, VYNIL_AREA_5, d);

    float d5 = xnormrange(d, VYNIL_AREA_4, VYNIL_AREA_5);
    float d4 = xnormrange(d, VYNIL_AREA_3, VYNIL_AREA_4);
    float d3 = xnormrange(d, VYNIL_AREA_2, VYNIL_AREA_3);
    float d2 = xnormrange(d, VYNIL_AREA_1, VYNIL_AREA_2);
    float stepCircOut = step(VYNIL_AREA_5, d);

    col += stepCircOut * C_BACK;
    col += stepCircIn1 * C_BACK;

    float uvAng = normAng(atan(uv.y, uv.x));
    float lightAng = mod(30. + iTime/.01, 360.) * DEG2RAD;
    float lightAngRange = 60. * DEG2RAD;
    float lightAngDist = min(abs(angDist(uvAng, lightAng)), abs(angDist(uvAng, lightAng+PI))); 
    float lightStep = step(lightAngDist, lightAngRange*.5);
    float lightStepSmooth = 1. - xnormrange(lightAngDist, 0., lightAngRange * .5);
    float noLightStep = mod(lightStep + 1., 2.);

    float lsin = sin(d5 * TWOPI*(VYNIL_AREA_1/VYNIL_LINE_LEN));
    vec3 col5 = vec3(0.);

    vec3 noLightColor = mix(C_VYNIL_1, C_VYNIL_2, lsin);
    vec3 lightColor = mix(C_VYNIL_1L, C_VYNIL_2L, lsin);
    col5 += stepCircIn5 * noLightStep * noLightColor;
    col5 += stepCircIn5 * lightStep * mix(noLightColor, lightColor, lightStepSmooth);
    col += col5;


    vec3 col4 = vec3(0.);
    float linesAmount = 5.;
    float lsin4 = sin(xnormrange(fract(d4*linesAmount), .0, .15)*TWOPI);
    vec3 noLightColor4 = mix(C_VYNIL_1, C_VYNIL_2, lsin4);
    vec3 lightColor4 = mix(C_VYNIL_1L, C_VYNIL_2L, lsin4);
    col4 += stepCircIn4 * noLightStep * noLightColor4;
    col4 += stepCircIn4 * lightStep * mix(noLightColor4, lightColor4, lightStepSmooth);
    col += col4;

    vec3 col3 = vec3(0.);
    float linesAmount3 = 2.;
    float lsin3 = sin(xnormrange(fract(d3*linesAmount3), .0, .25)*TWOPI);
    vec3 noLightColor3 = mix(C_VYNIL_1*.9, C_VYNIL_2*.5, lsin3);
    vec3 lightColor3 = mix(C_VYNIL_1*.9, C_VYNIL_2L*.5, lsin3);
    col3 += stepCircIn3 * noLightStep * noLightColor3;
    col3 += stepCircIn3 * lightStep * mix(noLightColor3, lightColor3, lightStepSmooth);
    col += col3;


    col += stepCircIn2 * mix(C_IN, C_IN*1.2, sat(sin(d2*PI)*2.));

    // Output to screen
    fragColor = vec4(col, 1.0);
}

float g(float x, float sigma, float mu) {
    x -= mu;
    sigma = sigma != 0.0 ? 1.0/sigma : 1.0;
    x *= sigma;
    x *= x;
    return sigma*0.3989422804014326779399460599343818684758586311649346576659258297*exp(-0.5*x);
}

