float noise(vec2 x) {
x *=2.0; // scale the noise
    float fx = fract(x.x);
    float fy = fract(x.y);
    x = floor(x);
    return mix(
    mix(texelFetch_repeat(iChannel2, ivec2(x)+ivec2(0,0), 0).r>0.9?1.0:0.0,texelFetch_repeat(iChannel2, ivec2(x)+ivec2(1,0), 0).r>0.9?1.0:0.0,fx),
    mix(texelFetch_repeat(iChannel2, ivec2(x)+ivec2(0,1), 0).r>0.9?1.0:0.0,texelFetch_repeat(iChannel2, ivec2(x)+ivec2(1,1), 0).r>0.9?1.0:0.0,fx),
    fy);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    //fragCoord *= 0.3*sin(iTime)+0.6;
    //float scale = sin(iTime)+2.0;
    float scale = 4.0;
    fragCoord += scale*iMouse.xy;
    fragCoord *= 1.0/(scale+1.0);
    fragColor = vec4(0);

    vec2 x;
    const float dt = 0.25;
    const float sig = 10.0;
    x=vec2(0);
    for(int i = 0; i < 99; i++) {
        x += dt*texture(iChannel1, (fragCoord+x)/iResolution.xy).xy;
        fragColor += g(dt*float(i), sig, 0.0)*vec4(vec3(noise(fragCoord+x)), 1.0);
    }
    x=vec2(0);
    for(int i = 0; i < 99; i++) {
        x -= dt*texture(iChannel1, (fragCoord+x)/iResolution.xy).xy;
        if(i == 0) continue;
        fragColor += g(dt*float(i), sig, 0.0)*vec4(vec3(noise(fragCoord+x)), 1.0);
    }

    fragColor = vec4(fragColor.a != 0.0 ? fragColor.rgb / fragColor.a : fragColor.rgb, 1.0);
    //fragColor = vec4(texture(iChannel0, fragCoord/iResolution.xy).rgb*(1.0-5.0*fragColor.rgb), 1.0);
    fragColor = vec4(texture(iChannel3, fragCoord/iResolution.xy).rgb*(1.0-2.0*fragColor.rgb), 1.0);
}

