vec4 texelFetch_repeat(sampler2D _sampler, ivec2 _coord, int _lod) {
    // no idea why the -1 is neccesary but the pressure is slightly wrong without it
    // maybe textureSize() is wrong?
    ivec2 _textureSize = textureSize(_sampler, _lod) - 1;

    // :sob: in GLSL, the % operator is undefined if one or both input integers are negative
    // so we blasphemously convert our integers to floats and back and use the float modulo
    //_coord.x = int(mod(float(_coord.x), float(_textureSize.x)));
    //_coord.y = int(mod(float(_coord.y), float(_textureSize.y)));
    //return texelFetch(_sampler, _coord, _lod);

    // fixed! https://www.alecjacobson.com/weblog/?p=1140
    return texelFetch(_sampler, ((_coord % _textureSize) + _textureSize) % _textureSize, _lod);
}

