    divergence += texelFetch_repeat(Divergence, ivec2(fragCoord) + ivec2( 1, -1), 0).x * 8.0;
    divergence += texelFetch_repeat(Divergence, ivec2(fragCoord) + ivec2( 1,  2), 0).x * 3.0;
    divergence += texelFetch_repeat(Divergence, ivec2(fragCoord) + ivec2( 1,  1), 0).x * 8.0;
    divergence += texelFetch_repeat(Divergence, ivec2(fragCoord) + ivec2( 0, -3), 0).x * 1.0;
    divergence += texelFetch_repeat(Divergence, ivec2(fragCoord) + ivec2( 0, -2), 0).x * 4.0;
    divergence += texelFetch_repeat(Divergence, ivec2(fragCoord) + ivec2( 0,  3), 0).x * 1.0;
    divergence += texelFetch_repeat(Divergence, ivec2(fragCoord) + ivec2( 0,  2), 0).x * 4.0;
    divergence += texelFetch_repeat(Divergence, ivec2(fragCoord) + ivec2( 0,  0), 0).x * 64.0;

    return (pressure - divergence) / 256.0;
