// ---------------------------------------------------------------------------------------
//	Created by fenix in 2023
//	License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
//  Another 2D particle experiment. I wanted to see what it would look like to model
//  circular particles with rotation and angular velocity.  I'm pleased with the result,
//  especially how the stacks get rolling all together with alternating directions per
//  level.
//
//  The rendering is also an experiment, combining a 2D particle simulation with a 3D
//  rendering style. For the shadow render, the particles are spheres, but the particle
//  render itself is just a 2.5D hack.
//
//      *mouse to interact*
//      *shift to render shock directions*
//      *space to reset*
//
//  Buffer A, B, and C perform particle simulation. Only A updates the particle neighbors.
//  Buffer D computes screen-space voronoi for render.
//
// ---------------------------------------------------------------------------------------

const vec3 LDIR = normalize(vec3(-10, 10, -1));
const float THICK = .1;

// From iq's Extrusion and Revolution SDF
// https://www.shadertoy.com/view/4lyfzw
float opExtrusion(vec3 p, float sdf, float h)
{
    vec2 w = vec2(sdf, abs(p.z) - h);
  	return min(max(w.x, w.y), 0.) + length(max(w, 0.));
}

// 3D SDF of shelves/wall
float mapScene(vec3 p)
{
    float d = opExtrusion(p, sdScene(p.xy, R), THICK);
    return min(d, THICK - p.z);
}

// normal of shelves/wall
vec3 normScene(vec3 p)
{
    const vec2 e = vec2(.001, 0);
    return normalize(vec3(mapScene(p + e.xyy) - mapScene(p - e.xyy),
                          mapScene(p + e.yxy) - mapScene(p - e.yxy),
                          mapScene(p + e.yyx) - mapScene(p - e.yyx)));
}

// raymarch shelves/wall
vec3 marchScene(vec3 p, vec3 rd)
{
    for (int i = 0; i < 100; ++i)
    {
        float d = mapScene(p);
        if (d < .001) return p;
        p += rd * d;
    }
    return p;
}

// 3D SDF of particles, for shadow render
float mapParticles(vec3 p)
{
    // render particle
    uint id = fxGetClosest(ivec2(p.xy * R.y + .5 * R)).x;
    float d = 1e6;
    if (id < g_MaxParticles)
    {
        fxParticle v = fxGetParticle(id);
        vec2 del = v.pos - p.xy;

        d = length(vec3(del, p.z)) - CANDY_R;
    }

    return d;
}

// From iq's Soft Shadow Variation
// https://www.shadertoy.com/view/lsKcDD
float calcSoftshadow(vec3 ro, vec3 rd, float mint, float tmax, bool par)
{
	float res = 1.;
    float t = mint;
    float ph = 1e10;

    for (int i = 0; i < 32; i++)
    {
		float h = mapScene(ro + rd * t);
        if (par) h = min(h, mapParticles(ro + rd * t)); // only render particles into shadow if rendering background

        float y = i == 0 ? 0. : h*h/(2.*ph); 
        float d = sqrt(h * h - y * y);

        res = min(res, 15. * d / max(0., t - y));
        ph = h;
        t += h;

        if (res < .0001 || t > tmax) break;
    }
    res = clamp(res, 0., 1.);
    return res * res * (3. - 2. * res);
}

const vec3 SHDIR = normalize(vec3(-1, 1, -2));

// render a single particle
void renderParticle(fxParticle p, vec2 u, uint id, inout vec4 O)
{
    float e = .5 / R.y;
    vec2 del = u - p.pos;
    float d2 = length2(del);
    if (d2 < sqr(CANDY_R + e))
    {
        float d = sqrt(d2);
        float a = atan(del.y, del.x) + p.r + d / CANDY_R * ((id & 2U) == 0U ? 1. : -1.);

        vec3 col = (id % 2U) == 0U ? vec3(1, .3, .3) : vec3(.2, .5, .2); // green or red
        const float STRIPE_SIZE = PI * .0625;
        float blur = e * 5000. * abs(p.av);
        col = mix(col, vec3(1), smoothstep(STRIPE_SIZE + blur, STRIPE_SIZE - blur, abs(mod(a, PI * .25) - PI * .125))); // stripes
        col = mix(col, vec3(1), smoothstep(CANDY_R * .5 + e, CANDY_R * .1, d)); // center

        if (keyDown(KEY_SHIFT))
            col = sin(atan(abs(p.sd.y * .1), p.sd.x) + vec3(0, 11, 33)) * .5 + .5;

        del *= smoothstep(CANDY_R * .6, CANDY_R, d); // make flat spot
        vec3 n = normalize(vec3(del, CANDY_R - sqrt(del.x * del.x + del.y * del.y))); // compute normal

        float sh = calcSoftshadow(vec3(u, 0), SHDIR, .01, .25, false); // shadow
        float diff = max(-.2, dot(n, LDIR)) * .3 + .15; // diffuse
        col *= diff * (.5 * sh + .5); // shadows only darken diffuse

        vec3 re = reflect(LDIR, n); // reflected
        vec3 rd = normalize(vec3(u, -1)); // ray dir
        float spec = pow(max(0., dot(rd, re)), 8.) * sh; // specular

        O.a = max(O.a, smoothstep(CANDY_R + e, CANDY_R - e, d)); // alpha
        O.rgb = min(O.rgb, col + spec);
    }
}

// From https://knarkowicz.wordpress.com/2016/01/06/aces-filmic-tone-mapping-curve/
vec3 ACESFilm(vec3 x)
{
    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    return clamp((x*(a*x+b))/(x*(c*x+d)+e), 0., 1.);
}

void mainImage(out vec4 O, vec2 u)
{
    computeMaxParticles();
    fxState s = fxGetState();

    vec2 p = .96 * (u - vec2(.5, .52) * R.xy) / R.y; // chop off the top so we don't have missing shadows
    O = vec4(1, 1, 1, 0);

    // render scene
    vec3 rd = normalize(vec3(p - vec2(0, 1), 10));
    vec3 ro = vec3(0, 1, -10);
    vec3 hit = marchScene(ro, rd);
    vec3 n = normScene(hit);

    if (hit.z > 0.) // don't render particles where blocked by a shelf
    {
        // render particles
        uvec4 ids = fxGetClosest(ivec2(p * R.y + .5 * R));
        for (int i = 0; i < 4; ++i)
        {
            uint id = ids[i];
            if (id >= g_MaxParticles) break;
            fxParticle v = fxGetParticle(id);
            renderParticle(v, p, id, O);
        }
    }

    if (O.a < 1.) // only render shadows where it is not completely blocked by a particle
    {
        // render scene
        float sh = calcSoftshadow( hit, SHDIR, .01, .5, true) * .2 + .3;
        vec3 col = (max(0., dot(n, LDIR) * .2 + .2) * vec3(.7, .7, .86)) * sh;
        O.rgb = mix(col, O.rgb, O.a);
    }

    // Simple vignette effect by Ippokratis
    // https://www.shadertoy.com/view/lsKSWR
	vec2 uv = u / R;
    uv *=  1. - uv.yx;   //vec2(1.0)- uv.yx; -> 1.-u.yx; Thanks FabriceNeyret 
    float vig = uv.x*uv.y * 15.; // multiply with sth for intensity
    vig = pow(vig, .15); // change pow for modifying the extend of the  vignette
    O *= vig;

    O.rgb = pow(ACESFilm(O.rgb), vec3(1./2.2));
    O.a = 1.;
}

