// Modifications by Theron Tarigo  2023

    vec3 rdd = DIR(ray).xyz;
    vec3 roo = ORI(ray).xyz;

	// ray-box intersection in box space
    vec3 m = 1.0/rdd;
    vec3 n = m*roo;
    vec3 k = abs(m)*rad;

    vec3 t1 = -n - k;
    vec3 t2 = -n + k;

	float tN = max( max( t1.x, t1.y ), t1.z );
	float tF = min( min( t2.x, t2.y ), t2.z );

	if( tN > tF || tF < 0.0) return surf_none();

	vec3 nor = -sign(rdd)*step(t1.yzx,t1.xyz)*step(t1.zxy,t1.xyz);
    vec3 norback = -sign(rdd)*step(t2.xyz,t2.yzx)*step(t2.xyz,t2.zxy);

    float s = 1.;
    if(all(lessThan(abs(roo),rad)))s=-1.;

    nor=nor*mat3(ray);
    norback=norback*mat3(ray);

    if(s>0.) return segc(false,nor,tN,norback,tF);
    return segc(true,norback,tF,vec3(0),DIST_INF);
}


seg intersect(seg A, seg B) {

  // sort nears
  if(A.n.w>B.n.w) { seg T=A;A=B;B=T; }

  seg R=surf_none();
  R.inside=A.inside&&B.inside;
  // most conservative case: never step beyond first surface, whatever it is
  R.n=A.n; R.f.w=0.;
  // return R;

  // if one of the segs is a step, step to it
  // should check for one finite when other is a step...
  if( A.n.w>A.f.w || B.n.w>B.f.w ) return R;

  if(A.inside&&B.inside) {R.f=A.f.w<B.f.w?A.f:B.f; }
  if(!A.inside&&!B.inside) {R.n=B.n; R.f=A.f.w<B.f.w?A.f:B.f; }
  if(A.inside&&!B.inside) {
    if(A.f.w<B.f.w) {R.n=B.n;R.f.w=0.; }
    else {R.n=A.n;R.f.w=0.; }
  }
  if(!A.inside&&B.inside) {
    if(A.f.w<B.f.w) { R.n=A.n; R.f=A.f; R.f.w=0.; }
    else { R.n=A.n; R.f=A.n; }
  }

  return R;
}

seg neg (seg A) {
  A.inside=!A.inside;
  return A;
}

seg uniond (seg A, seg B) {
  return neg(intersect(neg(A),neg(B)));
}

seg unionconvex (seg A, seg B) {
// to be used only when each input is either a convex primitive or a result of unionconvex !
  seg R = surf_none();
  R.inside = A.inside||B.inside;
  if(A.n.w>B.n.w) { seg T=A;A=B;B=T; }
  R.n=A.n;
  R.f=B.n.w>A.f.w?A.f:B.f;
  return R;
}

seg makestep (seg A) {
  A.f.w=0.;
  return A;
}

