  vec3 lastnorm=vec3(0);
  vec3 hitnorm;
  float hitdist;
  for(int iter=0;iter<itermax;iter+=1+rtzero) {

    seg d = geometry(ray);

    d.n.w=max(d.n.w,.0001); // should scale to current dist?
    hitdist=(d.n.w);
    bool surf=true;
    hitnorm=d.n.xyz;

    if(d.n.w>d.f.w) {
      surf=false;
    }
    if(d.inside) {
      hitdist=0.;
      hitnorm=vec3(0,0,0);
      surf=true;
    }

    if(hitnorm==vec3(0))hitnorm=lastnorm;

    if(hitdist>=DIST_INF) break;
    ray[3].xyz+=ray[0].xyz*(hitdist)*(1.0001);
    distaccum+=hitdist;
    hitdist=distaccum;
    if(!surf&&hitnorm!=vec3(0)) lastnorm=hitnorm;
    if(hitnorm==vec3(0))hitnorm=lastnorm;

    if(surf) break;
  }

  vec3 col=vec3(.5);
  vec3 n=normalize(mat3(ray)*hitnorm);
  vec3 p=ray[3].xyz;
  if(hitdist<DIST_INF){
    col=.7+.3*n;
    col*=tex_triplan(iChannel1,p/4.,n);
    p.z+=.9;
    p.z*=4.;
    col*=(1.-.8*exp(-.3*dot(p,p)));
  }

  ImgOut.rgb = col;
}

