vec3 tone(vec3 v)
{
    mat3 m = mat3(.842479062253094,  .0423282422610123, .0423756549057051,
                  .0784335999999992, .878468636469772,  .0784336,
                  .0792237451477643, .0791661274605434, .879142973793104);

    v = sat((log2(m * v) + 12.47393) / 16.5);

    vec3 v2 = v * v;

    v = v2 * (v2 * (15.5 * v2 - 40.14 * v + 31.96) - 6.868 * v + .4298) + .1191 * v - .00232;

    v = inverse(m) * mix(vec3(dot(v, vec3(.2126, .7152, .0722))),
                         pow(vec3(1, .9, .7) * v, vec3(1.3)), 1.2);

    return mix(1.055 * pow(v, vec3(1) / 2.4) - .055, v * 12.92, lessThan(v, vec3(.0031308)));
}

// Fix by ttg: smooth noise -> white noise

void mainImage(out vec4 O, vec2 I)
{
    O = vec4(0);

    if (1.5 * abs(I.x - .5 * R.x) > R.y) return;

    vec2 p = I / R.xy;

    vec3 col = vec3(texture(iChannel0, p - vec2(.001, 0)).x,
                    texture(iChannel0, p).y,
                    texture(iChannel0, p + vec2(.001, 0)).z);

    col = tone(col * (h33(I.xyx) * .3 + .7) * (p.y * .8 + .2));

    col += S01(col);

    O = vec4(col * .5, 1);
}

