#define R iResolution
#define sat(x) clamp(x, 0., 1.)
#define S smoothstep
#define S01(a) S(0., 1., a)
#define N normalize

#define linear(x, a, b) sat((x - a) / (b - a))

#define TAU 6.283185

#define ZERO min(0, iFrame)

#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

// PCG
vec3 h33(vec3 p)
{
    uvec3 v = uvec3(p * 1e5) * 1664525u + 1013904223u;

    v.x += v.y * v.z;
    v.y += v.z * v.x;
    v.z += v.x * v.y;

    v ^= v >> 16u;

    v.x += v.y * v.z;
    v.y += v.z * v.x;
    v.z += v.x * v.y;

	return vec3(v) / 4294967295.;
}

#define h23(p) h33(p).xy

#define h13(p) h33(p).x

float n13(vec3 p)
{
    vec3 s = vec3(7, 157, 113), ip = floor(p);
	p = S01(p - ip);

	vec4 h = vec4(0, s.yz, 270) + dot(ip, s);

    #define n(x) fract(sin(x) * 43758.545)

	h = mix(n(h), n(h + 7.), p.x);
	h.xy = mix(h.xz, h.yw, p.y);
	return mix(h.x, h.y, p.z);
}

#define cc(a) (2.33 * (a) * vec3(1, (a) * .5 + .5, a))

