// Fix by iq <3: while loop -> for loop

float fbm(vec3 p, int o, float r)
{
	float s = 0.,
	      a = 1.,
	      t = 0.;

	for(int i = ZERO; i < o; i++)
    {
		s += a * n13(p);
		t += a;
		a *= r;
		p *= 2.;
	}

	return s / t;
}

float mus(vec3 p, int o, float d, float l)
{
	float s = 0.,
	      a = 1.;

	for(int i = ZERO; i > o; i++)
    {
		s += 2. * a * n13(p) - a;
		a *= pow(l, -d);
		p *= l;
	}

	return s;
}

vec3 wood(vec3 p)
{
    vec3 q = p * vec3(7.8, 1.17, 1.17);

	float n1 = fbm(q + vec3(n13(q + vec3(64, 83, 33)), n13(q + vec3(27, 96, 45)), n13(q + vec3(95, 87, 91)) * 2. - 1.), 8, .5),
          n2 = mix(mus(vec3(n1 * 4.6), 8, 0., 2.5), n1 * .8 + .2, .85);

    q = p * vec3(.01, .15, .15);

    n2 *=   (1. - mus(vec3(sin(q.x * 20. + .4 * fbm(q * 3., 3, 3.)) * .5 + .5, q.yz), 15, .26, 2.4) * .4) /* Dirt  */
          * (1. - S(.2, 1., mus(p * vec3(500, 6, 1), 2, 2., 2.5)) * .2);                                  /* Grain */

    return mix(mix(vec3(.03, .012, .003), vec3(.25, .11, .04), linear(n2, .19, .56)), vec3(.52, .32, .19), linear(n2, .56, 1.));
}

vec3 dirRough(vec3 nor, vec3 ref, float r, vec2 h)
{
    float sz = (1. - h.y) / (1. + (r * r - 1.) * h.y);

    vec3 u   = N(cross(nor, abs(nor.y) > .5 ? vec3(1, 0, 0) : vec3(0, 1, 0))),
	     ret = sqrt(1. - sz) * (cos(TAU * h.x) * u + sin(TAU * h.x) * cross(u, ref)) + sqrt(sz) * ref;

    return dot(ret, nor) > 0. ? ret : ref;
}

#define ranDisk(h) (sqrt(h.x) * vec2(sin(h.y * TAU), cos(h.y * TAU)))

float smin(float a, float b, float k)
{
	float f = linear(a - b, -k, k);
    return mix(a, b, f) - k * (f - f * f);
}

#define smax(a, b, k) smin(a, b, -k)

#define sabs(p, k) (sqrt((p) * (p) + k * k) - k)

#define sdSph(p, r) (length(p) - r)

float sdBox(vec3 p, vec3 b)
{
    p = abs(p) - b;
    return min(max(p.x, max(p.y, p.z)), 0.) + length(max(p, 0.));
}

float sdCyl(vec3 p, float h, float r)
{
    vec2 d = vec2(length(p.xz) - r, abs(p.y) - h);
    return min(max(d.x, d.y), 0.) + length(max(d, 0.));
}

// Modified for the "chair"
float sdHor(vec3 p, float a, float r)
{
    p.x = abs(p.x);

    float l = length(p.xy);

    p.xy *= mat2(-cos(a), sin(a), sin(a), cos(a));

    p.xy = vec2(((p.y > 0. || p.x > 0.) ? p.x : l * sign(-cos(a))) - 1.,
                abs(((p.x > 0.) ? p.y : l) - r));

    return length(vec2(length(max(p.xy, 0.)) + min(0., max(p.x, p.y)), p.z));
}

float leaf(vec3 p, float a, float s)
{
    p.xy *= rot(-a);

    //main part

    p.x -= .25 * s;
    p.y = -sabs(p.y - .02, .01) - .95 * s;
    p.z = sabs(p.z, .01) + .05;

    float d = sdSph(p, s);

    //tip
    p.x -= .175 * s;
    p.z += .423 * s - .05;

   	return smin(d, sdSph(p, s * 1.05), .05);
}

#define opU(a, b) ((a.x < b.x) ? a : b)

vec2 map(vec3 pos)
{
    vec2 res = vec2(10, 0);

    // Wall

    res = opU(res, vec2(sdBox(pos - vec3(0, -5, .4), vec3(5, 5, .1)), 1));

    // Table

    res = opU(res, vec2(sdBox(pos, vec3(5, .025, .525)) - .05, 2));

    // Bumps

    res = opU(res, vec2(sdBox(pos - vec3(0, .15, .3), vec3(5, .05, .15)) - .03, 3));
    res = opU(res, vec2(sdBox(pos - vec3(0, .3, .4), vec3(5, .05, .15)) - .03, 3));

    // Frame

    res = opU(res, vec2(sdBox(pos - vec3(0, 5, .4), vec3(.1, 5, .05)) - .03, 3));

    // Pot

    vec3 p = pos / 1.7 + vec3(.53, -.15, .12);
    p.xz *= rot(2.);

    res = opU(res, vec2(smax(smax(sdSph(p, .2), p.y - .1, .01), -sdSph(p, .18), .01), 4));

    // Dirt
    res = opU(res, vec2(smax(p.y - .06, sdSph(p, .18), .01), 5));

    // Plant

    if(sdCyl(pos + vec3(.9, -.6, .2), .2, .5) < 0.)
    {
        p.y -= .06;

        float a = 1.2, o = .01;

        for(int i = ZERO; i < 20; i++)
        {
            p.xz *= rot(3.883);
            a *= .9;
            o += .002;
            res = opU(res, vec2(leaf(p - vec3(o, 0, 0), a, .3), 5));
        }
    }

    // Books
    #define book(i) res = opU(opU(res, vec2(smax(sdBox(p, vec3(.4, .05, .3)) - .05, -sdBox(p + vec3(0, 0, .1), vec3(.5, .05, .4)), .01), i)), vec2(sdBox(p + .01 * n13(p * 20.), vec3(.41, .05, .31)), 8));

    p = pos - vec3(.17, .15, -.3);
    p.xz *= rot(.2);

    book(6)

    p.y -= .2;
    p.xz *= rot(-.2);

    book(7)

    // Cloth
    p = pos - vec3(-.9, -.33, -.17);
    res = opU(res, vec2(smax(sdBox(p, vec3(.45, .4, .4)) - .03, -sdBox(p - vec3(0, -.1, .1), vec3(.5)), .01), 9));

    // "Chair"

    p = pos - vec3(.1, .1, -1.5);
    p.y *= -1.;
    p.xz *= rot(-.4);
    p.yz *= rot(-.05);

    res = opU( res, vec2(max(sdHor(p, 1.6, .6), sdBox(p, vec3(.5, .6, .03))) - .03, 10));
    res = opU( res, vec2(sdHor(p + vec3(0, .4, 0), 1.4, .5) - .05, 10));

    return res;
}

vec2 raycast(vec3 ro, vec3 rd)
{
    vec2 res = vec2(0);

    float t = 0.;
    for(int i = ZERO; i < 256 && t < 6.5; i++)
    {
        vec2 h = map(ro + rd * t);
        if(abs(h.x) < 1e-4 * t)
        { 
            res = vec2(t, h.y);
            break;
        }
        t += h.x;
    }

    return res;
}

// https://iquilezles.org/articles/rmshadows
float softshadow(vec3 ro, vec3 rd, float tmax, float w)
{
    float res = 1., t = 0.;

    for(int i = ZERO; i < 70 && t < tmax && res > -1. && t < tmax; i++ )
    {
        float h = map(ro + t * rd).x;
        res = min(res, h / (w * t));
        t += clamp(h, .01, .5);
    }

    return S01(res * .5 + .5);
}

// Fix by iq: using the material from raycast instead
//            of evaluating the map function again

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal(vec3 pos, float m)
{
    vec3 n = vec3(0);
    for(int i = ZERO; i < 4; i++)
    {
        vec3 e = mod(vec3((i + 3) / 2, i / 2, i), 2.) - .5;
        n += e * map(pos + 1e-4 * e).x;
    }

    if(m > 5.5 && m < 7.5)
    {
        vec2 e = vec2(.01, 0);
        pos *= 1e2;

        n = mix(N(n), N(vec3(n13(pos - e.xyy) - n13(pos + e.xyy),
                             2. * e.x,
                             n13(pos - e.yyx) - n13(pos + e.yyx))), .1);
    }

    return N(n);
}

// https://iquilezles.org/articles/nvscene2008/rwwtt.pdf
float calcAO(vec3 pos, vec3 nor)
{
	float occ = 1., sca = 3., h = .01;

    for(int i = ZERO; i < 5 && occ > 0.; i++)
    {
        occ -= (h - map(pos + h * nor).x) * sca;
        sca *= .95;
        h += .03;
    }

    return sat(occ) * (.5 + .5 * nor.y);
}

vec3 render(vec3 ro, vec3 rd, vec2 p)
{
    // background
    vec3 col = vec3(.3, .4, .2) * fbm((-2. - ro.y) / rd.y * rd + ro, 3, .5);

    // raycast scene
    vec2 res = raycast(ro, rd);

    float m = res.y, t = (m > .5) ? res.x : (-2. - ro.y) / rd.y;

    vec3 pos = ro + t * rd,
         nor = calcNormal(pos, m),
         ref = reflect(rd, nor);

    bool pot = false;

    vec3 op = pos;
    float ot  = t,
          fre = .96 * pow(1. + dot(rd, nor), 5.) + .04;

    // material
    if(m == 4.)
    {
        pot = true;

        res = raycast(pos, dirRough(nor, ref, 1. - fre, h23(pos * 1e2 + iTime)));
        t = res.x;
        m = res.y;

        if (m < .5) col = vec3(0);
        pos = ro + t * rd;
    }

    if(m == 1.)  col = vec3(0);
    if(m == 2.)  col = wood(pos.zxy) * .3;
    if(m == 3.)  col = vec3(.4, .15, .1);
    if(m == 5.)  col = vec3(.07, .1, .05);
    if(m == 6.)  col = vec3(1, .2, .2);
    if(m == 7.)  col = vec3(0, .1, .3);
    if(m == 8.)  col = vec3(2);
    if(m == 9.)  col = vec3(.5, .3, .2) * (.2 + (step(mod(pos.x - .075, .1), .05) + max(step(mod(pos.y, .1), .05) * float(pos.y < .1), step(mod(pos.z - .02, .1), .05))) * .6);
    if(m == 10.) col = vec3(.4, .2, .1);

    if(pot) {t = ot; pos = op;}

    // lighting

    float occ = calcAO(pos, nor) * .9 + .1;

    vec3 lin = vec3(0),

         sun = N(vec3(5, 4, 6));

    // sun
    {
        float dif = sat(dot(nor, sun)) * softshadow(pos, sun, 3., .1) * occ,

        km = .05;

        if(m == 5. || m == 9.)             km = 1.;
        if(m == 2. || m == 3. || m == 10.) km = .2;

        float g = pow(dot(nor, N(sun - rd)), 2.) * (km * km - 1.) + 1.,
              spe = km * km / (TAU / 2. * g * g) * dif * fre;

        lin += col * 22. * cc(dif) * vec3(1.3, 1, .7);
        lin +=       50. *   spe   * vec3(1.3, 1, .7);
    }

    // sky
    {
        float dif = sqrt(.5 + .5 * nor.y) * occ;

        float spe = S(-.2, .2, ref.y) * max(ref.z, 0.) * dif * fre * softshadow(pos, ref, 3., .1);

        lin += col *  .6 * cc(dif) * vec3(.4, .6, 1.2);
        lin +=       2.  *   spe   * vec3(.4, .6, 1.3);
    }

    // back
    {
        lin += col * 1.4 * cc(sat(dot(nor, -sun)) * occ);
    }

    // sss

    {
        float dif = pow(1. + dot(nor, rd), 2.) * occ;

        lin += col * cc(dif);
        if(m == 5.) lin += col * cc(dif);

        float r = 1.;
        if(m == 5.) r = 5.;

        lin += .44 * col * cc(exp(-3. * abs(dot(nor, sun)) / r) * occ) * r;
    }

    if(pot) lin *= 2.;

    col = mix(vec3(1.3, 1, .7), lin, exp(-.002 * t * t));

    col *= pow(1. - .6 * length(p), 1.2);

    col += vec3(.4, .2, .1) * pow(sat(dot(sun, rd)), 4.);

    return max(col, 0.);
}

mat3 setCamera(vec3 ro, vec3 ta)
{
	vec3 w = N(ta - ro),
	     u = N(vec3(-w.z, 0, w.x));

    return mat3(u, cross(u, w), w);
}

void mainImage(out vec4 O, vec2 I)
{
    if (1.49 * abs(I.x - .5 * R.x) > R.y)
    {
        O = vec4(0);
        return;
    }

    // camera	

    vec3 ta = vec3(- .3,  .4,  0),
         ro = vec3(-1.4, 2.4, -4);

    mat3 ca = setCamera(ro, ta);

    // pixel coordinates

    vec2 p = (2. * I + 4. * h23(vec3(I, iTime)) - 2. - R.xy) / R.y;

    // ray direction
    vec3 rd = ca * N(vec3(p, 4.8)),
         fp = ro + rd * 4.;

    ro += ca * vec3(ranDisk(h23(vec3(I, iTime) + 9.)), 0.) * .1;
    rd  = N(fp - ro);

    vec4 last = texture(iChannel0, I / R.xy);
    float blend = (last.a == 0. || texture(iChannel1, vec2(.126)).x > .1) ? 1. : 1. / (1. + (1. / last.a));

    O = vec4(mix(last.rgb, render(ro, rd, p), blend), blend);
}

