//hash by IQ https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x += k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

vec2 m(vec2 uv, float s){

    vec2 a,x,p=uv;

    for (int i = 0; i++<2<<iters;){

        vec2 t = p, b = t*0.;
        a = uv;
        a = mix(a, t               , s(s-b++)); // Julia set becomes this at a certain input 
        a = mix(a, vec2(uv.x,  t.y), s(s-b++)); 
        a = mix(a, vec2(t.  x,uv.y), s(s-b++));
        a = mix(a, vec2(t.  y,uv.x), s(s-b++)); // Not surprised its asymmetrical.
        a = mix(a, vec2(uv.y,  t.x), s(s-b++)); // Curly area below looks cool. Top edge might be interesting
        a = mix(a, uv.     yx      , s(s-b++)); // Favourite one
        a = mix(a, t.      yx      , s(s-b++)); // What's this?! Seems rotationally symmetrical
                                                // which is incredibly weird. Why 3? Huh???
        a = mix(a, t*uv.yx         , s(s-b++));                                
        a = mix(a, t.yx*uv         , s(s-b++));                                

        a = mix(a, uv.     xy      , s(s-b++)); // Restore to loop

        p.x = t.x*t.x - t.y*t.y + a.x;
        p.y = 2.*t.x*t.y + a.y;

        x.y = float(i);

        if (length(p) > 2.) break;
    }
    x.x = dark exp(-x.y/4e1);

    return x;

}

void mainImage( out vec4 O, vec2 U ){
    vec2 r = iResolution.xy;
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;

    float s = mod(iTime/2., 10.);
    cuv *= 2.;

    int ssaa = 1 + int(iFrameRate/10.);

    vec2 x = uv*0.;
    for (int i = 0; i++<ssaa;){

        x += m(cuv + (hash(uvec3(i)).xy*2.-1.)/r.y, s);

    }
    x /= float(ssaa);

    O = x.xxxx;
}

void mainImage( out vec4 f, vec2 p ){

    vec3 q=iResolution,d=vec3(p-.5*q.xy,q.y)/q.y,c=vec3(0,.5,.7);
    vec2 uv = p / iResolution.xy;

    q = d/(.1-d.y*5.5);
    // q.x -= 0.5;
    float a = iTime;

    float k = sin(0.2*a);

    float w = q.x *= q.x-=.05*k*k*k*q.z*q.z;
    // w *= 0.2;

    f.xyz=d.y>.015?c:
    	sin(4.*q.z+40.*a)>0.?
        w>2.?c.xyx:w>1.2?d.zzz:c.yyy:
	    w>2.?c.xzx:w>1.2?c.yxx*2.:(w>.004?c:d).zzz;


    vec3 lighting = vec3(0.2, 0.2, 0.2); // ambient lighting
    float distToSide = 1.0 - clamp(abs(w - 1.2) * 0.5, 0.0, 1.0);
    float spotStrength = (sin((4.0 * q.z + 40.0 * a) * 0.25) * 0.5 + 0.5);
    float lightStrength = spotStrength * distToSide;
    lightStrength *= lightStrength;
    lighting += vec3(0.6, 0.5, 0.3) * lightStrength * 1.2;

    if (d.y <= 0.015) { f.xyz *= lighting; } else { f.xyz *= 0.3; }

}

