void SetBldParms ()
{
  outWd = 20.;
  corWd = 2.;
  flrHt = 3.;
  stCent = vec2 (-16., 2.);
}

float DukDf (vec3 p, float dMin)
{
  vec3 q;
  vec2 cs, r;
  float d, dm, h, s;
  dm = dMin;
  p /= dukScl;
  if (0.9 * dukScl * PrSphDf (p, 1.) < dm) {
    dm /= dukScl;
    p.xz = Rot2Cs (p.xz, dukAzCs);
    p.yz = Rot2Cs (p.yz, dukElCs);
    p.z = - p.z;
    cs = sin (0.3 + vec2 (0.5 * pi, 0.));
    h = 0.5;
    r = vec2 (0.2, 0.3 + 0.05 * clamp (p.z, -2., 2.));
    s = (length (p.xz / r) - 1.) * min (r.x, r.y);
    d = min (max (s, abs (p.y) - h), length (vec2 (s, p.y)) - h);
    dm = min (dm, d);
    q = p;
    q.x = abs (q.x);
    q -= vec3 (0.5, 0.3, 0.6);
    q.yz = Rot2Cs (q.yz, cs);
    q.xy = Rot2Cs (q.xy, vec2 (cs.x, - cs.y));
    q.xz = Rot2Cs (q.xz, vec2 (cs.x, - cs.y));
    r = vec2 (0.3, 0.5 + 0.08 * clamp (q.z, -2., 2.));
    h = 0.07;
    s = (length (q.yz / r) - 1.) * min (r.x, r.y);
    d = SmoothMin (min (max (s, abs (q.x) - h), length (vec2 (s, q.x)) - h), dm, 0.05);
    dm = min (dm, d);
    d = SmoothMin (PrEllipsDf (p - vec3 (0., 0.75, -0.4), vec3 (0.4, 0.35, 0.5)), dm, 0.1);
    dm = min (dm, d);
    q = p - vec3 (0., 0.65, -0.9);
    q.zy = Rot2Cs (q.zy, vec2 (cs.x, - cs.y));
    h = 0.15;
    r = vec2 (0.15, 0.04) * (1. - 0.1 * min (2., max (0., 1. - q.z / h)));
    d = PrEllCylDf (q, r, h);
    q.z -= -0.9 * h;
    d = SmoothMin (max (d, - PrEllCylDf (q, r - 0.02, 0.5 * h)), dm, 0.01);
    dm = min (dm, d);
    q = p;
    q.x = abs (q.x);
    d = PrSphDf (q - vec3 (0.26, 0.825, -0.6), 0.125);
    dm = min (dm, d);
    dm *= 0.9 * dukScl;
  }
  return dm;
}

float ObjDf (vec3 p)
{
  vec3 q, qq;
  vec2 wSz;
  float dMin, d, a, r, s;
  dMin = dstFar;
  q = p;
  d = PrBoxDf (q, vec3 (outWd + 1., 0.2, outWd + 1.));
  DMIN (idDnFlr);
  qq = p;
  a = (length (qq.xz) > 0.) ? atan (qq.z, - qq.x) : 0.;
  qq.xz = Rot2D (qq.xz, 2. * pi * (floor (4. * a / (2. * pi) + 0.5) / 4.));
  q = qq;
  d = dot (vec2 (qq.y, abs (abs (qq.x) - outWd + corWd)), sin (0.15 * pi + vec2 (0.5 * pi, 0.))) -
     4. * flrHt + 0.02 * sin (4. * pi * qq.z) - 0.2;
  d = max (max (d, abs (qq.x + outWd - 2.) - 3.), 4. * flrHt + 0.1 - qq.y);
  DMINQ (idRf);
  q.x = abs (q.x) - outWd + corWd;
  q.y -= 2. * flrHt + 0.15;
  d = PrBox2Df (q.xy, vec2 (corWd + 0.2, 0.2));
  DMIN (idUpFlr);
  q = qq;
  q.x = abs (abs (q.x) - outWd + corWd) - corWd;
  q.y -= 2. * flrHt + 0.1;
  d = PrBox2Df (q.xy, vec2 (0.1, 2. * flrHt));
  q.yz = mod (q.yz, vec2 (2. * flrHt + 0.2, 2. * corWd)) - vec2 (flrHt + 0.5, corWd);
  wSz = vec2 (1.7, 1.3);
  d = SmoothMax (d, - min (min (length (q.yz) - wSz.x, PrBox2Df (vec2 (q.z, q.y + wSz.y), wSz)),
     PrBoxDf (((qq.x < corWd - outWd) ? vec3 (qq.x + outWd, qq.y - 0.5, abs (qq.z) - 2.) :
     vec3 (qq.x + outWd - 2. * corWd, q.y + flrHt - 0.1, qq.z + 6.)), vec3 (0.25, 0.5, wSz.x))), 0.01);
  DMINQ (idWall);
  if (! isSh) {
    q.xz = abs (qq.xz);
    q -= vec3 (outWd - corWd, flrHt - 0.5, 0.5 * outWd);
    d = max (PrCylDf (q.xzy, 0.2, 0.05), - min (qq.y, 5. * flrHt - qq.y));
    DMIN (idLit);
  }
  q = qq;
  q.x = abs (q.x + 8.);
  q -= vec3 (1.6, 0.9, 1.95);
  d = PrSphDf (q, 0.15);
  q = qq;
  q.z = abs (q.z + 6.);
  q -= vec3 (-15.95, 7.3, 1.6);
  d = min (d, PrSphDf (q, 0.15));
  DMIN (idBall);
  qq -= vec3 (stCent, flrHt + 0.3).xzy;
  q = qq;
  q.xz = Rot2D (q.xz, -0.73 * pi);
  r = length (q.xz);
  a = (r > 0.) ? atan (q.z, - q.x) : 0.;
  q.xz = vec2 (26. * a / (2. * pi), r - 8.);
  q.xy = Rot2D (q.xy, -0.25 * pi);
  s = mod (q.x, 0.5 * sqrt (2.));
  d = max (0.5 * max (q.y + abs (s - 0.25 * sqrt (2.)) - 0.25 * sqrt (2.),
     max (- q.y - 0.1, abs (q.z) - 1.5)), abs (qq.y + 0.2) - (flrHt + 0.2));
  DMINQ (idStr);
  d = max (0.5 * max (abs (q.y - 0.3) - 0.4, abs (abs (q.z) - 1.6) - 0.1),
     max (max (- qq.x, qq.z), - qq.y - (flrHt + 0.2)));
  q.x = a;
  DMINQ (idStWall);
  q = p;
  q.xz = abs (mod (q.xz + corWd, 2. * corWd) - corWd) - 0.1;
  q.y -= 2. * flrHt + 0.1;
  d = max (PrCylDf (q.xzy, 0.07, 2. * flrHt), PrBoxAn2Df (p.xz, vec2 (outWd - 2.), 3.));
  DMIN (idPil);
  q = p;
  q.xz = abs (q.xz) - (outWd - corWd);
  q.y -= 4. * flrHt + 1.8;
  d = PrSphDf (q, 0.2);
  DMIN (idBall);
  q = p;
  q.y -= 0.8;
  d = PrCylAnDf (q.xzy, 2.4, 0.15, 0.7);
  DMIN (idCen);
  if (dukVu > 0.) {
    d = DukDf (p - dukPos, dMin);
    DMIN (idDuk);
  }
  if (! isSh && ntMode > 0.) {
    for (int k = 0; k < nLit; k ++) {
      q = p - ltPos[k];
      d = PrSphDf (q, 0.1);
      DMIN (idMvLit);
    }
  }
  if (isSh) {
    q = p;
    q.y -= refSpRad;
    d = PrSphDf (q, refSpRad);
    DMIN (0);
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 160; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd, float ltDist)
{
  vec3 qHitT;
  float sh, d, h;
  int idObjT;
  idObjT = idObj;
  qHitT = qHit;
  isSh = true;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 40; j ++) {
    h = ObjDf (ro + d * rd);
    sh = SmoothMin (sh, smoothstep (0., 0.05 * d, h), 0.1);
    d += h;
    if (sh < 0.05 || d > ltDist) break;
  }
  idObj = idObjT;
  qHit = qHitT;
  isSh = false;
  return 0.5 + 0.5 * sh;
}

float Truch (vec2 p)
{
  vec2 ip;
  ip = floor (p);
  p -= ip + 0.5;
  if (Hashfv2 (ip) < 0.5) p = vec2 (- p.y, p.x);
  return min (length (0.5 + p), length (0.5 - p));
}

vec3 ShGrid (vec2 p, vec2 g, int stag)
{
  vec2 q, sq, ss;
  q = p * g;
  if (stag > 0 && 2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.02, 0.03, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.07 * pi * smoothstep (0.4, 0.47, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

vec4 ObjCol (vec3 ro, vec3 rd, inout vec3 vn)
{
  vec4 col4;
  vec3 rg;
  vec2 vf;
  float s;
  bool fxz;
  vf = vec2 (0.);
  if (idObj == idWall) {
    col4 = vec4 (0.9, 0.8, 0.7, 0.1);
    if (abs (qHit.x) < 0.095) {
      col4 *= 0.9;
      vf = vec2 (64., 1.);
    } else if (abs (vn.y) < 0.01) {
      rg = ro;
      rg.y = mod (rg.y, 2. * flrHt + 0.2);
      fxz = (abs (vn.x) > 0.99);
      rg = ShGrid ((fxz ? rg.zy : rg.xy), vec2 (2., 4.), 1);
      col4 *= rg.y;
      rg.xz *= sign (fxz ? vn.x : vn.z);
      if (fxz) {
        if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
        else vn.xz = Rot2D (vn.xz, rg.x);
      } else {
        if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
        else vn.zx = Rot2D (vn.zx, rg.x);
      }
      vf = vec2 (32., 1.);
    }
  } else if (idObj == idDnFlr) {
    rg = ShGrid (ro.xz, vec2 (1.), 0);
    col4 = vec4 (0.8, 0.75, 0.7, 0.1);
    if (vn.y > 0.99) {
      if (rg.x == 0.) vn.yz = Rot2D (vn.yz, rg.z);
      else vn.yx = Rot2D (vn.yx, rg.x);
    }
    if (length (ro.xz) < 2.5) col4.rgb *= vec3 (0., 0.3, 0.5);
    s = Maxv2 (abs (ro.xz));
    if (s < outWd - 2. * corWd)
       col4.rg *= 0.8 + 0.1 * smoothstep (0.3, 0.4, length (fract (ro.xz) - 0.5));
    else if (s > outWd) col4.rg *= 0.95;
    else col4.rgb = mix (vec3 (0.5, 0.5, 0.8), col4.rgb,
       smoothstep (0.05, 0.07, abs (Truch (ro.xz) - 0.5)));
    col4.rgb *= rg.y;
    vf = vec2 (64., 0.5);
  } else if (idObj == idUpFlr) {
    rg = ShGrid (ro.xz, vec2 (1.), 0);
    col4 = vec4 (0.75, 0.65, 0.7, 0.1);
    if (vn.y > 0.99) {
      if (rg.x == 0.) vn.yz = Rot2D (vn.yz, rg.z);
      else vn.yx = Rot2D (vn.yx, rg.x);
      if (abs (Maxv2 (abs (ro.xz)) - outWd + corWd) < corWd) col4.rgb = mix (vec3 (0.3, 0.7, 0.3),
         col4.rgb, smoothstep (0.05, 0.07, abs (Truch (2. * ro.xz) - 0.5)));
    }
    col4.rgb *= rg.y;
    vf = vec2 (64., 0.5);
  } else if (idObj == idRf) {
    col4 = (vn.y > 0.) ? vec4 (0.8, 0.3, 0., 0.1) : vec4 (0.8, 0.5, 0.4, 0.1);
    col4 *= 0.6 + 0.4 * smoothstep (0.03, 0.05, abs (fract (2. * qHit.x + 0.5) - 0.5));
  } else if (idObj == idStr) {
    col4 = vec4 (0.8, 0.8, 0.9, 0.1) * (0.6 + 0.4 *
       smoothstep (0.03, 0.05, abs (fract (2. * qHit.z + 0.5) - 0.5)));
    vf = vec2 (64., 0.5);
  } else if (idObj == idStWall) {
    rg = ShGrid (qHit.xy - vec2 (0., 0.1), vec2 (20., 5.), 1);
    col4 = vec4 (0.8, 0.8, 0.9, 0.1) * rg.y;
    if (abs (vn.y) < 0.01) {
      if (rg.x == 0.) vn = normalize (vec3 (vn.xz, rg.z)).xzy;
      else vn.xz = Rot2D (vn.xz, - rg.x);
    }
    vf = vec2 (32., 1.);
  } else if (idObj == idPil) {
    s = fract (4. * mod (ro.y, 2. * flrHt + 0.2));
    col4 = vec4 (0.7, 0.7, 0.8, 0.1) * (0.8 + 0.2 * smoothstep (0.03, 0.05, abs (s)));
    vn = normalize (vec3 (vn.xz, -0.3 * (1. - smoothstep (0.05, 0.1, abs (s))) * sign (s))).xzy;
    vf = vec2 (32., 1.);
  } else if (idObj == idBall) {
    col4 = vec4 (1., 1., 0.1, 0.3);
  } else if (idObj == idCen) {
    rg = ShGrid (vec2 (atan (ro.z, - ro.x) / pi, ro.y + 0.05), vec2 (16., 4.), 1);
    col4 = vec4 (0.8, 0.9, 0.7, 0.1) * rg.y;
    if (vn.y < 0.01) {
      if (rg.x == 0.) vn = normalize (vec3 (vn.xz, rg.z)).xzy;
      else vn.xz = Rot2D (vn.xz, - rg.x);
    }
    vf = vec2 (32., 1.);
  } else if (idObj == idLit) {
    col4 = vec4 (0.9, 0.9, 0.7, -1.);
  } else if (idObj == idMvLit) {
    col4 = vec4 (0.9, 0.9, 0.95, -1.);
  } else if (idObj == idDuk) {
    s = max (dot (vn, - rd), 0.);
    col4 = vec4 (0.9 * vec3 (1., 0.9, 0.3) * (0.55 + 0.45 * s) + 0.1 * pow (s, 4.) +
       4. * pow (max (dot (VaryNf (1024. * ro, vn, 4.), - rd), 0.), 128.), -1.);
  }
  if (vf.y > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
  return col4;
}

vec3 GrndCol (vec3 ro, vec3 rd, float dstGrnd, inout vec3 vn)
{
  vec3 col, qh;
  vec2 vf;
  vn = vec3 (0., 1., 0.);
  vf = vec2 (0.);
  if (length (ro.xz) < 50.) {
    col = vec3 (0.5, 0.5, 0.55);
    qh = HexGrid (1.5 * ro.xz);
    vn.yz = Rot2D (vn.yz, 0.5 * max (length (qh.xy) - 0.3, 0.) *
       (1. - smoothstep (-0.1, -0.05, rd.y)));
    vn.zx = vn.z * vec2 (qh.x, - qh.y) / length (qh.xy);
    vf = vec2 (16., 0.5);
  } else {
    col = vec3 (0.4, 0.4, 0.3) * (0.85 + 0.15 * smoothstep (0.3, 0.7, Fbm2 (8. * ro.xz)));
    vf = vec2 (4., 1.);
  }
  vf.y *= 1. - smoothstep (0.3, 0.6, dstGrnd / dstFar);
  if (vf.y > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
  return col;
}

vec3 StarPat (vec3 rd, float scl)
{
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 131.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 171.13 * scl * q.yx));
  return 4. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, skCol;
  vec2 q;
  float f, fd, ff, sd, a;
  a = atan (rd.z, - rd.x) / (2. * pi) + 0.5;
  if (rd.y > -0.02 && rd.y < 0.02 * mix (IFbm1 (16. * (a + 1.)), IFbm1 (16. * a), a)) {
    col = vec3 (0.3, 0.35, 0.45);
    if (ntMode > 0.) col *= 0.15;
  } else {
    q = 0.02 * (ro.xz + 2. * tCur + ((100. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    sd = max (dot (rd, sunDir), 0.);
    skCol = vec3 (0.3, 0.4, 0.8) + step (0.1, sd) * vec3 (1., 1., 0.9) *
       min (0.3 * pow (sd, 64.) + 0.5 * pow (sd, 2048.), 1.);
    col = mix (skCol, clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
    if (ntMode > 0.) col = vec3 (0.1) * col.r + StarPat (rd, 6.);
  }
  return col;
}

vec4 SphHit (vec3 ro, vec3 rd, float rad)
{
  vec3 vn;
  float b, d, w, dMin;
  dMin = dstFar;
  b = dot (rd, ro);
  w = b * b - dot (ro, ro) + rad * rad;
  if (w > 0.) {
    d = - b - sqrt (w);
    if (d > 0.) {
      dMin = d;
      vn = (ro + d * rd) / rad;
    }
  }
  return vec4 (dMin, vn);
}

float GlowCol (vec3 ro, vec3 rd, float dstLim)
{
  vec3 dirGlow;
  float dstGlow, brGlow;
  brGlow = 0.;
  for (int k = VAR_ZERO; k < nLit; k ++) {
    dirGlow = ltPos[k] - ro;
    dstGlow = length (dirGlow);
    brGlow += smoothstep (-0.1, 0.1, dstLim - dstGlow) *
       pow (max (dot (rd, dirGlow), 0.) / dstGlow, 2048.) / dstGlow;
  }
  return clamp (brGlow, 0., 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, ds4;
  vec3 col, c, vn, roo, rog, ltDir, ltVec;
  float dstObj, dstSph, dstGrnd, sh, atten;
  dukScl = 0.3;
  refSpRad = 2.1;
  isSh = false;
  roo = ro;
  for (int k = VAR_ZERO; k < 2; k ++) {
    dstObj = ObjRay (ro, rd);
    if (k == 0) {
      ds4 = SphHit (ro - vec3 (0., refSpRad, 0.), rd, refSpRad);
      dstSph = ds4.x;
      if (dstSph < min (dstObj, dstFar)) {
        ro += dstSph * rd;
        rd = reflect (rd, ds4.yzw);
        ro += 0.01 * rd;
      } else break;
    }
  }
  rog = ro;
  sh = 1.;
  atten = 1.;
  dstGrnd = dstFar;
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
  } else if (rd.y < 0.) {
    dstGrnd = - ro.y / rd.y;
    ro += dstGrnd * rd;
    vn = vec3 (0., 1., 0.);
  }
  if (ntMode > 0.) {
    ltVec = roo - ro;
    atten = smoothstep (0.7, 0.85, dot (ltAx, - normalize (ltVec))) / (1. + ((extVu == 0.) ?
       0.01 : 0.0005) * dot (ltVec, ltVec));
    ltVec += ltPos[nLit];
    ltDir = normalize (ltVec);
  }
  if (dstObj < dstFar && idObj != idDuk && idObj != idLit || dstGrnd < dstFar || rd.y < 0.)
     sh = (ntMode == 0.) ? ObjSShadow (ro + 0.01 * vn, sunDir, dstFar) : 1.;
  if (dstObj < dstFar) {
    col4 = ObjCol (ro, rd, vn);
    if (ntMode == 0.) {
      if (col4.a >= 0.) col = col4.rgb * (0.2 + 0.1 * max (dot (vn, sunDir * vec3 (-1., 1., -1.)), 0.) +
         0.1 * step (sh, 0.95) * abs (dot (vn, sunDir * normalize (vec3 (-1., 0., -1.)))) +
         0.8 * sh * max (dot (vn, sunDir), 0.)) +
         col4.a * step (0.95, sh) * pow (max (dot (sunDir, reflect (rd, vn)), 0.), 32.);
      else col = col4.rgb;
    } else {
      if (col4.a >= 0.) {
        col = atten * (col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.)) +
           col4.a * step (0.95, sh) * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.));
      } else {
        col = col4.rgb * (0.3 + 0.7 * max (- dot (rd, vn), 0.));
      }
    }
  } else if (rd.y < 0.) {
    col4.rgb = GrndCol (ro, rd, dstGrnd, vn);
    if (ntMode == 0.) {
      col = col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.));
      col = mix (col, vec3 (0.3, 0.35, 0.45), pow (1. + rd.y, 16.));
    } else {
      col = col4.rgb * atten * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.));
    }
  } else {
    col = SkyBgCol (ro, rd);
  }
  if (ntMode > 0. && (dstObj < dstFar && col4.a >= 0. || dstGrnd < dstFar && rd.y < 0.)) {
    c = vec3 (0.);
    for (int k = VAR_ZERO; k < nLit; k ++) {
      ltVec = ltPos[k] - ro;
      atten = 1. / (1. + 0.01 * dot (ltVec, ltVec));
      if (atten > 0.01) {
        ltDir = normalize (ltVec);
        sh = ObjSShadow (ro, ltDir, length (ltVec));
        c += atten * col4.rgb * sh * max (dot (vn, ltDir), 0.);
      }
    }
    col = mix (col, c * vec3 (1.), smoothstep (-0.05, 0.05, Maxv3 (c) - Maxv3 (col)));
  }
  if (ntMode > 0.) col = mix (col, vec3 (1., 1., 0.3), min (2. * GlowCol (rog, rd,
     min (dstObj, dstFar)), 1.));
  return clamp (col, 0., 1.);
}

vec4 ShowWg (vec2 uv, vec2 canvas, vec4 parmV)
{
  vec4 wgBx[4];
  vec3 cc[4], col;
  vec2 ut, ust, c;
  float vW[2], asp;
  c = vec2 (1., 0.5);
  cc[0] = c.xyy;
  cc[1] = c.yxy;
  cc[2] = c.yyx;
  cc[3] = c.xxy;
  asp = canvas.x / canvas.y;
  for (int k = 0; k < 2; k ++)
     wgBx[k] = vec4 ((0.41 + 0.05 * float (k)) * asp, -0.15, 0.014 * asp, 0.18);
  for (int k = 2; k < 4; k ++)
     wgBx[k] = vec4 ((0.41 + 0.05 * float (k - 2)) * asp, -0.4, 0.02, 0.02);
  vW[0] = parmV.x;
  vW[1] = parmV.y;
  col = vec3 (0.);
  for (int k = 0; k < 2; k ++) {
    ut = 0.5 * uv - wgBx[k].xy;
    ust = abs (ut) - wgBx[k].zw * vec2 (0.7, 1.);
    if (Maxv2 (ust) < 0.) {
      if  (Minv2 (abs (ust)) * canvas.y < 2.) col = 0.3 * cc[k];
      else col = (mod (0.5 * ((0.5 * uv.y - wgBx[k].y) / wgBx[k].w - 0.99), 0.1) *
         canvas.y < 6.) ? 0.3 * cc[k] : 0.6 * cc[k];
    }
    ut.y -= (vW[k] - 0.5) * 2. * wgBx[k].w;
    ut = abs (ut) * vec2 (1., 1.5);
    if (Maxv2 (abs (ut)) < 0.025 && Maxv2 (ut) > 0.02) col = cc[k];
  }
  for (int k = 2; k < 4; k ++) {
    ust = abs (0.5 * uv - wgBx[k].xy) - wgBx[k].zw;
    if (Maxv2 (ust) < 0.) {
      col = cc[k];
      if (Minv2 (abs (ust)) * canvas.y < 2.) col *= 0.6;
    }
  }
  return vec4 (col, step (0.001, length (col)));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat, mPtr, wgBxC, parmV, c4;
  vec3 ro, rd, col, cw;
  vec2 canvas, uv, uvv;
  float el, az, zmFac, asp, sr, tCurM, cm, dukAz, dukEl;
  int wgSel;
  nFrame = iFrame;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  dstFar = 120.;
  SetBldParms ();
  parmV = Loadv4 (0);
  zmFac = 1.5 / (1. - 0.8 * parmV.y);
  extVu = parmV.z;
  ntMode = parmV.w;
  stDat = Loadv4 (1);
  mPtr = vec4 (stDat.xyz, 0.);
  wgSel = int (stDat.w);
  stDat = Loadv4 (2);
  el = - stDat.x;
  az = - stDat.y;
  tCur = stDat.z;
  tCurM = stDat.w;
  stDat = Loadv4 (3);
  vuPos = stDat.xyz;
  stDat = Loadv4 (4);
  vuAz = stDat.x;
  vuEl = stDat.y;
  nLit = int (stDat.w);
  stDat = Loadv4 (5);
  dukPos = stDat.xyz;
  dukPos.y += 1.2;
  stDat = Loadv4 (6);
  dukAz = stDat.x;
  dukEl = stDat.y;
  dukAzCs = CosSin (- dukAz);
  dukElCs = CosSin (- dukEl);
  dukVu = 1.;
  asp = canvas.x / canvas.y;
  wgBxC = vec4 (0.48 * asp, -0.46, 0.022, 0.);
  if (ntMode == 0.) sunDir = normalize (vec3 (0.7, 2., -1.));
  if (extVu > 0.) {
    if (false && (mPtr.z <= 0. || wgSel >= 0)) {
      az -= 0.1 * tCur;
      el -= 0.1 * pi * (1. + sin (0.03 * pi * tCur));
    }
    el -= 0.1 * pi;
    el = clamp (el, -0.45 * pi, -0.02 * pi);
    ro = vec3 (0., 2., -60.);
    zmFac *= 1.5;
  } else {
    az -= vuAz;
    el -= vuEl + 0.1;
    el = clamp (el, -0.35 * pi, 0.35 * pi);
    ro = vuPos;
    ro.y += flrHt + 0.5;
  }
  vuMat = StdVuMat (el, az);
  if (extVu > 0.) {
    ro = vuMat * ro;
    if (ntMode > 0.) ltPos[nLit] = vuMat * vec3 (0., 0., 20.);
  } else {
    if (ntMode > 0.) ltPos[nLit] = vec3 (0., 0.5, 0.5);
  }
  if (ntMode > 0.) {
    for (int k = 0; k < nLit; k ++) {
      stDat = Loadv4 (7 + k);
      ltPos[k] = stDat.xyz;
      ltPos[k].y += 2.;
    }
  }
  if (ntMode > 0.) ltAx = vuMat * vec3 (0., 0., 1.);
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) *
       pi)) / zmFac;
    rd = vuMat * normalize (vec3 (2. * tan (0.5 * atan (uvv.x / asp)) * asp, uvv.y, 1.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  if (tCur - tCurM < 7.) {
    c4 = ShowWg (uv, canvas, parmV);
    cw = mix (col, c4.rgb, c4.a);
    cm = 0.2 + 0.8 * smoothstep (6., 7., tCur - tCurM);
  } else {
    cw = vec3 (0., 0.7, 0.7);
    cm = 0.3 + 0.7 * step (2., abs (length (0.5 * uv - wgBxC.xy) - wgBxC.z) * canvas.y);
  }
  col = mix (cw, col, cm);
  if (mPtr.z > 0. && wgSel < 0) {
    if (Maxv2 (abs (uv)) < 0.05 && Minv2 (abs (uv)) < 0.005)
       col = mix (col, vec3 (0.1, 1., 0.1), 0.3);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrBoxAn2Df (vec2 p, vec2 b, float w)
{
  return max (PrBox2Df (p, vec2 (b + w)), - PrBox2Df (p, vec2 (b - w)));
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float PrEllCylDf (vec3 p, vec2 r, float h)
{
  return max ((length (p.xy / r) - 1.) * min (r.x, r.y), abs (p.z) - h);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec3 HexGrid (vec2 p)
{
  vec2 q;
  p -= HexToPix (PixToHex (p));
  q = abs (p);
  return vec3 (p, (sqrt3/2.) - q.x + 0.5 * min (q.x - sqrt3 * q.y, 0.));
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (mod (dot (p, vec2 (37., 39.)), 2. * pi)) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (mod (p + vec2 (0., 1.), 2. * pi)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (mod (dot (p, cHashVA2) + vec2 (0., cHashVA2.x), 2. * pi)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float IFbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 10.;
  for (int j = 0; j < 3; j ++) {
    f += floor (a * Noiseff (p));
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / (1.75 * a));
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

